css:
@-moz-document domain("kotaku.com")

{
* {
font-family:"Tahoma", sans-serif !important;
left:20px !important;
color:rgb(255,255,255) !important;
-moz-appearance: none !important;
}

html,body {
font-size:14px !important;
background-color:rgb(0,0,0) !important;
}

#header
  {display:none !important;background-image:none !important;text-decoration:none !important; font-weight:normal !important;color:white !important;
}
#header_container
  {display:none !important;background-image:none !important;text-decoration:none !important; font-weight:normal !important;color:white !important;
}
#sidebar
  {display:none !important;background-image:none !important;
}
.page_navigation
{display:none !important;
}
#postMeta
{display:none !important;
}
#post-supplement
{display:none !important;
}
#container
  {left:10px !important;background-image:none !important;text-decoration:none !important; font-weight:normal !important;color:white !important;background-color:rgb(100,0,130) !important;
opacity:100 !important;
-moz-border-radius:40px !important;
padding-bottom:40px !important;
}
#content_container
  {top:10px!important;left:0px !important;background-image:none !important;text-decoration:none !important; font-weight:normal !important;color:white !important;
-moz-border-radius:70px !important;
background-color:rgb(100,0,130) !important;
background-image:url('http://i34.tinypic.com/2l9iayq.png') !important;
background-position: 865px 200px !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
}
#main
  {position:relative!important;
   top:20px!important;background-image:none !important;text-decoration:none !important; font-weight:normal !important;color:white !important;
}
.metadata
 {display:none !important;
}
#tead
  {display:none !important;background-image:none !important
}
blockquote
  {background-color:black !important;left:200px !important;
}
table
  {background-color:black !important;left:200px !important;
}
#footer
  {display:none !important;
}
#dfpad
  {display:none !important;
}
#comments
 {display:none !important;
}
.separator
{display:none !important;
}
a{text-decoration:underline !important; font-weight:normal !important;color:white !important;}

#img{
background:transparent !important;
border:solid 3px rgb(100,0,130) !important;
-moz-border-radius:10px !important;
opacity:100 !important;
z-index:3 !important;
}

a.quotejs:hover, a:hover{color:rgb(240,220,150) !important;}

}
