css:
@-moz-document domain("peatarian.com") {
/* T Y P O G R A P H I C */
html{ font-size:62.5%; }
body, td{
  font-size:1rem;
  font-family:"Helvetica Neue", Calibri, Helvetica, sans-serif;
  line-height:1.618;
}
p{ font-family: 'Sentinel SSm A', 'Sentinel SSm B', 'Palatino Linotype', 'Book Antiqua', Palatino, serif;}
p, span,
ul > li,
dl > dd,
input,
textarea,
button { 
  font-size:1.618rem;
}
h1{ font-size: 11.089rem; margin: 3rem 0;}
h2,
h3{ font-size: 6.853rem; margin: 2rem 0;}
h4,
h5{ font-size: 4.235rem; margin: 1rem 0;}
h6{ font-size: 2.617rem; margin: 1rem 0;}
h1,h2,h3,h4,h5,h6,.qa-nav-sub{line-height:1.3em; font-family:"Playtime With Hot Toddies";}
h3,h5{ font-weight:normal;}

li{ margin:1.618rem 0 !important;}

/* prevent child font size increases */
h1 *, h2 *, h3 *, h4 *, h5 *, h6 *,
span > span, li > span, p > span, p > p, li > p,
li > input, p > input, span > input,
li > button, p > button, span > button
{ font-size: inherit; }

.qa-q-view-content,.qa-c-item-content,.qa-q-item-title,.qa-a-item-content,.qa-view-count{ font-size:1.618rem; }

.qa-nav-cat-link {
    display: inline-block;
    /* height: 2.618em; */
    margin: -.5em 0;
    line-height: 2.5em;
}
.qa-nav-cat-note {
    font-size: 1rem;
}

/* L A Y O U T */
.qa-notification{ position:fixed; top:0; }
.qa-q-view-buttons input {
    border-radius: .55rem;
    padding: .5rem 1rem;
}

.qa-q-view-buttons {
    float: right;
}

/* T R A N S I T I O N A L */
.qa-nav-main-link,
.qa-favorite-button{ transition: opacity .34s; }

.qa-nav-main-link {
    padding: 21rem 1.5rem 0;
    background-color: rgba(255,198,255,0);
    transition: all .34s;
}
.qa-nav-main {
    height: 21rem;
    top: -3rem;
    position: absolute;
    overflow:hidden;
}
.qa-nav-sub {
    margin: 3rem 0 2rem 1rem;
}
.qa-header {
    position:relative;
    padding:0;
    height: 18rem;
}
.qa-header:hover .qa-nav-main-link {
    padding-top: 18.8rem;
}
.qa-nav-main-item,
.qa-nav-main-item-opp{
    margin: 0 !important;
}
.qa-header .qa-nav-main-link:hover{ padding-top: 17rem; background-color: rgba(255,198,255,0.55);}
.qa-header:hover .qa-nav-main-selected:hover {
    padding-top: 18.8rem;
    background-color:rgba(158,158,121,0.34);
}


/* C O L O R */
body{ color: #293d39; background:#f2f2dd;}
.qa-body-wrapper{
    background-image: -webkit-gradient(
	linear,
	right bottom,
	left bottom,
	color-stop(0, #F2F2DD),
	color-stop(0.13, #FFFFEB),
	color-stop(0.87, #FFFFEB),
	color-stop(1, #F2F2DD));
  background-image: -o-linear-gradient(left, #F2F2DD 0%, #FFFFEB 13%, #FFFFEB 87%, #F2F2DD 100%);
  background-image: -moz-linear-gradient(left, #F2F2DD 0%, #FFFFEB 13%, #FFFFEB 87%, #F2F2DD 100%);
  background-image: -webkit-linear-gradient(left, #F2F2DD 0%, #FFFFEB 13%, #FFFFEB 87%, #F2F2DD 100%);
  background-image: -ms-linear-gradient(left, #F2F2DD 0%, #FFFFEB 13%, #FFFFEB 87%, #F2F2DD 100%);
  background-image: linear-gradient(to left, #F2F2DD 0%, #FFFFEB 13%, #FFFFEB 87%, #F2F2DD 100%);
  }
.qa-q-view-buttons input {  
  background: #ddd;
}
/* E F F E C T S */
H2{
	text-shadow:
		rgba(0,0,0,0.1) -1px 0, rgba(0,0,0,0.1) 0 -1px, rgba(255,255,255,0.1) 1px 0,
		rgba(255,255,255,0.1) 0 1px, rgba(0,0,0,0.1) -1px -1px, rgba(255,255,255,0.1) 1px 1px; 
}
}
