css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("fark.com"), domain("totalfark.com"), domain("foobies.com")
{
/*---------------------------------------------REMOVE JUNK---------*/
#iframeContainer1,#iframeContainer2, #iframeContainer3, #iframeContainer4, #iframeContainer5, #iframeContainer6, #iframeContainer7, #iframeContainer8, #abPleaBar, header img, #foobieslogo, #logo, #tagLine, #rightSideLeftMenubar, #headerTop, div[id^="rightAd"], #leaseBanner, .URLHover, .headlineTable .abbu, .headlineTable .spau, rightCol, #logo,img.tfbadge, #headlineCommentsInfoLineThingy, #headerMenuContainerNoScript, #footerFarkLinks,.submitLinkcontainer, copyrightInfo, .comlinkcount,  .howto, .refreshRedisplayComments, .cpafootnote, .commentPostingTable, .headlineRowTitle, .new_to_fark_p, .tryBareFark



{
display: none !important; visibility: hidden !important; margin-left: -10000pt !important; margin-top: -10000pt !important;
}
/* ------------------------------------------BROWN-------------- */
* 
{
color: #fff !important;
background-color: #000000 !important;
}
body{
  background-image: none !important;
}
pre {
  color: #aaaaaa !important;
  background-color: #000000 !important;
}
h6{
  color:#ff6600 !important;
}
h5{
  color:#ff9900 !important
}
h4{
  color:#a68a6c !important
}
h3{
  color:#b99a78  !important;
  font-size:large
}
h2{
  color:#947b60 !important;
  font-size:x-large !important
}
h1{
  color:#ffffff !important;
  font-size:xx-large !important
}

a:link{
  color: #9b703f  !important;
  text-decoration: none !important;
  border: 0 !important;
  height: 40px !important;
}
a:visited{
  color: #cda869  !important;
  border: 0 !important;
  text-decoration: none !important;

}
a:hover{
  text-decoration: underline !important;
    color: #816b54  !important;
     
}
a:active{
  text-decoration: none !important;
  color: #b99a78  !important;
  height: 90px !important;
}


/* -------------------------------------------------------------------HEADER CLEANUP------- */

#headerMenu li.menuSelected {
    background: #b99a78 !important;
    color: #fff  !important;
    height: 45px;
    }

#header {
  background-color: #000000 !important;
  border: 0 !important;
  height: 40px !important;
min-width: 600px;
}

#headerMenuContainer {
  background-color: #000000 !important;
  border: 0 !important;
  height: 50px !important;
  min-width: 650px;
  }

#catMenu {
  height: 60px !important;
  border: 0 !important;
  }
  
menuSelected {
border-bottom-color: #b99a78 !important;
}

/* -------------------------------------------------------------------COLUMN CLEANUP------- */

#rightCol {
  display: none !important;
}

td#rightColSpacer {
  width: 0px !important;
}

td#leftColSpacer {
  width: 98% !important;
}
#siteContainer,
#bodyContainer {
	width: 100% !important;
}
#bodyHeadlineContainer{
	margin-right: 0em !important;
}
div#loginLinks {
    text-align: left;
}
#searchBar {
    float: left; 
}

#newsContainer {
  min-width: 400px;
}

#container{
	min-width: 600px;
}
}
