css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.isen-bretagne.fr") {


	/* modif de navigation.css */
	
    div#portal-page-header div#tabs
    {
    	background-image: none !important;
    }
    
    div#portal-page-header div#tabs ul#tabList li a
    {
        background-color: #8cf !important;
    	border-color: #0cf !important;
    	border-radius: 5px !important;
    }
    
    div#portal-page-header div#tabs ul#tabList li.active-tab,
    div#portal-page-header div#tabs ul#tabList li.active-tab a,
    div#portal-page-header div#tabs ul#tabList li.editable-tab,
    div#portal-page-header div#tabs ul#tabList li.editable-tab a
    { 
    	background-color: #6af !important;
    	border-radius: 5px !important;
    }
    
    div#portal-page-header div#tabs ul#tabList li a:focus,
    div#portal-page-header div#tabs ul#tabList li a:hover
    {
        box-shadow: none !important;  
      	background-image: none !important;
    	background-color: #8cf !important;
    	border-color: #0cf !important;
    	border-radius: 5px !important;
    }

    
    div#portal-page-header div#tabs ul#tabList li ul#chanList li a:focus,
    div#portal-page-header div#tabs ul#tabList li ul#chanList li a:hover,
    div#portal-page-header div#tabs ul#tabList li.active-tab ul#chanList li a:focus,
    div#portal-page-header div#tabs ul#tabList li.active-tab ul#chanList li a:hover,
    div#portal-page-header div#tabs ul#tabList li.editable-tab ul#chanList li a:focus,
    div#portal-page-header div#tabs ul#tabList li.editable-tab ul#chanList li a:hover
    {
        background-image: none !important;
         transform: none !important;
         -ms-transform: none !important; /* IE 9 */
         -webkit-transform: none !important; /* Safari and Chrome */ 
    }
    
    div#portal-page-header div#tabs ul#tabList li ul#chanList li a:active,
    div#portal-page-header div#tabs ul#tabList li.active-tab ul#chanList li a:active,
    div#portal-page-header div#tabs ul#tabList li.editable-tab ul#chanList li a:active
    {
    	color: #fff !important;
        background-image: none !important;
    }
    
    div#portal-page-header div#tabs li:hover ul,
    div#portal-page-header div#tabs li a:hover ul
    {
        left: -20px !important;
    }

    /* modif de isen_standard.css */

    .portlet {
        box-shadow: none !important;
    }

    .portlet-toolbar {
        background-color: #8cf !important;
        box-shadow: none !important;
    }
    
    .uportal-channel-title, .portlet-toolbar h2 {
        background-color: #8cf !important;
    }
    
    .table1 tbody tr td {
        background-color: transparent !important;
    }
    
    .table1 tbody tr td .Style1 {
        color: #08f !important;
    }
}
