css:
@-moz-document url("chrome://browser/content/browser.xul") {
/*书签栏高度锁定*/
#PersonalToolbar{ 	
	background: rgba(255,255,255,.85) !important;
	border-radius: 0 !important;
	border: none !important;
	max-height:24px !important;
	 } 
/* 自动调整*/
#personal-bookmarks .bookmark-item[container="true"] {	width: auto;}
/*不是文件夹只显示图标 */
#personal-bookmarks .bookmark-item:not([container="true"]){	width: 24px;} 
/* 图标微调 */
#personal-bookmarks .bookmark-item > .toolbarbutton-icon{	margin: 0px 0px 0px 1px !important;	padding: 0 !important;}
/*书签栏鼠标样式*/
#personal-bookmarks  menuitem,
#personal-bookmarks  toolbarbutton,
#personal-bookmarks menu
{ 
	/*系统样式关闭*/
	-moz-appearance: none !important;
	background: none !important;
	margin: 0 !important;
	outline: 0px !important;
	border: 1px solid transparent !important;    /*透明边框*/
	border-radius: 0px !important;
}

#personal-bookmarks  menuitem:hover,
#personal-bookmarks  toolbarbutton:hover,
#personal-bookmarks menu:hover
{ 
	background-color: rgba(0,0,0,.15) !important;
	border-color:rgba(0,0,0,.3) !important;
	/*box-shadow: 0 0 1px #000 inset !important;*/
}
}
