css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("canvas.instructure.com") {
#header
div
{
background-color: black;
}
.discussion-pubdate
div
{
color: rgb(50, 50, 50);
}
#footer 
{
background-color: lightgrey;
}
div#header.no-print div#topbar ul.identity
{
background-color: green;
background-image: none;
}
div#wrapper
{
background-color: white;
}
div#discussion_subentries.discussion_subentries
{
background-color: whitesmoke;
}
#main, #inbox, #breadcrumbs, #tinymce, .toolbarView, .discussion_entry, .discussion-entry-reply-area, .conversations, #create_message_form, #create_message_form form, .scrollable-list, .section {
background-image: none !important;
background-color: transparent !important; 
box-shadow: none;
}
ul#identity
{
background-image: none !important;
background-color: green !important;
}
b.unread-messages-count
{
background-color: #4BCC18 !important;
}
strong.unread-count
{
background-color: #4BCC18 !important;
}
div.stream_header:hover
{
background-color: whitesmoke !important;
}
div.stream_header:hover div.title
{
color: black;
}
div.pinned.discussion-list.item-group
{
background-color: whitesmoke !important;
}
div.open.discussion-list.item-group
{
background-color: whitesmoke !important;
}
div.locked.discussion-list.item-group
{
background-color: whitesmoke !important;
}
li.discussion:hover
{
background-color: lightgrey !important;
}
button#start_new_course
{
background-color: lightgrey !important;
}
.prev.btn.module_button:hover
{
background-color: lightgrey !important;
}
.next.btn.module_button:hover
{
background-color: lightgrey !important;
}
.btn.topic-subscribe-button:hover
{
background-color: lightgrey !important;
}
.al-trigger.btn:hover
{
background-color: lightgrey !important;
}
.ui-button.ui-widget.ui-state-default.ui-corner-all.ui-button-text-only:hover
{
background-color: lightgrey !important;
}
#collapseAll.btn.disableWhileFiltering:hover
{
background-color: lightgrey !important;
}
#expandAll.btn.disableWhileFiltering:hover
{
background-color: lightgrey !important;
}
#discussion_topic.admin-link-hover-area.topic.discussion_entry.discussion_topic.can_be_marked_as_read.read
{
background-color: whitesmoke !important;
}
.edit_link.btn.button-sidebar-wide:hover
{
background-color: lightgrey !important;
}
.show_groups_link.btn.button-sidebar-wide:hover
{
background-color: lightgrey !important;
}
.btn.button-sidebar-wide:hover
{
background-color: lightgrey !important;
}
div#discussion-toolbar.toolbarView.unsubscribed
{
background-color: lightgrey !important;
}
div.headerBar.sticky
{
background-color: whitesmoke !important;
}
div#discussion-toolbar.toolbarView .headerBar
{
background-color: whitesmoke !important;
}
.content_summary:hover, .date:hover, .remove:hover
{
background-color: whitesmoke !important;
}
}
