css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://localhost:9092/transmission/web/") {


/* Menu bar area */

#toolbar {
    background-image: none !important;
}

#statusbar {
    background-image: none !important;
}

.torrent_footer {
    background-image: none !important;
}
#torrent_search {
    border-radius:0px !important;
}

/* Torrent area */

li.torrent.even {
    background-color: white !important;
}

li.torrent.even.selected {
    background-color: rgb(205, 205, 205) !important;
}


.torrent_progress_bar {
    background-image: none !important;
    background-color: rgb(207,207,207) !important;
    border-radius: 2px !important;
}

.torrent_progress_bar.complete.leeching {
    background-color: rgb(61, 157, 234) !important;
}

.torrent_progress_bar.complete.seeding {
    background-color: rgb(38, 158, 48) !important;
}

}
