css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('foopee.com') {


/* Base Styles
======================================== */
body {
  background: #f3f6f6;
  color: #95a5a6;
  margin: 0;
  font-size: 16px;
  font-family: "Lato", arial, sans-serif !important; }
  body a {
    text-decoration: none;
    color: #95a5a6;
    -ms-word-break: break-all;
    word-break: break-all;
    /* Non standard for webkit */
    word-break: break-word;
    -webkit-hyphens: auto;
    -moz-hyphens: auto;
    hyphens: auto; }
  body ul {
    /* gridify the list */
    width: 70%;
    max-width: 720px;
    margin: 2em auto;
    padding-bottom: 2em; }
    body ul ul {
      width: 100%;
      margin-left: 0;
      margin-right: 0; }

/* Header Styles
======================================== */
h1, h2 {
  color: white;
  padding: 1em 2.5% 1em;
  max-width: 685px;
  margin: 0 auto; }

h1 {
  padding-bottom: 0;
  color: #e74c3c; }

h2 {
  text-transform: uppercase;
  font-size: 0.725em;
  font-weight: bold;
  letter-spacing: 0.08em;
  padding-bottom: 1.5em;
  color: #95a5a6; }

/* Main Contents
======================================== */
ul {
  margin: 2em 0;
  padding: 0;
  background: #eff2f3; }
  ul li {
    line-height: 1.4;
    padding: 0;
    margin: 0;
    position: relative;
    list-style: none;
    text-transform: uppercase;
    font-size: 0.5em;
    letter-spacing: 0.08em;
    font-weight: bold;
    /* the date subheadings */
    /* sub-list - the gigs */ }
    ul li a {
      color: #95a5a6;
      border-bottom: none;
      display: inline-block;
      padding-left: 2.5%; }
      ul li a b:first-child {
        padding: 2.2em 0 1em;
        font-size: 11px;
        display: block; }
    ul li ul {
      margin: 0;
      padding: 0; }
      ul li ul li {
        text-transform: none;
        font-size: 1em;
        letter-spacing: 0;
        padding: 0.5em 2.5% 1em 2.5%;
        margin: 0;
        font-weight: normal;
        font-size: 1.5em;
        color: #ecf0f1;
        position: relative;
        /* venue info */
        /* band link styles */ }
        ul li ul li b {
          font-size: 0.75em;
          float: right;
          margin-right: -1em;
          line-height: 2; }
          ul li ul li b a {
            border: none;
            font-weight: normal;
            color: #bdc3c7;
            padding: 0; }
        ul li ul li a {
          text-decoration: none;
          color: #304357;
          opacity: 0.825;
          font-weight: bold;
          font-size: 1.667em;
          padding: 0;
          margin: 0 0.425em 0 0;
          transition: all 0.4s ease-in-out;
          border-bottom: 1px dotted #ecf0f1; }
          ul li ul li a:hover {
            color: #2980b9 !important;
            border-bottom: 1px dotted #2980b9; }
          ul li ul li a:visited {
            color: #d35400; }
          ul li ul li a:nth-child(2) {
            color: #222;
            opacity: 1; }
          ul li ul li a:last-child {
            margin-right: 1em; }
            ul li ul li a:last-child:after {
              visibility: hidden;
              display: block;
              font-size: 0;
              content: " ";
              clear: both;
              height: 0; }
      ul li ul li:hover {
        background: #e6ebed;
        color: #95a5a6;
        transition: all 0.4s ease-in-out; }
        ul li ul li:hover b a {
          color: #95a5a6; }
          ul li ul li:hover b a:hover {
            color: #7493b2; }

/* Bits
======================================== */
/* Back Link */
.backlink {
  position: fixed;
  top: 1.2em;
  left: 50%;
  margin-left: -432px; }
  .backlink a {
    color: #2980b9;
    font-weight: bold;
    font-size: 2.5em;
    display: inline-block;
    line-height: 1;
    padding: 0.25em 0.667em 0.5em;
    text-decoration: none;
    text-align: center;
    opacity: 0.333;
    transition: all 0.4s ease-in-out; }
    .backlink a:hover {
      background: white; }

}
