css:
@-moz-document url("http://www.bbc.co.uk/radio/player/bbc_1xtra") {

/* 
BBC Radio 1Xtra Black & Red (-Lord KM-) V.1.1
Do not edit my code or use it for anything other than what i give you 
Do not upload and call your own.  You have no right.
 
Updates
v.1.0 Release
v.1.1 Removed Unnecessary code

Enjoy this theme as you listen to Radio 1 Xtra!

*/

.radioplayer a {
color: rgb(189, 0, 0);
}
body {
font-family: Georgia, Times New Roman, Times, serif !important;
}
.control-image, .control-image:active, .control-image:focus {
background-image: url(http://www.damnpic.net/images/2013/12/24/controls-spritesheet.png);
}
#duration {
color: rgb(190, 0, 0);
}
#progress-scrubber-playback-bar {
background-color: rgb(255, 0, 0);
background-image: -o-linear-gradient(right, #FF0000 0%, #000000 55%);
background-image: -ms-linear-gradient(right, #FF0000 0%, #000000 55%);
background-image: linear-gradient(to right, #FF0000 0%, #000000 55%);
}
#progress-scrubber-load-bar {
background-color: rgb(240, 0, 0);
}
.radioplayer-head .components ul li {
display: block;
float: left;
text-align: center;
border-radius: 19px !important;
box-shadow: 0 0 42px rgb(233, 0, 0) !important;
}
.radioplayer-head .components ul li a {
background: transparent url(http://www.damnpic.net/images/2013/12/24/button-sprite.png) no-repeat 0 0;
}
.radioplayer-head .components input#search-input {
font-family: Georgia;
border: 1px solid #cc0000;
border-top-color: rgb(141, 141, 141);
color: rgb(255, 0, 0);
  border-radius: 19px !important;
background: #000;
  box-shadow: 0 0 42px rgb(233, 0, 0) !important;
background-image: -o-linear-gradient(bottom, #FF0000 0%, #000000 55%);
background-image: -ms-linear-gradient(bottom, #FF0000 0%, #000000 55%);
background-image: linear-gradient(to bottom, #FF0000 0%, #000000 55%);
}
.radioplayer-head .components #search-button {
background: transparent url(http://www.damnpic.net/images/2013/12/24/search-button-bg.png) 0 0 no-repeat;
}
.radioplayer .page .inner {
background: rgb(90, 0, 0);
color: rgb(179, 0, 0);
      border-radius: 19px !important;
      box-shadow: 0 0 42px rgb(0, 0, 0) !important;
height: 520px !important;
position: relative;
}
  .radioplayer .section {
border-bottom: 1px solid rgb(255, 8, 8);
      border-radius: 19px !important;
}  
.radioplayer #main-image {
border-radius: 19px !important;
box-shadow: 0 0 42px rgb(0, 0, 0) !important;
}
.radioplayer .details .bandwidth {
float: right;
font: 1em/1 Georgia;
color: rgb(180, 0, 0);
}
.radioplayer .page p {
font: 1.2em/15px georgia;
color: rgb(0, 0, 0);
text-shadow: 0 1px 0 rgb(156, 155, 155) !important;
}
.radioplayer .elsewhere li.elsewhere-link a {
background: url(http://www.damnpic.net/images/2013/12/24/sprites.gif) 0 3px no-repeat;
}
.p-f-dialog-heading {
color: rgb(138, 0, 0);
}
.radioplayer .next-on {
background: #000;
background-image: -o-linear-gradient(left bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
background-image: -ms-linear-gradient(left bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
background-image: linear-gradient(to left bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
}
.radioplayer .now-playing h3, .radioplayer .now-playing p {
font: 1.2em/1.5 georgia;
  border-radius: 19px !important;
background-image: -o-linear-gradient(right bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
background-image: -ms-linear-gradient(right bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
background-image: linear-gradient(to right bottom, #FF0000 0%, #000000 52%, #FF0000 100%);
color: rgb(213, 32, 32);
}
.radioplayer .now-playing #artist-image {
border: 1px solid #ff0000;
float: left;
width: 66px;
  border-radius: 19px !important;
box-shadow: 0 0 42px rgb(0, 0, 0) !important;
}
.p-f-label, .p-f-label-display {
color: rgb(194, 0, 0);
}
.p-f-icon, .p-f-button.p-f-refocus.p-f-focus.p-f-leave .p-f-icon {
background-color: rgb(143, 0, 0);
}
.bbc-st-slim .bbc-st-count {
background: url("http://www.damnpic.net/images/2013/12/24/sprite-032.png") no-repeat right -218px !important;
}
    .bbc-st-colour .bbc-st-count span {
background: rgb(0, 0, 0);
color: rgb(199, 0, 0);
border: 1px solid #cc0000;
}
    .bbc-st-colour ul li.bbc-st-share-cta a {
color: rgb(197, 0, 0);
background-color: rgb(0, 0, 0);
border: 1px solid #cc0000;
}
    .radioplayer .section h2 {
font: normal 1.1em/1 georgia;
margin-bottom: 4px;
text-transform: uppercase;
}
    .bbc-st-colour ul li.bbc-st-share-cta.on a, .bbc-st-colour ul li.bbc-st-share-cta a:focus, .bbc-st-colour ul li.bbc-st-share-cta a:hover {
background-color: rgb(77, 77, 77);
        }
        .clearfix {
_height: 1%;
box-shadow: 0 0 42px rgb(0, 0, 0) !important;
}
.radioplayer .now-playing .full-tracklist {
font: 1.1em/1 georgia;
}
.radioplayer #footer {
border-top: 1px solid rgb(255, 0, 0);
}
  .radioplayer #footer li, p#report-issue {
color: rgb(150, 1, 1);
font: 1em/17px georgia;
}
  p#report-issue a {
background: url(http://www.damnpic.net/images/2013/12/24/reporticon.png) no-repeat;
padding: 4px 0 5px 23px;
cursor: pointer;
}
  .radioplayer #footer li a, p#report-issue a {
color: rgb(201, 0, 0);
}
.overlay-controls {
border-radius: 19px !important;
background: transparent url(http://www.damnpic.net/images/2013/12/24/playing-controls-bg.png) 0 0 repeat;
}
  #my-station-controls .add-station {
background: transparent url(http://www.damnpic.net/images/2013/12/24/add-button.png) 0 0 no-repeat;
    border: 1px solid #000;
    margin-top: -1px;
}
  #my-station-controls .already-added {
background: transparent url(http://www.damnpic.net/images/2013/12/24/added.png) 0 0 no-repeat;
border: 1px solid #000;
margin-top: 0px;
width: 64px !important;
}
  #my-station-controls .full {
background: transparent url(http://www.damnpic.net/images/2013/12/24/full.png) 0 0 no-repeat;
border: 1px solid #000;
margin-top: 1px !important;
}
  #my-station-controls .discard-changes {
background-color: rgb(124, 0, 0);
}
.overlay-controls .close a {
background: transparent url(http://www.damnpic.net/images/2013/12/24/icon-close-btn.png) 100% 0 no-repeat;
}
      #my-station-controls .edit-stations a {
background: transparent url(http://www.damnpic.net/images/2013/12/24/presets-edit.png) left top no-repeat;
}
      #my-station-controls .save-changes a {
background: transparent url(http://www.damnpic.net/images/2013/12/24/presets-save.png) right top no-repeat;
border-left: 1px solid rgb(255, 0, 0);
}
      .edit-row-spacer {
height: 28px;
  border-radius: 19px !important;
background-color: rgb(124, 0, 0);
}
        .edit-row-spacer .last-played {
background-color: rgb(192, 0, 0);
  border-radius: 19px !important;
  box-shadow: 0 0 42px rgb(0, 0, 0) !important;
padding-top: 5px;
}
        .my-station-list, #mystations-loading {
background: rgb(175, 0, 0);
  border-radius: 19px !important;
margin: 0;
  box-shadow: 0 0 42px rgb(0, 0, 0) !important;
padding: 0;
}
.edit-row-spacer .last-played p {
background-color: rgb(0, 0, 0);
color: rgb(255, 255, 255);
}
#mystations .tooltip {
    border-radius: 19px !important;
background-color: rgb(165, 0, 0);
}
.my-station-list .last-played {
background-color: rgb(170, 0, 0);
}
.my-station-list .station-container {
float: left;
height: 43px;
width: 76px;
  border-radius: 19px !important;
text-align: center;
padding: 5px 7px 6px 7px;
}

.station-underlay {
background-color: rgb(213, 0, 0);
}
#playing .overlay-item {
background-color: rgb(80, 2, 2);
  border-radius: 19px !important;
}
.overlay-item .title {
color: rgb(255, 0, 0);
  box-shadow: 0 0 42px rgb(0, 0, 0) !important;
}
.p-f-dialog-heading {
color: rgb(138, 0, 0);
}
.p-f-dialog-image {
background: url(http://www.damnpic.net/images/2013/12/24/pre-signin-upsell.jpg) 0 0 no-repeat;
}
  .p-f-dialog-content-inner {
color: rgb(182, 16, 16);
}
.p-f-dialog-text {
color: rgb(243, 0, 0);
}
  .p-f-dialog-wrapper .id4-cta {
font-family: georgia;
}
  .p-f-dialog-wrapper .id4-cta .id4-cta-color-blue a.id4-cta-button:link, .p-f-dialog-wrapper .id4-cta .id4-cta-color-blue a.id4-cta-button:visited {
background-color: rgb(0, 0, 0);
color: rgb(155, 0, 0);
}
    .p-f-dialog-wrapper .id4-cta .id4-cta-large-blue a.id4-cta-button {
background-image: url(http://www.damnpic.net/images/2013/12/24/btn-rollover-l-b.gif);
}
  .p-f-dialog-signin a, .p-f-dialog-signin a.id4-cta-register {
color: rgb(214, 0, 0);
}
  .p-f-dialog-wrapper {
background-color: rgb(37, 0, 0);
    border-radius: 19px !important;
    box-shadow: 0 0 42px #cc0000 !important;
color: rgb(0, 0, 0);
}
  #radioplayer {
background: rgb(0, 0, 0) url('http://www.damnpic.net/images/2013/12/24/bbc1xtra.jpg') no-repeat !important;
    border-radius: 19px !important;
    box-shadow: 0 0 42px #cc0000 !important;
}
fieldset, img {
border: 1px solid #cc0000;
border-radius: 19px !important;}
}
