css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*news.ycombinator.com/item.*") {
table[width="85%"] > tbody > tr+tr > td > table[border="0"]{background-color:#111 !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"] > tbody > tr:nth-child(odd){background-color:#242424 !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"] > tbody > tr:nth-child(even){background-color:#1E1E1E !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"]:first-child{width:100% !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"]:first-child > tbody{width:100% !important;}
form[action="/r"] > textarea{width:97% !important; color: #DFDFDF !important; background-color: #1F1F1F !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"]:first-child > tbody > tr{background-color:#111 !important;}
table[width="85%"] > tbody > tr+tr > td > table[border="0"] > tbody > tr > td > table[border="0"] > tbody > tr > td:first-child{border-right:#333 1px solid !important;}
}
