css:
@-moz-document 

domain("wikia.com"),
domain("fandom.com"){

body, #WikiaMainContent, #WikiaPageBackground,
.va-chart-body, .pi-data-label
{
background: none #000000 !important; 
}
.WikiaPageBackground, .module
{
background: none #444444 !important; 
}
.search-form-wrapper .autocomplete
{
background-color: #444444 !important; 
}
.va-navbox-cell, .va-navbox-column, .va-navbox-image
{
background: #111111 !important; 
border: 1px #111111 solid !important; 
}
.wikia-button,
 .search-form-wrapper .autocomplete div:hover,
 .search-form-wrapper .autocomplete div.selected
{
background: #006CB0 !important; 
}
#globalNavigation,
.wds-global-navigation__logo,
.wds-global-navigation__link,
.wds-global-navigation__wikis-menu,
.wds-global-navigation__search,
.wds-global-navigation__account-menu,
.wds-global-navigation__start-a-wiki 
{
background-color: #006CB0 !important; 
}
#SPOTLIGHT_FOOTER
{
background: none !important; 
}
a:hover, .subnav-3a a:hover
{
color: #FFFFFF !important;
text-decoration: underline !important;
}
.WikiHeader, th, tr, .grid-1, .subnav,
.WikiaPageContentWrapper, h1, h2, h3,
#mw-content-text div, .va-chart-quest,
#exploreWikiaEntryPoint li, .va-chart-reward,
#TOP_LEADERBOARD_AB, .article-categories
{
background: #000 !important; 
}
.va-chart-quest
{
border: none !important; 
}
.catnav a:hover, input,
.subnav-2a, .subnav-3a,
#exploreWikiaEntryPoint a:hover,
.hubs-links a:hover
{
background: #333333 !important; 
}
.lcs-container td 
{
background: none !important; 
color: #FFF !important;
}
a, .WikiaPageBackground, .module,
h1, h2, h3, h4, h5, h6, p, tr, .lyricbox,
body, #WikiaMainContent, span, input,
.title, .category-gallery-item-image,
.pi-data-label, .pi-secondary-font
{
color: #FFF !important;
text-shadow: none !important;
}
.search-form-wrapper .autocomplete strong
{
color: #FFFFFF !important;
text-shadow: 1px 1px 5px #FFFFFF!important;
}
div#song-header-container, .WikiaPage, .lyricbox,
.global-nav-search-input-wrapper .search-input,
.va-dirbox, .va-table td, .va-table th
{
border: 1px #006CB0 solid !important; 
}
.search-form-wrapper .autocomplete div,
.wikia-page-header .header-container,
.WikiaPageHeader, .WikiaBlogListingHeader,
.WikiaArticle h2
{
border-bottom: 1px #006CB0 solid !important; 
}
.WikiaFooter section, .WikiaBarWrapper section
{
border-color: #006CB0 !important; 
}
.wds-global-navigation
{
box-shadow: none !important; 
}
.trc-content-sponsored 
{
display: none !important; 
}
}
