@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blender.pl") {

/* Normal, standard links. */


a:link, a:visited
{
	color: #e3e3e3;
	text-decoration: none;
	font-weight: bold;
}

a:hover
{
	color: #ec9c34 !important;
	text-decoration: none;
	font-weight: bold;
}

/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
	color: #7f7d89;
	text-decoration: none;
}
a.nav:hover
{
	color: #f67d12;
	text-decoration: underline;
}

/* Tables should show empty cells. */
table
{
	empty-cells: show !important;
}

/* By default (td, body..) use verdana in black. */
body, td, th , tr
{
	color: #9aa0a6;
	font-size: small;
	font-family: verdana, sans-serif;
}

/* The main body of the entire forum. */
body
{
/*	background-color: #303030;*/
	margin: 0px;
	padding: 12px 30px 4px 30px;
}

/* Input boxes - just a bit smaller than normal so they align well. */
input
{
	color: #dddddd;
	font-family: verdana, sans-serif;
	font-size: 90%;	
	background-color: #303034;
	border: 1px solid grey !important;
}

button
{
	color: #dddddd;
	font-family: verdana, sans-serif;
	font-weight: bold;
	font-size: 90%;	
	background-color: #303034;
		border: 1px solid grey !important;
}

textarea
{
	font-size: 100%;
	color: #9aa0a6;
	background-color: #131315;
	font-family: verdana, sans-serif;
		border: 1px solid grey !important;
}

/* All input elements that are checkboxes or radio buttons. */
input.check
{
}

/* Selects are a bit smaller, because it makes them look even better 8). */
select
{
	font-size: 90%;
	font-weight: normal;
	color: #dddddd;
	background-color: #303034;
	font-family: verdana, sans-serif;
		border: 1px solid grey !important;
}

select:hover
{
		border: 1px solid #ec9c34 !important;
}

/* Standard horizontal rule.. ([hr], etc.) */
hr, .hrcolor
{
	height: 1px;
	border: 0;
	color: #81878d;
	background-color: #81878d;
}

/* No image should have a border when linked */
a img
{
	border: 0;
}
/* A quote, perhaps from another post. */
.quote
{
	color: #9aa0a6;
	background-color: #1e1e21;
	border: 1px solid #909090;
	margin: 1px;
	padding: 1px;
	font-size: small;
	line-height: 1.4em;
}

/* A code block - maybe even PHP ;). */
.code
{
	color: #9aa0a6;
	background-color: #1e1e21;
	font-family: "courier new", "times new roman", monospace;
	font-size: small;
	line-height: 1.3em;
	/* Put a nice border around it. */
	border: 1px solid #909090;
	margin: 1px auto 1px auto;
	padding: 1px;
	width: 99%;
	/* Don't wrap its contents, and show scrollbars. */
	white-space: nowrap;
	overflow: auto;
	/* Stop after about 24 lines, and just show a scrollbar. */
	max-height: 24em;
}

/* The "Quote:" and "Code:" header parts... */
.quoteheader, .codeheader
{
	color: #a2a2a2;
	text-decoration: none;
	font-style: normal;
	font-weight: bold;
	font-size: x-small;
	line-height: 1.2em;
}

/* Generally, those [?] icons.  This makes your cursor a help icon. */
.help
{
	cursor: help;
}

/* /me uses this a lot. (emote, try typing /me in a post.) */
.meaction
{
	color: red;
}

/* The main post box - this makes it as wide as possible. */
.editor
{
	width: 96%;
	color: #c2c2cd;
	background-color: #131315;
}

/* Highlighted text - such as search results. */
.highlight
{
	background-color: #f58827;
	font-weight: bold;
	color: black;
}

/* Alternating backgrounds for posts, and several other sections of the forum. */
.windowbg, #preview_body
{
	color: #9aa0a6;
	background-color: #131315;

}

.windowbg2
{
	color: #9aa0a6;
	background-color: #131315;
}

.windowbg3
{
	color: #9aa0a6;
	background-color: #131315;
}

/* the today container in calendar */
.calendar_today
{
	background-color: #FFFFFF;
}

/* These are used primarily for titles, but also for headers (the row that says what everything in the table is.) */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td
{
	color: #e3e3e3;
	font-style: normal;
	background: #1b1b1b !important;
	border-bottom: solid 1px #606070;
	border-top: solid 1px #808090;
	padding-left: 10px;
	padding-right: 10px;
}

.titlebg, .titlebg a:link, .titlebg a:visited
{
	font-weight: bold;
	color: #e3e3e3;
	font-style: normal;
}

.titlebg a:hover
{
	color: #f58827;
}

/* same as titlebg, but used where bold text is not needed */
.titlebg2 a:link, .titlebg2 a:visited
{
	color: #e3e3e3;
	font-style: normal;
	font-weight: bold;
	text-decoration: none;
}

.titlebg2 a:hover
{
	text-decoration: none;
}

/* This is used for categories, page indexes, and several other areas in the forum.
.catbg and .catbg2 is for boardindex, while .catbg3 is for messageindex and display headers*/
.catbg , tr.catbg td , .catbg3 , tr.catbg3 td
{
	background: #1b1b1b !important;
	color: #d1d1d3;
	padding-left: 10px;
	padding-right: 10px;
				border-bottom: 1px solid #555560 !important;
}
.catbg2 , tr.catbg2 td
{
	background: #1b1b1b !important;
	color: #d1d1d3;
	padding-left: 10px;
	padding-right: 10px;
}
.catbg, .catbg2, .catbg3
{
	border-bottom: solid 0px #303035 !important;
}
.catbg, .catbg2
{
	font-weight: bold;
}
.catbg3, tr.catbg3 td, .catbg3 a:link, .catbg3 a:visited
{
	font-size: 95%;
	color: white;
	text-decoration: none;
}
.catbg a:link, .catbg a:visited , .catbg2 a:link, .catbg2 a:visited
{
	color: white;
	text-decoration: none;
}
.catbg a:hover, .catbg2 a:hover, .catbg3 a:hover
{
	color: #e0e0ff;
}
/* This is used for tables that have a grid/border background color (such as the topic listing.) */
.bordercolor
{
	background-color: #555560;
	padding: 0px;
}

/* This is used on tables that should just have a border around them. */
.tborder
{
	padding: 1px;
	border: 1px solid #696969;
	background-color: #0c0e0e;
}

/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */
.smalltext
{
	color: #9aa0a6;
	font-size: x-small;
	font-family: verdana, sans-serif;
}
.middletext
{
	color: #9aa0a6;
	font-size: 95%;
}
.middletext a:link, .middletext a:visited
{
	color: #e3e3e3;
	text-decoration: none;
	font-size: 95%;
}
.middletext a:hover
{
	color: #f58827;
	text-decoration: none;
	font-size: 95%;
}
.normaltext
{
	color: #9aa0a6;
	font-size: small;
}
.largetext
{
	color: #9aa0a6;
	font-size: large;
}


/* Posts and personal messages displayed throughout the forum. */
.post, .personalmessage
{
	width: 100%;
	overflow: auto;
	padding-right: 5px;
	line-height: 1.3em;
}

/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */
.signature
{
	width: 100%;
	overflow: auto;
	padding-bottom: 3px;
	line-height: 1.3em;
}

/* Sometimes there will be an error when you post */
.error
{
	color: #eb2d14;
}


/* definitions for the main tab, active means the tab reflects which page is displayed */
.maintab_first, .maintab_back, .maintab_last, .maintab_active_first, .maintab_active_back, .maintab_active_last
{
	color: #9ea4ac;
	text-transform: uppercase;
	vertical-align: top;
		border-bottom: 1px solid grey !important;	
}

.maintab_back, .maintab_active_back 
{
	color: #9ea4ac;
	text-decoration: none;
	font-size:  11px;
	font-weight: bold;
	vertical-align: top;
	padding: 2px 6px 6px 6px;
	font-family: verdana, sans-serif;
}

.maintab_first
{
	background: #1b1b1b !important;
	width: 10px;
	border-left: 1px solid grey !important;
}
.maintab_back
{        
	background: #1b1b1b !important;
}
.maintab_last
{
	background: #1b1b1b !important;
	width: 8px;
		border-right: 1px solid grey !important;
}
.maintab_active_first
{
	background: #e1771e !important;
	width: 6px;
}
.maintab_active_back
{
	background: #e1771e !important;
}
.maintab_active_last
{
	background: #e1771e !important;
	width: 8px;
}

/* how links behave in main tab. */
.maintab_back a:link , .maintab_back a:visited
{
	color: #81878d;
	text-decoration: none;
}

.maintab_active_back a:link , .maintab_active_back a:visited
{
	color: #000000 !important;
	text-decoration: none;
}

.maintab_back a:hover
{
	color: #f58827;
	text-decoration: none;
}

.maintab_active_back a:hover
{
	color: #000000;
	text-decoration: none;
}
/* definitions for the mirror tab */
.mirrortab_first, .mirrortab_back, .mirrortab_last, .mirrortab_active_first, .mirrortab_active_back, .mirrortab_active_last
{
	color: #9ea4ac;
	text-transform: uppercase;
	vertical-align: top;
				border-top: 1px solid grey !important;
}
.mirrortab_back, .mirrortab_active_back
{
	color: #9ea4ac;
	text-decoration: none;
	font-size: 9px;
	vertical-align: bottom;
	padding: 6px 6px 2px 6px;
	font-family: verdana, sans-serif;
}

.mirrortab_first
{
	background: #1b1b1b !important;
	width: 10px;
		border-left: 1px solid grey !important;
}
.mirrortab_back
{
	background: #1b1b1b !important;
}
.mirrortab_last
{
	background: #1b1b1b !important;
	width: 6px;
				border-right: 1px solid grey !important;
}
.mirrortab_active_first
{
	background: no-repeat;
	width: 6px;
}
.mirrortab_active_back
{
	background: repeat-x;
}
.mirrortab_active_last
{
	background: no-repeat;
	width: 8px;
}

/* how links behave in mirror tab. */
.mirrortab_back a:link , .mirrortab_back a:visited
{
	color: #81878d;
	text-decoration: none;
}

.mirrortab_back a:hover
{
	color: #f58827;
	text-decoration: none;
}

.mirrortab_active_back a:link , .mirrortab_active_back a:visited
{
	color: #000000;
	text-decoration: none;
}

.mirrortab_active_back a:hover
