/******************************************
Ratskep phpBB Prosilver Black. v5.1

Bespoke stylesheet for Rationalskepticism.org
to work with the forum's Prosilver style.

White on black, minimal image placeholders, resized text
and avatars, mod-control panel, comments, sigs, PMs, buttons, dropdown menus,
preferences, fast loading.

by THWOTH thwoth(at)thwoth.net Feb 2015
****************************************************/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rationalskepticism.org") {


html, body {
    width:100%;
    height:101%;
    color:#ddd;
    background-color:#000 !important;
    background-image:url('http://thwoth.net/image/ratzbg03.png');
    background-position:top;
    background-attachment:fixed;
    font-size:88% !important;
    font-family:sans-serif;
    }
#wrap {
    width:90%;
    max-width:920px;
    height:auto;
    margin:0px auto 0px auto;
    clear:both;
    }
/*TOP LINKS*/
div#wrap div#page-header div a span[style="color: #CC0000;"] strong,
  div#wrap div#page-header div a span[style="color: #804080;"] strong {
    color:#ddd !important;
    }
div#wrap div#page-header div a:hover span[style="color: #CC0000;"] strong,
  div#wrap div#page-header div a:hover span[style="color: #804080;"] strong {
    color:#ff3 !important;
    }
div#wrap > div#page-header > div {
    color:#ccc !important;
    }  


h3 {
    color:#fff !important;
    text-shadow:1px 1px 1px #000000;
    }
a {
    -moz-transition:color 555ms;
    -webkit-transition:color 555ms;
    -o-transition:color 555ms;
    transition:color 555ms;
    }
a, a:link, a:active {
    color:#eee !important;
    font-weight:bold;
    text-decoration:none !important;
    }
a:hover, a:visited {
    color:#dd3 !important;
    }
p {
    color:#ccc !important;
    }
div.navbar {
    color:#ddd !important;
    font-size:0.80em !important;
    background-color:transparent !important;
  /*  background-image:none !important;   */
    margin:0px auto 44px auto !important;
    display:block;
    clear:both;
    }
div.navbar .inner li, div.navbar .inner li a {
    text-shadow:2px 2px 5px #000000;
    }
ul.linklist.navlinks li.icon-home, ul.linklist.leftside li.icon-ucp, ul.linklist.rightside li.icon-logout {
    background-image:none !important;
    background-color:transparent !important;
    padding-left:0px !important;
    }
 div.navbar div.inner ul.linklist a, div#page-header a {
    color:#ddd !important;
    /*background-image:none;*/
    }
div.navbar div.inner ul.linklist a:hover, div#page-header a:hover {
    color:#dd2 !important;
    }
div#page-body .inner {
    background:transparent none no-repeat 0 0 !important;
    }
div.inner span.corners-top, div.inner span.corners-bottom, #acp span.corners-top, #acp span.corners-bottom {
    height:0px;
    width:0px;
    display:none;
    }
div#page-header div.headerbar {
    background-color:transparent !important;
    background-image:none !important;
    margin:12px auto 22px auto !important;
    }
    
#search-box a {
    text-shadow:2px 2px 4px #000000;
    }
div#site-description {
    padding-left:0px !important;
    }    
div#site-description h1 {
    color:#fff;
    font-size:2.6em !important;
    font-variant:small-caps;
    text-shadow:3px 3px 8px #000 !important;
    letter-spacing:0.05em;
    }
div#site-description p {
    width:100% !important;
    height:auto !important;
    font-size:0px !important;
}
div#site-description:after {
    content:'A Lifeboat For The Rational Mind';
    font-size:1.1em !important;
    line-height: normal;
    font-weight:bold;
    color:#ddd;
    letter-spacing:0.09em;
    font-variant:small-caps;
    text-shadow:2px 2px 3px #000000;
    }
div#message.panel {
    color:#aaa !important;
    background-color:#222 !important;
    border:1px solid #333 !important;
    border-radius:6px;
    box-shadow:2px 2px 8px rgba(0,0,0,0.5);
    }
div#message.rules, div#page-body div.rules, div.postbody p.rules {
    color:#ccc !important;
    background-color:#444 !important;
    padding:2px 12px !important;
    text-align:left !important;
    border:3px solid #555;
    border-radius:1.2em;
    box-shadow:2px 2px 12px rgba(0,0,0,0.5);
    margin:11px auto 33px auto !important;
    }
div#message.rules:hover, div#page-body div.rules:hover, div.postbody p.rules:hover {
    background-color:#600 !important;
    border-color:#c00 !important;
    }
div#message.rules:hover strong, div#page-body div.rules:hover a, div#message.rules:hover a, div.postbody p.rules:hover a {
    color:#ff3 !important;
    }
    
div#message p, form#confirm p, form#donate p {
    color:#ccc;
    }
div#wrap div#page-body form#confirm {
    background:#222 url('http://thwoth.net/images/bg/blackwood2.png') left repeat !important;
    }
div#wrap div#page-body form#confirm .panel, div#wrap div#page-body form#confirm .panel .inner {
    background-color:transparent !important;
    }
div#message h2, form#confirm h2, form#donate h2 {
    color:#bbb;
    border-bottom:2px dotted #999 !important;
    }

#announce_header.rules span a.postlink {
    text-decoration:none !important;
    border:0px !important;
    }
#announce_header.rules span a.postlink:hover {
    color:#ff3 !important;
    background-color:inherit !important;
    text-decoration:none !important;
    }

div#page-body h2, div#page-body h2 a {
    color:#eee !important;
    text-shadow:2px 2px 4px #000000;
    margin-bottom:33px !important;
    }

div#page-body a.left {
    color:#ccc !important;
    background-image:none !important;
    padding-left:0px !important;
    }
div#page-body form#postform h3 {
    color:#eee !important;
    }
div#page-body form#confirm div.panel, div#page-body form#report div.panel {
    background-color:#222 !important;
    border:1px solid #444;
    border-radius:7px;
    box-shadow:2px 2px 12px rgba(0,0,0,0.5);
    }
form#report div.panel textarea#report_text.inputbox {
    color:#fff !important;
    background-color:#000 !important;
    }
div#page-body div#comment.panel {
    background-color:#222 !important;
    border:1px dotted #333 !important;;
    border-right:2px solid #111 !important;;
    border-bottom:2px solid #111 !important;;
    }
div#page-body div#comment.panel h3 {
    color:#ccc !important;
    }
dl.left-box.details dt, dl.left-box dd {
    color:#888 !important;
    font-weight:bold;
    }
dl.left-box.details dd {
    color:#ccc !important;
    font-weight:normal !important;
    }
dl.left-box dt img {
    background-color:#eee;
    padding:4px !important;
    }
div.column1 dl.details dt, div.column2 dl.details dt {
    color:#aaa !important;
    font-weight:bold;
    }
#viewprofile div.column2 dl.details dd {
    color:#ccc !important;
    }
#viewprofile div.column1 h3, #viewprofile div.column2 h3 {
    color:#ddd !important;
    text-shadow:1px 1px 3px #000000;
    }
div#page-footer ul.linklist a {
    color:#ccc !important;
    font-size:0.80em !important;
    }
div#page-footer div.copyright {
    color:#888 !important;
    }
div#page-footer div.copyright a {
    color:#999 !important;
    }
div#page-footer div.copyright a:hover {
    color:#bb3 !important;
    }

/* --- FORUMBG TOPICLIST --- */
div#page-body div.forumbg, div#page-body div.forabg {
	background-color:transparent !important;
	background-image:none !important;
    border:0px;
    padding:0px;
    margin:0px auto 0px auto !important;
    }
div.forumbg div.inner ul.topiclist {
    border-radius:1em 2em 3em 4em;
    }
ul.topiclist li.header dt {
    color:#eee !Important;
    padding-left:3px !important;
    }
ul.topiclist li.header dl.icon {
    border-top:1px dotted #aaa;
    background-color:#212121 !Important;
    }
ul.topiclist li.header dd, ul.topiclist li.header dt {
    color:#ddd !important;
    font-size:1.3em !important;
    font-weight:bold;
    }
ul.topiclist li, ul.topiclist.cplist li {
    color:#ccc !important;
    border:1px dotted #aaa !important;
    border-top:0px !important;
    }
ul.topiclist dl dd, ul.topiclist.cplist dl dd {
    color:#ccc !important;
    }
ul.topiclist dl.icon dt, ul.topiclist dl.icon dd {
    height:auto !important;
    min-height:50px !important;
    }
ul.topiclist li.header dt, ul.topiclist li.header dd {
    max-height:1.2em !important;
    min-height:1.2em !important;
    padding-top:6px !important;
    }

ul.topiclist li.row {
    color:#ddd !important;
    background-color:#111 !important;
    }
ul.topiclist li.row.bg1 {
    color:#ddd !important;
    background:#222 /* url('http://thwoth.net/images/bg1.png') top repeat */ !important;
    }
ul.topiclist li.row.bg2 {
    color:#ddd !important;
    background:#333 /* url('http://thwoth.net/images/bg2.png') top repeat */ !important;
    }
ul.topiclist dl.icon:hover {
    background-color:#201 !important;
    }
ul.topiclist dl.icon dt strong {
    color:#ccc !important;
    font-weight:normal !important;
    font-variant:small-caps;
    }
ul.topiclist dl.icon dd.posts, ul.topiclist dl.icon dd.views, ul.topiclist dl.icon dd.lastpost {
    height:auto !important;
    border-left:1px dotted #aaa !important;
    }

ul.topiclist li.header dd.posts, ul.topiclist li.header dd.views, ul.topiclist li.header dd.lastpost {
    height:auto !important;
    border:0px !important;
    }

    
ul.topiclist strong.pagination {
    color:#ccc !important;
    }
ul.topiclist strong.pagination span a {
    color:#ddd !important;
    background-color:#000 !important;
    border:1px solid #aaa;
    border-radius:1em;
    padding:0px 2px;
    }
ul.topiclist strong.pagination span a:hover {
    color:#000 !important;
    background-color:#ddd !important;
    border:1px solid #ddd;
    }
ul.topiclist dt strong.pagination {
    background-image:none !important;
    }


a.topictitle, a.topictitle:link, a.topictitle:active, a.topictitle:focus, a.topictitle:visited {
    color:#fff !important;
    text-decoration:none !important;
    border:0 !important;
    }
a.topictitle:hover {
    color:#ff3 !important;
    text-decoration:underline !important;
    }
ul.topiclist a {
    color:#ddd !important;
    font-weight:bold;
    }
ul.topiclist a:hover {
    color:#ff3 !important;
    }



div.back2top {
    width:auto !important;
    height:10px !important;
    float:right;
    clear:both;
    }
a.top, a.print {
    font-size:0px !Important;
    width:12px !important;
    height:12px !important;
    background-image:none !important;
    background-color:#369 !important;
    border:1px solid rgba(0,0,0,0);
    border-radius:100%;
    box-shadow:0px 0px 4px 2px rgba(0,0,0,0.5);
    margin-top:22px !important;
    }
a.top:hover, a.print:hover {
    background-color:#ee3 !important;
    }
a.print {
    float:left;
    }
.rightside a.print {
    position:relative;
    right:12px;
    top:-18px;
    }
/* --- VIEWTOPIC --- */

div.post {
    width:100%;
    height:auto;
    padding:6px !important;
    margin:9px auto !important;
    border:1px dotted #666 !important;
    border-radius:6px;
    display:block !important;
    }
div.post.bg1 {
    color:#eee !important;
    background:#111 /* url('http://thwoth.net/images/bg1.png') top repeat */ !important;   
    }
div.post.bg2 {
    color:#fff !important;
    background:#222 /* url('http://thwoth.net/images/bg2.png') top repeat */ !important;
    }
.post.bg1.online, .post.bg2.online, .panel.bg1.online, .panel.bg2.online, #cp-main.ucp-main .panel.online {
    background-image:url('http://thwoth.net/images/online-dot-purple.png') !important;
    }   
#page-body .bg1 {
    background:#111 /* url('http://thwoth.net/images/bg1.png') top repeat */ !important;
    }
#page-body .bg2 {
    background:#222 /* url('http://thwoth.net/images/bg2.png') top repeat */ !important;
    }
#page-body .bg3 {
    background:#333 /* url('http://thwoth.net/images/bg3.png') top repeat */ !important;
    }
#cp-main.ucp-main .panel {
    border:1px solid transparent !important;
    border-radius:12px;
    }
#cp-main .panel, #cp-main.ucp-main form#viewfolder .panel {
    color:#aaa !important;
    background-color:#000 !important;
    }
#cp-main.ucp-main form#viewfolder ul.topiclist li.header {
    border-top:1px solid #aaa !important;
    background-color:#111 !important;
    }
#cp-main div.postbody h3.first, #cp-main dl.details dd, #cp-main div.panel h3 {
    color:#fff !important;
    }
#cp-main #mcp .table1 td {
    color:#ddd !important;
    }
#cp-main h3#review {
    color:#aaa !important;
    font-weight:normal !important;
    }    
#cp-main.mcp-main div.postbody {
    color:#ccc !important;
    }
#cp-main.mcp-main div.panel {
    background-color:#000 !important;
    }
#cp-main.mcp-main ul.topiclist dd.moderation {
    width:33%;
    height:auto;
    border-left:1px dotted #aaa !important;
    float:right !important;
    }
#cp-main.mcp-main ul.topiclist li.bg1:hover {
    background-color:#404 !important;
    }
#cp-main.mcp-main ul.topiclist li.bg2:hover {
    background-color:#301 !important;
    }
#cp-main.mcp-main ul.topiclist li.header {
    border-top:1px solid #aaa !Important;
    border-bottom:0px !important;
    }
#cp-main.mcp-main div#minitabs {
    position:relative;
    font-size:0.80em !important;
    }
#cp-main.mcp-main div#minitabs li {
    background-color:#222 !Important;
    background-image:none !Important;
    border:1px dotted #000000;
    }
#cp-main.mcp-main div#minitabs li.activetab {
    background-color:#000 !important;
    }
#cp-main.mcp-main div#minitabs li:hover {
    background-color:#444 !Important;
    }
#cp-main.mcp-main div#minitabs li a {
    color:#ccc !important;
    }
#cp-main.mcp-main div#minitabs a:hover {
    color:#fff !important;
    }

/* --- POLS --- */
form div.panel {
    background-color:#222 !important;
    }
fieldset.polls {
    padding:8px !important;
    }
fieldset.polls dl {
    border-top:1px dotted #aaa !important;
    }
fieldset.polls dl dd {
    text-align:right !important;
    }
fieldset.polls dl dd.resultbar {
    text-align:left !important;
    }

/*--- MOD NOTES ---*/
div#ip table.table1 tbody td {
    color:#ccc !important;
    font-weight:bold !important;
    background-color:#000 !important;
    border-bottom:1px dotted #aaa !Important;
    border-top:0px !Important;
    }
div#ip table.table1 tbody td:hover {
    color:#fff !important;
    background-color:#c00 !important;
    }
div#ip table.table1 thead th.name {
    color:#ddd !Important;
    font-weight:bold !important;
    border-bottom:1px dotted #aaa !Important;
    }
/* --- LOGIN -- */
form#login div.panel, div form#agreement div.panel {
    background-color:#222 !important;
    }
form#login div.panel div.content, form#login div.panel div.content p, form#agreement div.content p {
    color:#ddd;
    }
form#login div.panel div.content h2, form#agreement div.content h2 {
    color:#eee !important;
    }
select#f, select#quick-mod-select, select#report_id {
    font-size:0.88em !important;
    }

/* -- BUTTONS -- */
a.button1, a.button2,
  button.button1,
   button.button2,
     input[type="submit"].button1,
       input[type="submit"].button2,
         input[type="reset"].button1,
           input[type="reset"].button2, 
             select, select[name="g"],
               div#format-buttons input[type="button"].button2 {
    appearance:none !important;
    -webkit-appearance:none !important;
    -moz-appearance:none !important;
    color: #ccc !important;
    background: #222 !important;
    border: 2px solid #888 !important;
    border-radius:10px;
    font-size: 0.9em !important;
    font-weight: bold;
    text-align:center !important;
    text-decoration:none !important;
    vertical-align: middle !important;
    padding:3px 6px 3px 6px !important;
    box-shadow:0px 2px 4px rgba(0,0,0,0.5);
    -moz-box-shadow:2px 2p 2px rgba(0,0,0,0.5);
    text-shadow:1px 1px 2px rgba(0,0,0,0.5);
    transition:background-color 0.3s,color 2s,border-color 1s;
    -webkit-transition:background-color 0.3s,color 2s,border-color 1s;
    }
a.button1:hover, 
  a.button2:hover,
    input[type="submit"]:hover,
      input[type="submit"]:hover,
        input[type="submit"]:focus,
          input[type="reset"]:focus, 
            select:hover, select:focus, select[name="g"]:hover, select[name="g"]:focus,
              div#format-buttons input[type="button"].button2:hover {
    -webkit-appearance:none !important;
    -moz-appearance:none !important;
    color: #fff !important;
    background: #444 !important;
    border-color: #aaa !important;
    }
.reply-icon span, .locked-icon span, .post-icon span {
    color:#cccccc;
    background-image:linear-gradient(rgba(11,11,11,0.6) 10%, rgba(0,0,0,0)) !important;
    background-color:#112233 !important;
    text-align:center;
    text-shadow:1px 1px 0px #000000;
    font-size:0.6em;
    font-weight:normal;
    padding:3px 0px;
    border:2px solid #555555;
    box-shadow:inset -4px -4px 1px #555555;
    margin: 0;
    vertical-align:middle !important;
    -moz-transition:background-color 500ms;
    -o-transition:background-color 500ms;
    -webkit-transition:background-color 500ms;
    transition:background-color 500ms;
    }
.reply-icon span:hover, .locked-icon span:hover, .post-icon span:hover {
    color:#ffffff;
    background-color:#435363 !important;
    }
.reply-icon span::after {
    content: "Reply";
    }
.locked-icon span::after {
    content: "Locked";
    }
.post-icon span:after {
    content: "New Topic";
    }
select {
    text-align:left !important;
    padding-left:4px !important;
    border-radius:10px 0px 0px 10px;
    }
select option {
    color:#ddd !important;
    text-align:left !important;
    padding-left:4px !important;
    }
div#format-buttons input[type="button"].button2 {
  margin-bottom:2px !important;
  }
::selection {
    color:#fff;
	background: #369636; /* Safari */
	}
::-moz-selection {
    color:#fff;
	background: #369636; /* Safari */
}



/* -- SPOILERS ---*/
.spoiler .spoiler-box .spoiler-hidden {
    background-color:#421 !important;
    }

/* --- SEARCHES --- */
label {
    color:#aaa !important;
    font-size:0.66em !important;
    }
select {
    font-size:0.66em !important;
    }
div.searchbox input.inputbox.narrow {
    color:#fff !important;
    background-color:#888 !important;
    border-color:#aaa !important;
    }

form fieldset input[type="text"], input[type="text"], input[type="password"] {
    -moz-appearance:none !important;
    appearance:none !important;
    height:1.2em !important;
    vertical-align:middle !important;
    color:#fff !important;
    background:#333 !important;
    font-size:1em !important;
    padding:4px 6px 4px 6px !important;
    border:2px solid #666 !important;
    border-radius:7px;
    }
/* -- PAGINATION --- */
div.topic-actions .pagination, div.topic-actions .pagination a, ul.linklist .pagination a {
    color:#ccc !important;
    font-size:0.66em !important;
    }
div.topic-actions .pagination a, ul.linklist .pagination a {
    font-weight:bold;
    }
div.topic-actions .pagination a:hover, ul.linklist .pagination a:hover {
    color:#dd3;
    }
div.topic-actions .pagination span a, ul.linklist .pagination span a {
    color:#ccc !important;
    background-color:#333 !important;
    border:1px solid #eee;
    }
div.topic-actions .pagination span a:hover, ul.linklist .pagination a:hover {
    color:#333 !important;
    background-color:#ccc !important;
    border:1px solid #999;
    }
div.topic-actions .pagination span strong, ul.linklist .pagination span strong {
    color:#000 !important;
    background-color:#ddd !important;
    }
div.topic-actions .pagination span a:hover strong, ul.linklist .pagination span a:hover strong {
    color:#000 !important;
    background-color:#ccc !important;
    border:1px solid #ccc;
    }

/* --- POSTING -- */
form#postform div.panel {
    color:#ccc !important;
    background-color:#222 !important;
    }
form#postform div#message-box {
    border:3px solid #aaa;
    margin-top:12px !important;
    }
form#postform div#message-box #message {
    color:#ddd !important;
    font-weight:bold !important;
    background-color:#000 !important;
    border:1px solid #ddd;
    }
/* -- LOWER TABS -- */
#options-panel.bg1 div.inner {
    background:#111 /* url('http://thwoth.net/images/bg1.png') top repeat */ !important;
    } 
#options-panel.bg2 div.inner {
    background:#222 /* url('http://thwoth.net/images/bg2.png') top repeat */ !important;
    }   
#options-panel.bg3 div.inner {
    background:#333 /* url('http://thwoth.net/images/bg3.png') top repeat */ !important;
    }
#options-panel.bg1 label, #options-panel.bg2 label, #options-panel.bg3 label {
    color:#ddd !important;
    }  

form#postform div#tabs ul li#options-panel-tab.activetab a, form#postform div#tabs ul li#options-panel-tab.activetab a span {
    color:#fff !important;
    background-color:#333 !important;
    background-image:none !important;
    font-weight:bold;
    font-variant:small-caps;
    border:0px !important;
    }
form#postform div#tabs ul li#attach-panel-tab a, form#postform div#tabs ul li#attach-panel-tab a span {
    color:#fff !important;
    background-color:#222 !important;
    background-image:none !important;
    font-weight:bold;
    font-variant:small-caps;
    }
form#postform div#tabs ul li#options-panel-tab.activetab a:hover span, form#postform div#tabs ul li#attach-panel-tab a:hover span {
    color:#dd3 !important;
    }

div#preview.post.pm {
    background-color:#000 !important;
    border:1px dotted #555;
    border-top:0px;
