css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("luoo.net") {
BODY,
#luooPlayerSingle,
.track-info.clearfix,
#luooPlayerPlaylist.luoo-player,
#luooPlayerTracklist,
.head,
.toolbar{background:none #C7EDCC !important;}
}

@-moz-document 
url("http://www.luoo.net/"),
regexp("http://www\.luoo.net/music/((?!tag).)*"),
regexp("http://www\.luoo.net/anbai/((?!tag).)*"),
regexp("http://www\.luoo.net/china/((?!tag).)*"),
regexp("http://www\.luoo.net/world/((?!tag).)*"),
regexp("http://www\.luoo.net/electronic/((?!tag).)*"),
regexp("http://www\.luoo.net/classical/((?!tag).)*"),
regexp("http://www\.luoo.net/jazz/((?!tag).)*"),
regexp("http://www\.luoo.net/vocal/((?!tag).)*"),
regexp("http://www\.luoo.net/special/((?!tag).)*")
{
BODY,
#main,
.clearfix,
.volindex-aside,
.volindex-article,
.widget-content{
width:300px !important;
margin-left:auto !important;
margin-right:auto !important;
}

.volindex-article{
position: absolute !important;
margin-top: 1180px !important;
}

#main{margin-top:-35px !important;}

.track-info.clearfix{
width:200px !important;
margin-left:auto !important;
margin-right:auto !important;
border: 1px solid rgba(111,0,255,.9) !important;
border-radius:5px;
text-align:center
}

.cover{
margin-left:20px !important;
margin-right:auto !important;
border: 1px solid rgba(156,0,0,.9) !important;
border-radius:2px;
}

.toolbar{
width:200px !important;
margin-left:auto !important;
margin-right:auto !important;
}

.btn-action-like.icon-like-large-actived,.btn-action-like.icon-like-large{
position: absolute !important;
margin-top: -85px !important;
margin-left:142px  !important;
}

.luoo-scroller-page.clearfix{
width:180px !important;
margin-left:auto !important;
margin-right:auto !important;
text-align:center
}

.widget.luoo-scroller.relative-vols{
position: absolute !important;
margin-top: -10px !important;
width:300px !important;
}

.widget-title.clearfix
{width:280px !important;
margin-top: 20px !important;
margin-left:auto !important;
margin-right:auto !important;
}

#fmWrapper{
width:300px !important;
margin-left:-60px !important;
margin-right:auto !important;
margin-top:-5px !important;
}

.tags-wrapper.clearfix{
width:285px !important;
margin-left:auto !important;
margin-right:auto !important;
}

#volSelectorWrapper{
width:280px !important;
margin-left:auto !important;
margin-right:auto !important;
text-align:center
}

.ad-wrapper,
.comment.index-comment,
.fm-cover,
.head,
.fm-info,
.foot,
.progress.jp-volume-bar,
#luooActiveTips
{display:none !important;}
}
