css:
@-moz-document regexp(".*\\.trade\\.taobao\\.com.*") {
div#detail-panel, 
div.feedback,
div#immediate-help-trigger, 
div#footer,
div.banner,
div#site-nav-bd, 
div.wrap-box, 
div#nav-crumbs, 
div.helpline, 
div#J_TopBanner, 
div.banner~div, 
div#J_GlobalRobotEntry, 
div#tstart-tdog-enable, 
div#decSaleTopBanner, 
.J_Tuijian_Modules_OrderDetail, 
div#tstart
{display:none!important;}

div#J_TabView {margin-top: -20px!important;}

div#header {margin-top:-25px!important;}

.order-info dl {line-height:1em;}
.order-info dd, .order-info dt {min-height:1.5em!important;}
.get-money {padding-top:0;}
.tabs-panels {margin:0!important;}
#site-nav {border-style:none;}
}

@-moz-document url-prefix("https://lab.alipay.com/consume/queryTradeDetail.htm"), url-prefix("https://lab.alipay.com/consume/transfer/detail.htm"), url-prefix("https://shenghuo.alipay.com") {
div.tip-extra  ,
div#onlineService, 
.global-subheader,
.global-nav-item-arrow, 
div.global-top {
  display:none!important;
}

#container, #main {width: 1000px!important; }

.tb-border th{width: 5.5em!important;}

.p-trade-list .explain , td.amount, .time {color: black!important; font-family: Arial;}

th {text-align:left;}

.tip-extra {padding: 0}

table.consume-detail-memo tbody tr:nth-child(2) {display:none;}
table.memo-tb-inner {border-color: white!important;}

.p-trade-list {margin-top: 0px;}

.tip-faq, .ui-footer {display:none}

.memo-tb-inner {line-height: 0px!important;}

#container {height: 0!important;}
}

@-moz-document url-prefix("http://trade.taobao.com/trade/detail/trade_item_detail.htm?outTradeNo=") {
div#site-nav, 
#immediate-help-trigger, 
.feedback, 
div.tabs-container ~ div, 
#footer, 
.order-dashboard, 
#detail-panel,
p.attention, 
#nav-crumbs,
.wrap-box, 
.helpline, 
span.ww-light a
{
  display:none;
}

.order-ft .get-money strong {color: black;}

div#content {margin-top: -20px;}

div#header {display:none;}

div#page {padding-top: 50px; margin-left: 50px;}

div.tabs-panels {margin-right: 20px;}

.order-info dt, .order-info dd {min-height: 0px!important; margin: 0!important; padding: 0!important; line-height: 1.5em;}
}

@-moz-document url-prefix("http://jipiao.trip.taobao.com/view_order.htm"), url-prefix("http://jipiao.trip.taobao.com/tripViewOrder.htm") {
span.price, span.pi-price {
  color: black!important;
  font-weight: bold!important;
}

.site-nav {
    display:none!important;
}

body {
  color: black!important;
}	

div.confirm-tips  {
  display:none!important;
}

section {
  padding-top: 0!important;
  padding-bottom: 0!important;
  margin-top: 0!important;
  margin-bottom: 0!important;
}

div#content {
  margin-top: 0!important;
  margin-bottom: 0!important;
}

div.mui-mbar-tabs, 
div.mui-mbar-mini, 
div.tstart-tdog-enable {
  display:none!important;
}
}

@-moz-document url("https://kyfw.12306.cn/otn/queryOrder/printMyOrderPage") {
.order .lay-bd, .content {min-height: 0px!important;}

.content {margin: 100px 0px 0px 200px!important;}
}

@-moz-document url-prefix("https://lab.alipay.com/consume/transfer/detail.htm?billNo=") {
/*https://lab.alipay.com/consume/transfer/detail.htm?billNo=*/

#onlineService, 
.global-top, 
.tip-extra, 
#faq, 
.ui-footer {
  display:none;
}
    
div.p-trade-detail table tr td:nth-child(1) {
    width: 5em;
}

.title .link {
    display:none;
}
    
.result-prog, .result-progbar {
    display:none;
}
}

@-moz-document url-prefix("https://pbsz.ebank.cmbchina.com/") {
.tbExplain,
#trAdv {
    display:none!important;
  }
}

@-moz-document url-prefix("http://trade.tmall.com/detail/orderDetail.htm") {
#site-nav, 
div.crumbs, 
.flowstep, 
#J_trade_status,
#J_MUIMallbar, 
.rap-box,
.header-extra, 
#footer, 
tbcc, 
#J_GlobalRobotEntry					
{display:none!important;}

#J_bought_listform .item-meta .item-link 
{white-space:normal!important; min-height:auto!important; height:auto!important;}
}

@-moz-document url-prefix("http://manage.infocall.cn:8089/") {
* {font-family: 'Times New Roman', SimSun!important; font-size: 14px;}
table.t1 td {padding: 0!important; text-align: center!important;}
div#Div_Info {width: 90%!important; margin: 0 auto!important;}
fieldset {border-style: none!important;}
table {width: 100%!important; margin: 0 auto!important;}

span#lblUpperAllFee, span#lblAllFee {font-weight: bold!important;}
}

@-moz-document url("https://zd.alipay.com/ebill/financeEbill.htm") {
#onlineService,
#save-wapper, 
#bill-wapper, 
#consume-wapper, 
.ui-comsume-header,
.ui-footer,
#globalMore, 
.global-subheader {
  display:none!important;
}
  
.global-common-a .global-nav {margin-right: 20px!important;}
}
