css:
@-moz-document domain("geizhals.at"), domain("geizhals.net"), domain("geizhals.de"){


body#ghbody { background: #eee; }
div#gh_content_wrapper { min-height: inherit; }
.gh_cat { background: #FFF; }
a { color: #115699; text-decoration: none; }
a:visited { color: #85a4c1; }

#gh_brcr, #gh_statline, #gh_boxfm_js, .ghinfobox[style*="background-color:#ECFAFF"] { display: none; }

#gh_hdr { background-color: #115699; background-image: none !important; }

#gh_hdr_tabs { background: rgba(255,255,255,0.05); box-shadow: rgba(0,0,0,.2) 0 -1px 50px; }
.gh_tabs { background: none !important; }
a.gh_tabs { border: none; }

#gh_leftnav { background: rgba(0,0,0,0.1) !important; color: #444 !important; box-shadow: 0px 0px 200px 0px rgba(0,0,0,0.15) !important; border-right: 1px solid rgba(0,0,0,0.1); }
#gh_leftnav a { color: #000 !important; font-weight: bold; font-size: 10px; }
ul.ghnavsub { background: none; }
#gh_leftnav ul.ghnavsub a { color: #333 !important; font-weight: normal;}
.ghloginbox { background: none; }

li.mkat { background: #FFF; width: 47%; }
@media (max-width: 1142px) { li.mkat { width: 100%; } }
li.mkat a, li.mkat div { margin: 5px; }

#ghbody div.ghinfoboxh { background: rgba(0,0,0,0.05); }

h1.gh_listtitle { background: none; border: none; }

.gh_gradient0 { background: rgba(0,0,0,0.05); color: rgba(0,0,0,0.75); }

.filterbox, #xf_div, #ghbody #gh_afilterbox { margin-bottom: 10px; }

.xf_tr_even, .xf_tr_odd { border-bottom: 1px solid rgba(0,0,0,0.03); }
.xf_th, .xf_td { padding-top: 5px; padding-bottom: 5px; }
.xf_th { background: none; border: none; }
.xf_tr_even { background: rgba(255,255,255,1.00); }
.xf_tr_odd { background: rgba(255,255,255,0.75); }

.tr2, .tr1 { background: rgba(0,0,0,0.05); }
.tr2 > th { border: none; color: rgba(0,0,0,0.5);}
.tr2 > th a { color: rgba(0,0,0,0.5); }
#cmp_hdr { background: none !important; }
td.ty { background: none; }
tr.t1, tr.t2 { border-bottom: 1px solid rgba(0,0,0,0.03); }
tr.t1 { background: rgba(255,255,255,1.00); }
tr.t2 { background: rgba(255,255,255,0.75); }
tr.t1 > td, tr.t2 > td { border: none; background: none; }

#ghbody .box10h { background: rgba(0,0,0,0.05); border: none; }

.gh_cat { border: 1px solid rgba(0,0,0,0.1); border-radius: 0; }

.gh_listtitle, div.arthdr { background: none; border: none; padding: 0; }

#gh_proddesc { background: none; border: none; }

.lh1 { background: none; }
th.lh1, .tr2 > th { border: none; }

div#monav { border-bottom: 1px solid rgba(0,0,0,0.2); background: linear-gradient(to bottom, rgba(255,255,255,0.00) 0%,rgba(0,0,0,0.05) 100%); }
span.monav, span.monav_disabled { border-radius: 0; }
span.monav_disabled { border: rgba(0,0,0,0.15) 1px solid; border-bottom: none; font-weight: bold; }
span.monav { border: rgba(0,0,0,0.05) 1px solid; background: rgba(0,0,0,0.1); border-bottom: none; }
span.monav:hover { background: rgba(0,0,0,0.05); }
span.monav a { color: rgba(0,0,0,0.75); font-weight: bold; }

@media (max-height: 1500px) and (max-width: 1336px) {
#img_container { height: auto !important; }
}

}
