css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("douban.com"){

/*主体宽度*/
#wrapper{width:980px !important;}

/*主体圆角*/
#wrapper{
border:1.5px solid #5d5d5d !important;
padding:20px !important;
border-radius:15px;
box-shadow:0px 3px 3px #333;}

/*友邻输入框*/
.item{overflow:visible !important;}
#isay-cont{width:654px !important;overflow:visible !important;}
.btn{width:670px !important;
top:10px !important;
background:none !important;
border-color:transparent !important;
border:0 !important;
box-shadow:none !important;}

/*友邻条目个人主页去边框背景*/
.bd,.lp-sep.lp-loaded{
background:none !important;
border-color:transparent !important;
border:0 !important;
box-shadow:none !important;}
.ex1,.ex2{display:none !important;}

/*侧栏宽度*/
.fixed-fields{width:260px !important;}

/*右侧栏H1~H4 Style*/
H1{
background:none !important;
margin-bottom:10px !important; 
font-size:26px !important; 
line-height:44px !important;
}
H2{
background:none !important;
width:auto !important;
border-radius:1px !important;
padding:1px !important;
font-weight:bold !important;
}
H3{
background:none !important;
border-radius:1px !important;
padding:4px 3px !important;
}
H4{
background:none !important;
margin-left:-5px !important;
padding-left:3px !important;
border-radius:3px !important;
}
#events,.entrance-link.first-link{display:none !important;}

/*豆瓣顶部固定状态栏*/
.global-nav{
height:28px;
color:#d5d5d5;
background-color:#545652;
min-width:960px;
padding:0 12px 0 0;
position:fixed !important;
margin:auto;
z-index:15 !important;
width: 100%;
border-bottom:1px solid #ebf8f9;
}

.global-nav-a .bd{width:960px;margin:0 auto}
.nav .nav-wrap{margin-top:28px !important;border-bottom:1px solid #e5ebe4}
}

/*友邻界面主体内容宽度*/
@-moz-document url("http://www.douban.com/"){
.stream-items{width:690px !important;}
}

/*友邻广播界面宽度*/
@-moz-document regexp("http://www\.douban.com/people/.*/statuses"){
.stream-items{width:980px !important;}
.pl2{display:none !important;}
}

/*小组话题列表宽度*/
@-moz-document url-prefix("http://www.douban.com/group"){
.topics{width:670px !important;}
.fixed-fields{background:none !important;}
#db-global-nav{margin-top: -28px !important;}
}

/*豆瓣乐评*/
@-moz-document url-prefix("http://music.douban.com/review"){
.article{width:920px !important;}
.aside{display:none !important;}
}

/*豆瓣影评*/
@-moz-document url-prefix("http://movie.douban.com/review"){
.article{width:960px !important;}
#fixedInfo{display:none !important;}
}

/*豆瓣书评*/
@-moz-document url-prefix("http://book.douban.com/review"){
.article{width:920px !important;}
.aside{display:none !important;}
}

/*豆瓣日记*/
@-moz-document url-prefix("http://www.douban.com/note"){
.article{width:980px !important;}
.aside{display:none !important;}
}

/*手机豆瓣*/
@-moz-document domain("m.douban.com"){

.logo{display:none !important;}
.wrapper{margin-top:30px !important;}

.nav{
margin-left:-5px !important;
margin-top:-35px !important;
position:fixed !important;
margin:auto;
z-index:15 !important;
width: 100%;
border-bottom:1px solid #ebf8f9;
}
}

/*豆瓣电台*/
@-moz-document url("http://douban.fm/"){

.show-channel-detail,#fm-section-app-entry,#fm-sidebar,
#fm-bg,.fm-side-scrollbar,#fm-sharing,#ft-ads-slot,#fm-bonus,
.pro-promo,#logo,#fm-banner-ad-slot,#fm-footer{
display:none !important;}
    
#fm-channel-list{z-index:42 !important;}
#fm-section{min-height:0 !important;}

#fm-channel-list .ch-list {margin: 20px 0 10px 12px !important;}
#fm-section3{padding:0!important;}
#fm-channel-list,.ch-list-container{overflow:hidden !important;}
.ch-list-wrapper{padding:33px 15px !important;}

#fm-bg{background:none !important;}

html{
background-color:black;
background-image:
radial-gradient(white, rgba(255,255,255,.2) 2px, transparent 40px),
radial-gradient(white, rgba(255,255,255,.15) 1px, transparent 30px),
radial-gradient(white, rgba(255,255,255,.1) 2px, transparent 40px),
radial-gradient(rgba(255,255,255,.4), rgba(255,255,255,.1) 2px, transparent 30px);
background-size: 550px 550px, 350px 350px, 250px 250px, 150px 150px; 
background-position: 0 0, 40px 60px, 130px 270px, 70px 100px;
}
body{
font-family:Arial,sans-serif !important;
font-size:13px !important;
background:url(chrome://userchromejs/content/IMAGE/fm.png) !important;
background-repeat:no-repeat !important;
background-position:70% 95% !important;
background-size:auto 45% !important;
background-color:black;
}

.ch-list-container {width:1022px !important;}

.menu{
opacity:.8 !important;
border: none !important;
border-radius:3px !important;
}
 .menu li.hover{border-radius:3px !important;}
    
#fm-channel-list .chl_section .title .line {border:none !important;}
    
.channel_list li.channel{
font-size:16px !important;
 width:240px !important;
height:44px !important;
line-height:44px !important;
}
#fm-channel-list .ch-list-wrapper{width:280px !important;}

 .channel_list li.channel .chl_name{
border-radius: 22px !important;
border-bottom-left-radius:3px !important;
border-top-left-radius:3px !important;
width:240px !important;
background:rgba(255,255,255,.8) !important;
opacity:.7 !important;
transition: background 0.4s linear 0s,border 0.3s linear 0s,margin 0.3s linear 0s !important;
border-left: 8px solid #d8EBE0 !important;
}
.channel_list li.selected .chl_name {
border-left:8px solid transparent  !important;
background-color: #9DD6C5 !important;
opacity:1 !important;
}
.channel_list li.hover .chl_name{
opacity:1 !important;
margin-left:30px !important;
}

.channel_list li.selected.hover .chl_name{margin-left:0px !important;}
    

#fm-channel-list .chl_section{width:auto !important;}

.fav_fast{
margin-right:-20px !important;
height:44px !important;
line-height:44px !important;
border-radius:22px !important;
width:44px !important;
border-top-right-radius: 3px !important;
border-bottom-right-radius: 3px !important;
}
.star{
background-position: -26px 4px !important;
height:44px !important;
width:44px !important;
border-radius:22px !important;
border-top-right-radius: 3px !important;
border-bottom-right-radius: 3px !important;
}    

#fm-player .player-wrap{
margin-top:-20px !important;
border-radius:7px !important;
box-shadow:none !important;
}
#radioplayer{border-radius:7px !important;}

#rec_played,#rec_liked,#faved{color:#9DD6C5 !important;}
}
