@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("autokrata.pl") {
body, div.top, div.nav, div.main, div.slideshow__text, div.slideshow > div.wrapper {background:none !important; background-color:black !important; color: white !important;}
.page, .entry--side-promo, .entry--promo-box, .scrollable-wrap {background:black !important;}
.comment__actions {background-image: none !important; background: black !important;}
.title, .slideshow__title, .author {color: grey !important;}
.top__logo {background-color: grey !important;}
.hotspot:after {background: none !important;}
.comment {background-color: black !important; border-top: 1px solid #FF0000 !important;}
.nav a {color: grey !important; background-color: black !important;} 
iframe {border: 1px solid yellow !important;}
div.article, div.comments__list--popular, div.slideshow, [id^=entry] {background-color: black !important;}
div.article > div.text > div:last-child, div.like, [class^=advertisement], .fb-box , .fb_iframe_widget, .motosos, div.entries > div:not([id^=entry]) {display: none !important;}
textarea#x-comment-text, input.input {background-color: grey !important; border: 1px solid black !important;}
div.spec {background: black !important; color: grey !important; border: 1px solid grey !important; }

 
