css:
@-moz-document domain("ixquick.com"), domain("startpage.com")
{

*
{
  font-family: /*[[zfont]]*/ !important;
  color: #333 !important;
}

html, body
{
  background: #FFF !important;
  margin: 0 !important;
  padding: 0 !important;
}

a
{
  color: #AAA !important;
  text-decoration: none !important;
}

a:hover
{
  color: #888 !important;
  text-decoration: underline !important;
}


/** HIDE
 **/

#news_message, /*now omitted because the HTML does match on both sites*/
#enhanced, .fixstar,
div#head table:nth-of-type(1),
#content a#main_link.addsettings p#caption,
div#links a,
#people_table tbody tr td table tbody tr td input.invisible,
#results_content #tip #tod_wrap table tbody tr td a.tod,
#results_content #tip #tod_wrap table tbody tr td font,
#sponsored,
#side_bar.f-l .date_promo,
a.bookmark, 
#results_header #search_form #search a#detail,
#results_header #search_form #search #enhanced,
.tdtopcurve, .tdbottomcurve,
#results_content #ix_promo_box,
#footer #search_footer table tbody tr#bottom_input_p,
#content a#main_link.addsettings p#caption,
 table tbody tr td #links,
#light, #footer_links,
#main_link,
#results > div:nth-child(1), /*unknown padding 24px*/
#within /*im not convinced this does anything*/
{
  display: none !important;
}


/** MENU
 **/

#head *
{
  font-size: small !important;
  font-size: 11px !important;
}

#head
{
  background: #F2F2F2 !important;
  height: 38px !important;
  line-height: 38px !important;
  padding: 0 16px !important;
  margin: 0 !important;
  text-align: right !important;
}

#head table[width="100%"]
{
  line-height: 32px !important;
}

#category,
#settings
{
  line-height: 38px !important;
}

#category
{
  float: left !important;
}

#settings
{
  margin-top: 0 !important;
  float: right !important;
}

#head_left
{
  margin-left: -3px !important;
}

#head table[width="100%"] td[width="260"]
{
  display: none !important;
}

/**lang menu**/
#lang_div
{
  top: 38px !important;
  border: 2px solid #F2F2F2 !important;
  border-top: 0 !important;
  background-color: #F8F8F8 !important;
  padding: 4px !important;
  width: 150px;
  border-radius: 0 !important;
}


/** LOGO
 **/

#content
{
  /*background: url(https://i.imgur.com/gvEo5Y8.png) no-repeat center top !important;*/ /*light*/
  /*background: url(https://i.imgur.com/OLbEdZo.png) no-repeat center top !important;*/ /*dark*/
  margin-top: 71px !important;
  padding-top: 111px !important;
  width: 800px !important;
  white-space: nowrap !important;
}


/** SEARCH BOX
 **/

input#query,
input#query_top
{
  background: #F8F8F8 !important;
  color: #555 !important;
  padding: 6px 6px 5px !important;
  border: 1px solid #CCC !important;
  border-right: 0 !important;
  box-shadow: none !important;
}

input#query:focus,
input#query_top:focus
{
  border-color: #009A57 !important;
  border-right-color: #CCC !important;
}

input[src*="/stp_search.gif"]
{
  width: 0 !important;
  background: url(https://i.imgur.com/HHV1A4Y.png) #009A57 no-repeat center center !important;
  padding-left: 37px !important;
  height: 36px !important;
  box-shadow: none !important;
  border: 0 !important;
}

#submit1
{
  margin-left: 0 !important;
}

#no_search_error
{
  background: transparent !important;
}

/**auto complete**/

.ac_results
{
  margin-top: -23px !important;
  border: 1px solid #009A57 !important;
}

.ac_over
{
  background: #EEE !important;
}


/** RESULTS PAGE SEARCH BOX
 **/

#results_header
{
  margin-top: -47px !important;
  max-width: 70% !important;
  width: 70% !important;
}


#results_header #search_form #search
{
  padding-top: 0 !important;
  padding-left: 30px !important;
  width: auto !important;
}

#results_header input#query_top
{
  background: #FFF !important;
  font-size: medium !important;
  padding: 1px 4px !important;
}

#results_header #submit1
{
  padding-left: 28px !important;
  height: 27px !important;
}


/** SIDEBAR
 **/

#side_bar
{
  margin-left: 6px !important;
  width: 180px !important;
}

#by_time,
#results
{
  border: 0 !important;
}


/** RESULTS
 **/

#results_count_p
{
  color: #AAA !important;
  margin: 18px 0 0 215px !important;
  font-size: 11px !important;
  line-height: 1.6 !important;
  width: auto !important;
}

#tod_wrap
{
  border: 0 !important;
  margin: 0 0 0 189px !important;
}

#tod_wrap table td:nth-child(1)
{
  line-height: 28px;
}

.sugg:after
{
  content: "?";
}

#results
{
  margin: 0 0 0 187px !important;
}

#results_content
{
  min-width: 600px !important;
}

#results_content.hide_side_bar #results
{
  margin: 0 !important;
}

#show_hide
{
  position: fixed !important;
  top: 0 !important;
  bottom: 0 !important;
  left: 0 !important;
  width: 6px !important;
  background: #DDD !important;
  cursor: pointer;
}

#show_hide:hover
{
  background: #CCC !important;
}

#show_hide span
{
  display: none !important;
}

#logo img[src*="startpage_logo_res.gif"]
{
  width: 0 !important;
  background: url(https://i.imgur.com/0loMANy.png) no-repeat 0 1px !important;
  padding-left: 153px !important;
}

#logo img[src*="ixquick_res.gif"]
{
  width: 0 !important;
  background: url(https://i.imgur.com/baYsrr8.png) no-repeat 0 1px !important;
  padding-left: 111px !important;
}

#results_header #logo.f-l
{
  background: transparent !important;
  top: 55px !important;
  left: 28px !important;
  padding: 0 !important;
  margin: 0 !important;
  line-height: 10px !important;
  height: 10px !important;
}

#results_header #logo.f-l a img
{
  height: 10px !important;
}

#results_header #search_form #search
{
  margin-top: 15px !important;
  margin-left: 149px !important;
}

#results #first-result.result,
#results .result
{
  padding: 8px 10px !important;
  width: auto !important;
}

#results div:nth-child(3)#first-result
{
  padding: 4px 10px !important;
}
/*[[zmode]]*/

#results .result h3
{
  line-height: 1.4 !important;
}

#results .result .url,
#results .result .url b
{
  color: #333 !important;
  font-family: monospace !important;
  font-size: small !important;
  line-height: 1.8 !important;
}

#results .result a.proxy
{
  text-transform: lowercase;
  font-variant: small-caps;
  font-size: small !important;
}

#results .result p {
  color: #FFF !important;
  padding: 0 !important;
  margin: 0 !important;
  line-height: 1.4 !important;
}

#results .result p.desc
{
  color: #333 !important;
  width: 540px !important;
}


#results .result h3 a
{
  color: #0050C0 !important;
  text-decoration: underline !important;
}

#results .result h3 a > b
{
  color: #0050C0 !important;
}

#results .result h3 a:visited
{
  color: #A03080 !important;
}

#results .result h3 a:visited > b
{
  color: #A03080 !important;
}

#WzTtDiV, #WzBoDy
{
  padding: 6px 10px !important;
  width: auto !important;
}

#WzBoDy
{
  background: #F2F2F2 !important;
  color: #555 !important;
  border: 1px solid #CCC !important;
  border-radius: 0 4px 4px;
}

.classified
{
  padding: 18px 0 46px 10px !important;
}


/** IT'S OFFICIAL BITCH
 **/

#office_website
{
  display: inline !important;
}

.official_block,
.official_block span
{
  background: none !important;
  padding: 0 !important;
  margin: 0 !important;
}

.official_block span
{
  background: #888 !important;
  background-image: linear-gradient(to bottom, #AAA 0%, #555 100%) !important;
  color: #FFF !important;
  height: auto !important;
  line-height: inherit !important;
  padding: 1px 5px !important;
  margin-left: 8px !important;
  border-radius: 3px !important;
}


/** ICONS
 **/

span[id*="title_stars"]
{
  height: 11px !important;
}

span[id*="title_stars"] img
{
  vertical-align: top !important;
}

span[id*="title_stars"] img[src*="star.gif"]:nth-child(1)
{
  width: 0 !important;
  background: url(https://i.imgur.com/Z2tA2cW.png) no-repeat center center !important;
  background: url(https://i.imgur.com/9O2Dus1.png) no-repeat 0 0 !important;
  padding-left: 12px !important;
  height: 11px !important;
}

span[id*="title_stars"]:hover img[src*="star.gif"]
{
  background-position: -12px 0 !important;
}


span[id*="title_stars_"] img[src*="star.gif"]:nth-child(2),
span[id*="title_stars_"] img[src*="star.gif"]:nth-child(3),
span[id*="title_stars_"] img[src*="star.gif"]:nth-child(4)
{
  display: none !important;
}

img[src*="cluster_c"]
{
  vertical-align: top !important;
  width: 0 !important;
  padding-left: 12px !important;
  height: 11px !important;
}

img[src*="cluster_check.gif"]
{
  background: url(https://i.imgur.com/RH1fgLX.png) no-repeat center center !important;
}

img[src*="cluster_cross.gif"]
{
  background: url(https://i.imgur.com/HM9GsBT.png) no-repeat center center !important;
}


/** MISC IMAGES
 **/

img[src*="yellow_warning.gif"]
{
  width: 0 !important;
  background: url(https://i.imgur.com/JilH5vn.png) no-repeat center left !important;
  padding-left: 17px !important;
  height: 17px !important;
  vertical-align: text-bottom !important;
}


/** FOOTER
 **/

#footer,
#footer *
{
  font-size: 11px !important;
  color: #AAA !important;
}

#footer
{
  margin-left: 9px !important;
}

.air #footer
{
  padding: 12px !important;
  margin: auto !important;
  clear: left !important;
}

#footer #pagenavigation,
#footer #search_footer
{
  margin: 8px 0 20px !important;
}

#footer #pnform
{
  color: #333 !important;
  font-size: x-large !important;
  line-height: 1 !important;
}

#footer .numbers_st,
#footer #prevnavbar .numbers_st,
#footer #nextnavbar .numbers_st
{
  color: #888 !important;
  vertical-align: 30% !important;
  font-weight: normal !important;
  display: inline-block !important;
  padding: 8px !important;
  margin: 0 !important;
}

#footer .numbers_st:hover
{
  text-decoration: overline !important;
}

#footer #prevnavbar .numbers_st
{
  margin-left: 11px !important;
}

#footer #nextnavbar .numbers_st
{
  width: auto !important;
}

#footer #prevnavbar .numbers_st,
#footer #nextnavbar .numbers_st
{
  background: none !important;
  vertical-align: 20% !important;
}


/**end of all**/

}


@-moz-document domain("ixquick.com")
{

#content
{
  background: url(https://i.imgur.com/HscjLTF.png) no-repeat center top !important;
}

#results .result:nth-child(2)
{
  padding: 8px 10px 4px !important;
}

}

@-moz-document domain("startpage.com")
{

#content
{
  background: url(https://i.imgur.com/gvEo5Y8.png) no-repeat center top !important;
}

}
