}

.fbar {
background: rgba(255, 0, 0, 0.38) !important;
border-top: 1px solid #FF0000 !important;
font-size: small;
line-height: 40px;
min-width: 980px;
}

#appbar {
background: rgba(255, 255, 255, 0) !important;
-webkit-box-sizing: border-box;
width: 100%;
}

#hdtbSum {
background: rgba(255, 0, 0, 0.38) !important;
border-bottom: 1px solid #FF0000 !important;
height: 40px;
line-height: 35px;
padding: 0;
position: relative;
z-index: 102;
}

#resultStats, .ab_center_col {
color: #E2ACC4 !important;
font-size: 13px;
line-height: 35px;
overflow: hidden;
text-overflow: ellipsis;
white-space: nowrap;
}

#hdtb {
background: rgba(255, 255, 255, 0) !important;
color: #E2ACC4 !important;
outline-width: 0;
outline: none;
position: relative;
z-index: 102;
}

#hdtb_msb #hdtb_more, #hdtb_msb #hdtb_tls {
color: #A30000 !important;
}

.gb_ib {
background: rgba(255, 0, 0, 0.38);
}

.gb_la .gb_g {
background-position: -444px -69px;
height: 0px !important;
width: 92px;
}

.gbqfqw {
background: rgba(255, 255, 255, 0) !important;
background-clip: padding-box;
border: 1px solid #FF0000 !important;
border-color: rgba(255, 0, 0, 1) !important;
border-right-width: 0;
height: 30px;
-webkit-box-sizing: border-box;
box-sizing: border-box;
}

.gbqfif, .gbqfsf {
color: #F00 !important;
font: 16px arial,sans-serif;
}

#gbqfb {
border: none;
border-bottom-left-radius: 0;
border-top-left-radius: 0;
height: 30px;
outline: none;
padding: 0 0;
width: 60px;
-webkit-box-shadow: none;
box-shadow: none;
background: #4285f4;
background: -webkit-linear-gradient(top,#FF0000,#7A0000) !important;
}

.ksb.ksbs, .ksb.ksbs:hover, .ab_button.selected, .ab_button.selected:hover {
background-color: #eee;
background-image: -webkit-gradient(linear,left top,left bottom,from(#eee),to(#e0e0e0));
background-image: -webkit-linear-gradient(top,#FF0000,#7A0000) !important;
background-image: linear-gradient(top,#eee,#e0e0e0);
border: 1px solid #F00;
-webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
color: #222;
margin: 0;
}

.ksb, .ab_button {
background-color: #f5f5f5;
background-image: -webkit-gradient(linear,left top,left bottom,from(#f5f5f5),to(#f1f1f1));
background-image: -webkit-linear-gradient(top,#FF0000,#7A0000) !important;
background-image: linear-gradient(top,#f5f5f5,#f1f1f1);
border: 1px solid #dcdcdc !important;
border: 1px solid rgba(255, 0, 0, 1)!important;
color: #444;
}

.action-menu .mn-dwn-arw {
border-color: #660000 transparent !important;
left: 0;
margin-left: 3px;
}

#cnt {
background-color: rgba(241, 241, 241, 0) !important;
}

.lhshdr, .tphdr, #tbbcc, .rshdr {
