@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

/*Follow me at glacier-coast.tumblr.com */
body{background: url('http://i44.tinypic.com/2s9upeg.png') !important;

background-size: 100% !important;
background-attachment: fixed !important;
background-color:#000000 !important;
background-position: 0px -100px !important;
margin:auto !important;}

table.mceLayout {


	    border:0; border-left:1px solid #000000;
	    border:1px solid #000000;
	   	 background: #FFFFFF !important;
	    border-radius: .4em;
	}
	table.mceLayout tr.mceFirst td {
	    padding: 3px 2px;


	}
	.link_text_container, .link_button{background: #4F5671 !important;
	border-color:#5A6796 !important}

	.defaultSkin table.mceLayout tr.mceLast td style {}
	 td.mceToolbar{
	    background: rgba(90,103,150,.75) !important;
	    border-radius: .4em .4em 0 0;
	    border: #000000 !important;

	}
	 .mceStatusbar {
    background:#000000 !important;
	    font-family:'MS Sans Serif',sans-serif,Verdana,

Arial;
    font-size:9pt; line-height:16px;
    overflow:visible;
        border: #000000 !important;

	    height:20px;
		
    border-radius: 0 0 .4em .4em;
	    padding-left: 5px;
	        border: #000000 !important;
	}
	.mceIcon {background:none no-repeat 20px 20px}
	.notification {

background: rgba(0,0,0,.85) !important;

border: rgba(0,0,0,.8) !important;

background-image: none !important;

margin-bottom: 1px !important }

.notification_type_icon { display: none !important }

.notification a { border-bottom: 2px solid rgba(255,255,255,0.3) !important;

font-size: 12px !important; }

.notification a:hover { border-bottom: 2px solid rgba(0,0,0,0.3) !important; }


html .mceContentBody {

max-width: 584px;
background: #FFFFFF !important;
}
ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;

border-color: rgba(90,103,150,.5) !important;
}

ol#posts li blockquote blockquote,

body.mceContentBody blockquote{
border-color: rgba(90,103,150,.5) !important;
}
.l-content {background-color:rgba(12,19,44,.5) !important;}

h1,h2,h3,h4,h5,h6
        {color:#000000 !important;}

a, a:visited
        {color: #6976A1 !important;}

a:hover
        {color:#0C132C !important;}
.selection_nipple { border-bottom: solid 12px #6976A1 !important;}


 
	
	.controls_section li { color: rgba(255,255,255,.3) !important; }

.with_subtitle small { color:  rgba(255,255,255,.3) !important; }
.search_form_row{background: rgba(12,19,44,.5) !important;}
#right_column{color:#fff !important;}

.nipple { display: none !important }



.permalink {width: 15px !important;
        height: 15px !important;
        background: #5A6796 !important;
        border-bottom-left-radius: 10px
!important;
        border-top-right-radius: 10px !important;

        margin-top: 3px !important;
        margin-right: 4px !important;}
#return_to_top .return_to_top_icon{
background:
url('http://i46.tinypic.com/aebnlv.png') no-repeat

transparent!important;

width:56px !important;

height:59px !important;

opacity:.3 !important;}

#return_to_top .return_to_top_icon:hover{opacity:.35 !important;}


a.logo_anchor {
    

opacity:.5!important;}


a.logo_anchor:hover {
opacity: .2 !important;
}
.search_form_row {background-color:rgba(12,19,44,.5) !important;}
#left_column{background-color:rgba(0,0,0,0)}
#right_column{background-color:rgba(12,19,44,.5);
margin-top:0px !important;}
.l-header{background-color:rgba(12,19,44,.9) !important;}
