/* Author: Ahad Khalilvand
 * Edited By Hooshmandk
 * ======================== */

/* TABLE OF CONTENT
 * ==================
 *  1. Fonts
 *  2. Login Page
 *  3. Floating Panel and Title Bar
 *  4. Left Sidebar
 *  5. Right Sidebar
 *  6. Title Only View
 *  7. Magazine View
 *  8. Cards View
 *  9. Full Article View
 * 10. Others
 * 11. Fork
 **/
 
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.feedly.com/home"), url-prefix("https://www.feedly.com/home"),
               url-prefix("http://feedly.com/index.html"), url-prefix("https://feedly.com/index.html"),
               domain("cloud.feedly.com"), domain("feedly.com") {

/* ==================== Fonts ==================== */
@font-face {
  font-family: 'Alegreya Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Alegreya Sans'), local('AlegreyaSans-Regular'), url(https://themes.googleusercontent.com/static/fonts/alegreyasans/v1/KYNzioYhDai7mTMnx_gDgtUFqmyT-Ih543PWa_C0ig4.woff) format('woff');
}

@font-face {
  font-family: 'B Yekan';
  font-style: normal;
  font-weight: 700;
  src: local('B Yekan'),
     url('http://awebfont.ir/services/fonts/683a5dddcff7f1cbb08d964cc275cb50ce7840291901931.woff') format('woff');
  font-weight: normal;
  font-style: normal;
}


@font-face {
  font-family: 'B Mitra';
  font-style: normal;
  font-weight: 700;
  src: local('B Mitra'),
     url('http://awebfont.ir/services/fonts/759bba0f218e4740e1ce2e37ebc80ef3c29300291901931.woff') format('woff');
  font-weight: normal;
  font-style: normal;
}

* {
font-family: 'B Mitra', 'Alegreya Sans','B Yekan',"tahoma"!important;
text-decoration: none!important;
}

/* ==================== Login Page ==================== */
.narrow.notlogged #feedlyFrame, .narrow.notlogged #feedlyPart0,
.narrow.notlogged #feedlyTabsHolder, .narrow.notlogged #feedlyTabs, .welcomeNav, #welcomeBanner,
.narrow.notlogged, .narrow.notlogged div.start, .narrow.notlogged div.hero, .narrow.notlogged  section.qa {
background-color: #e1e2e3!important;
}
     
.narrow.notlogged #feedlyFrame {
min-width: 563px!important;
}

.narrow.notlogged #mainBar {
margin-left: 260px!important;
min-width: 310px!important;
}

.narrow.notlogged #feedlyTabsHolder {
width: 260px!important;
}

.narrow.notlogged #feedlyTabs {
overflow: hidden!important;
opacity: 1!important;
}

.narrow.notlogged .panels {
visibility: visible!important;
}

#feedlyTabs .secondaryPanelButton, #feedlyTabs .panelButton, .welcomeNav a {
background-color: transparent!important;
color: #ffffff!important;
text-shadow: 0 0 3px #000000,0 0 3px #000000,0 0 3px #000000!important;
font-size: 24px!important;
font-weight: bold!important;
border: none!important;
box-shadow: none!important;
}

.welcomeNav, #welcomeBanner, .welcomeFooter {
width: 100%!important;
margin-left: auto!important;
margin-right: auto!important;
padding-left: 0!important;
padding-right: 0!important;
}

#welcomeBanner {
min-height: 400px!important;
}

#welcomeBanner div[style="text-align:center; margin-left:auto; margin-right:auto; padding-top:20px"] {
background-color: #005cb8!important;
color: #ffffff!important;
border: 1px solid #444444!important;
box-shadow: 0 0 3px 1px #333333!important;
width: 200px!important;
height: 125px!important;
}

#welcomeBanner .header {
background: #005cb8!important;
text-shadow: 0 0 8px #000000,0 0 8px #000000,0 0 8px #000000!important;
font-size: 24px!important;
margin: -15px 0 0 0!important;
padding: 0!important;
width: 200px!important;
}

#welcomeBanner .header:hover {
background: #005cb8!important;
color: #ffffff!important;
margin: -15px 0 0 0!important;
padding: 0!important;
width: 200px!important;
}

#welcomeBanner .description {
display: none!important;
}

#welcomeBanner div[style="margin-top: 44px; clear:both"] {
margin-top: 64px!important;
width: 200px!important;
}

#welcomeBanner #feedlyGetStartedButton {
background-color: transparent!important;
color: #ffffff!important;
text-shadow: 0 0 6px #000000,0 0 6px #000000,0 0 6px #000000!important;
font-size: 18px!important;
font-weight: bold!important;
border: none!important;
box-shadow: none!important;
margin-top: -25px!important;
padding: 5px 0!important;
width: 160px!important;
height: 20px!important;
}

div[style="margin-top:17px; color:#999; font-size:13px"] {
color: #ffffff!important;
font-size: 12px!important;
font-weight: bold!important;
margin-top: 10px!important;
}

div[style="margin-top:17px; color:#999; font-size:13px"] a {
color: #ffffff!important;
text-shadow: 0 0 6px #000000,0 0 6px #000000,0 0 6px #000000!important;
font-size: 14px!important;
font-weight: bold!important;
}

/* ==================== Floating Panel and Title Bar ==================== */
#proBanner, #feedlyMessageBar, #feedlyProBar {
display: none!important;
}

#floatingBar {
background: #ffffff!important;
border-bottom: 3px solid #213C48!important;
box-shadow: 0 0 4px 1px #444444!important;
}

#floatingBar[style*="transform: translateY(-45px);"], #floatingBar[style*="transform: translateY(-45px);"] #floatingTitleBar {
background: transparent!important;
border: none!important;
box-shadow: none!important;
}

#floatingTitleBar {
background: #ffffff!important;
color: #fc2103!important;
font-size: 18px!important;
border-bottom: 1px solid #ffffff!important;
}

#floatingBar span {
color: #404040!important;
font-size: 13px!important;
font-weight: bold!important;
}

#floatingBar span.action:hover  {
color: #910e07!important;
}

.wide.streched #feedlyTitleBar, .narrow.streched #feedlyTitleBar {
color: #032aff!important;
font-size: 18px!important;
}

.wide #feedlyTitleBar, .narrow #feedlyTitleBar {
color: #000000!important;
font-size: 18px!important;
}

#feedlyTitleBar span {
color: #202020!important;
font-size: 13px!important;
font-weight: bold!important;
}

#feedlyTitleBar span.action:hover {
color: #910e07!important;
}

.wide.streched .hAction.secondary, .wide.streched .hAction.tertiary,
.narrow.streched .hAction.secondary, .narrow.streched .hAction.tertiary {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #333333!important;
font-weight: bold!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 2px 4px!important;
top: 0!important;
opacity: 0.6!important;
transition: opacity 0.5s linear 0s!important;
}

.wide .hAction.secondary, .wide .hAction.tertiary,
.narrow .hAction.secondary, .narrow .hAction.tertiary {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #777777!important;
font-weight: bold!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 2px 4px!important;
top: 0!important;
opacity: 0.6!important;
transition: opacity 0.5s linear 0s!important;
}

.wide.streched .hAction.promptLogin, .narrow.streched .hAction.promptLogin, .narrow.notlogged .hAction.promptLogin {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #333333!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 6px 16px!important;
top: 0!important;
opacity: 1!important;
}

.wide .hAction.promptLogin, .narrow .hAction.promptLogin {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #777777!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 6px 16px!important;
top: 0!important;
opacity: 1!important;
}

.wide.streched .hAction.secondary:hover, .wide.streched .hAction.tertiary:hover,
.wide .hAction.secondary:hover, .wide .hAction.tertiary:hover,
.narrow.streched .hAction.secondary:hover, .narrow.streched .hAction.tertiary:hover,
.narrow .hAction.secondary:hover, .narrow .hAction.tertiary:hover {
opacity: 1!important;
}

.pageAction {
opacity: 1!important;
}

.wide.streched .pageActionBar img, .narrow.streched .pageActionBar img, .narrow.notlogged .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -moz-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -ms-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: -o-linear-gradient(bottom,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
background-image: linear-gradient(to top,#e1e1e1,#eeeeee,#f0f0f0,#fdfdfd)!important;
color: #333333!important;
border: 1px solid #aaaaaa!important;
border-radius: 3px!important;
padding: 0 8px!important;
}

.wide .pageActionBar img, .narrow .pageActionBar img {
background-image: -webkit-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -moz-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -ms-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: -o-linear-gradient(bottom,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
background-image: linear-gradient(to top,#d1d1d1,#dddddd,#e0e0e0,#e8e8e8)!important;
color: #777777!important;
border: 1px solid #555555!important;
border-radius: 3px!important;
padding: 0 8px!important;
}

#floatingBar[style*="transform: translateY(0px);"] .pageActionBar img {
border: 1px solid #aaaaaa!important;
margin-top: 5px!important;
}

#floatingBar[style*="transform: translateY(0px);"] .hAction.secondary,
#floatingBar[style*="transform: translateY(0px);"] .hAction.tertiary,
#floatingBar[style*="transform: translateY(0px);"] .hAction.promptLogin,
#floatingBar[style*="transform: translateY(0px);"] .pageActionBar img {
border: 1px solid #aaaaaa!important;
}

.popup.customizer .item.selectedItem {
color: #000000!important;
font-weight: bold!important;
border: none!important;
}

.wide.streched #searchBox, .narrow.streched #searchBox {
background: #fafafa!important;
border-radius: 3px!important;
border-width: 0!important;
box-shadow: 1px 1px 2px 0 #cccccc inset!important;
padding-left: 7px!important;
padding-right: 7px!important;
height: 25px!important;
}

.wide #searchBox, .narrow #searchBox {
background: #eff0f2!important;
border-radius: 3px!important;
border-width: 0!important;
box-shadow: 1px 1px 2px 0 #bbbbbb inset!important;
padding-left: 7px!important;
padding-right: 7px!important;
height: 25px!important;
}

h2 {
color: #000000!important;
font-weight: bold!important;
text-transform: capitalize!important;
padding-top: 10px!important;
}

.wide.streched h2 *.action, .narrow.streched h2 *.action {
color: #032aff!important;
font-weight: bold!important;
font-size: 13px!important;
}

.wide h2 *.action, .narrow h2 *.action {
color: #00008f!important;
font-weight: bold!important;
font-size: 13px!important;
}

.wide.streched h2 *.action:hover, .narrow.streched h2 *.action:hover {
color: #910e07!important;
}

.wide h2 *.action:hover, .narrow h2 *.action:hover {
color: #610e07!important;
}

.feedUnreadCountHint {
color: #333333!important;
font-weight: bold!important;
}

.wide.streched .infoBox, .narrow.streched .infoBox {
color: #202020!important;
}

.wide .infoBox, .narrow .infoBox {
color: #000000!important;
}

.wide.streched .infoBox a, .narrow.streched .infoBox a {
color: #0000ff!important;
font-weight: bold!important;
}

.wide .infoBox a, .narrow .infoBox a {
color: #0000bf!important;
font-weight: bold!important;
}

.wide.streched .infoBox a:hover, .narrow.streched .infoBox a:hover,
.wide .infoBox a:hover, .narrow .infoBox a:hover {
color: #910e07!important;
}

/* ==================== Left Sidebar ==================== */
#navSelectorHolder {
margin-top: 0!important;
}

.wide #navSelectorHolder #navSelector_my,
.narrow #navSelectorHolder #navSelector_my {
padding-left: 52px!important;
margin-left: 0!important;
}

.wide #navSelectorHolder #navSelector_store,
.narrow #navSelectorHolder #navSelector_store {
border-right-width: 0!important;
padding-right: 21px!important;
margin-right: 0!important;
}

.wide #navSelectorHolder #navSelector_my.navSelector.selected {
border-bottom-color: rgba(255,255,255,0.3)!important;
}

.wide #navSelectorHolder #navSelector_store.navSelector {
border-bottom-color: rgba(255,255,255,0.1)!important;
}

.wide #navSelectorHolder #navSelector_my.navSelector,
.narrow #navSelectorHolder #navSelector_my.navSelector,
.narrow #navSelectorHolder #navSelector_store.navSelector {
border-bottom-color: rgba(0,0,0,0.1)!important;
}

.wide #navSelectorHolder #navSelector_store.navSelector.selected,
.narrow #navSelectorHolder #navSelector_store.navSelector.selected,
.narrow #navSelectorHolder #navSelector_my.navSelector.selected {
border-bottom-color: rgba(0,0,0,0.3)!important;
}

.wide #navSelectorHolder #navSelector_my.navSelector.selected, .wide #navSelectorHolder #navSelector_store.navSelector {
background: transparent!important;
color: #ffffff!important;
}

.wide #navSelectorHolder #navSelector_my.navSelector, .wide #navSelectorHolder #navSelector_store.navSelector.selected {
background: #dddddd!important;
color: #000000!important;
}

.narrow #navSelectorHolder #navSelector_my.navSelector.selected, .narrow #navSelectorHolder #navSelector_store.navSelector {
background: transparent!important;
color: #000000!important;
}

.narrow #navSelectorHolder #navSelector_my.navSelector, .narrow #navSelectorHolder #navSelector_store.navSelector.selected {
background: #dddddd!important;
color: #000000!important;
}

.narrow.notlogged #navSelectorHolder #navSelector_my.navSelector.selected,
.narrow.notlogged #navSelectorHolder #navSelector_store.navSelector {
background: #1a2421!important;
color: #ffffff!important;
}

.narrow.notlogged #navSelectorHolder #navSelector_my.navSelector,
.narrow.notlogged #navSelectorHolder #navSelector_store.navSelector.selected {
background: #dddddd!important;
color: #000000!important;
}

#feedlyTabs {
overflow-x: hidden!important;
}

#feedlyTabs #mytab {
margin-top: -30px!important;
}

#feedlyTabsHolder .icon, #feedlyTabsHolder:hover .header.selected .icon {
opacity: 0.4!important;
}

#feedlyTabsHolder:hover .icon {
opacity: 1!important;
}

.dark #feedlyTabs, .light #feedlyTabs {
color: #ffffff!important;
}

.narrow #feedlyTabs {
color: #000000!important;
}

.dark #feedlyTabs .header div, .dark #feedlyTabs .feedIndex div,
.light #feedlyTabs .header div, .light #feedlyTabs .feedIndex div {
color: #ffffff!important;
font-weight: bold!important;
}

.narrow #feedlyTabs .header div, .narrow #feedlyTabs .feedIndex div {
color: #000000!important;
font-weight: bold!important;
}

.dark #feedlyTabs .label.empty, .dark #feedlyTabs .feedIndexTitleHolder.empty,
.light #feedlyTabs .label.empty, .light #feedlyTabs .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

.narrow #feedlyTabs .label.empty, .narrow #feedlyTabs .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

.dark #feedlyTabs .header.selected .label.empty, .dark #feedlyTabs .feedIndex.selected .feedIndexTitleHolder.empty,
.light #feedlyTabs .header.selected .label.empty, .light #feedlyTabs .feedIndex.selected .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

.narrow #feedlyTabs .header.selected .label.empty, .narrow #feedlyTabs .feedIndex.selected .feedIndexTitleHolder.empty {
font-weight: normal!important;
}

#feedlyTabs .categoryUnreadCount.simpleUnreadCount, #feedlyTabs .feedUnreadCount.simpleUnreadCount {
font-size: 11px!important;
}

.dark .header:hover,  .dark .feedIndex:hover,
.light .header:hover, .light .feedIndex:hover {
background: rgba(255,255,255,0.3)!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 35px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

.narrow .header:hover, .narrow .feedIndex:hover {
background: rgba(1,24,32,0.1)!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 35px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

.dark .header.selected, .dark .feedIndex.selected,
.light .header.selected, .light .feedIndex.selected {
background: #ffffff!important;
border-left: 9px solid #570000!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

.narrow .header.selected, .narrow .feedIndex.selected {
background: #011820!important;
border-left: 9px solid #ff4500!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
-webkit-transition: all 0.2s!important;
-moz-transition: all 0.2s!important;
-ms-transition: all 0.2s!important;
-o-transition: all 0.2s!important;
transition: all 0.2s!important;
}

.dark #feedlyTabs .header.selected div, .dark #feedlyTabs .feedIndex.selected div,
.light #feedlyTabs .header.selected div, .light #feedlyTabs .feedIndex.selected div {
color: #570000!important;
font-weight: bold!important;
}

.narrow #feedlyTabs .header.selected div, .narrow #feedlyTabs .feedIndex.selected div {
color: #ffffff!important;
font-weight: bold!important;
}

.dark .header.selected:hover, .dark .feedIndex.selected:hover,
.light .header.selected:hover, .light .feedIndex.selected:hover {
background: #ffffff!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
}

.narrow .header.selected:hover, .narrow .feedIndex.selected:hover {
background: #011820!important;
margin-left: -9px!important;
margin-right: -40px!important;
padding-right: 40px!important;
}

.dark #mytab_icon, .light #mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my-active.png")!important;
}

.narrow #mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my.png")!important;
}

.dark #savedtab_icon, .light #savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved-active.png")!important;
}

.narrow #savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved.png")!important;
}

.dark #latesttab_icon, .light #latesttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-latest-active.png")!important;
}

.narrow #latesttab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-latest.png")!important;
}

.dark .icon.handle, .light .icon.handle {
background-image: url("http://s3.feedly.com/production/head/images/selector-right-arrow-active.png")!important;
}

.narrow .icon.handle {
background-image: url("http://s3.feedly.com/production/head/images/selector-right-arrow.png")!important;
}

.dark #feedlyTabs .header.selected #mytab_icon, .light #feedlyTabs .header.selected #mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my.png")!important;
}

.narrow #feedlyTabs .header.selected #mytab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-my-active.png")!important;
}

.dark #feedlyTabs .header.selected #savedtab_icon, .light #feedlyTabs .header.selected #savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved.png")!important;
}

.narrow #feedlyTabs .header.selected #savedtab_icon {
background-image: url("http://s3.feedly.com/production/head/images/selector-saved-active.png")!important;
}

.dark #feedlyTabs .header.selected #latesttab_icon, .light #feedlyTabs .header.selected #latesttab_icon {
