css:
@-moz-document url-prefix("http://instasynch.com/rooms/") {
#media{
  width:960px;
  height:540px;
  float:none;
}
#media iframe, #vimeo{
  width:100% !important;
  height:100% !important;
  float:none;
}
#stage #media{
  float:none;
  width:960px;
  height:540px;
  margin-left:25px;
}
#bn{
  display:none;
}
#chat{
  position:absolute;
  top:120px;
  right:0px;
  width:30%;
  float:left;
  height:540px;
    border:0;
  border-width:0;
}
#chat #chat_list{
width:75%;
  height:500px;
  border:0;
  border-width:0;
    padding:0;
}
#chat #chat_users{
width:25%;
  height:500px;
    border:0;
  border-width:0;
  padding:0;
  background:#f7f7f7;
}
#gear, #chat #chat_controls .controls{
display:none;
}
#chat #cin{
width:75%;
  border:0;
  padding:0;
height:30px;
  font-size:15px;
}
#playlist{
border-right:none;
 width:100%;
}
#playlist #playlistcontrols{
  width:96%;
  margin:auto;
}
#playlist_items{
 width:100%;
}
#stage .stage{
 width:100%;
}
.basic-button{
  background:#359661;
    border-width:0px;
  }
.stage{
 width:100%;
}
#st-descr{
height:35px;
}
#truetop{
height:50px;
  background:#359661;
  border:0;
}
#loginfrm{
padding-top:2px;
}
#media-title .title{
font-size:11px;
line-height:10px;
}
.poll-container{
 float:left;
 margin-left:50px;
}
}
