css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.fanfics.ru/read.php?")
{
    html body div#Comments.comments
    {
       width: 700px!important;
       margin: 0 auto!important;
    }

    html body div#read_footer
    ,div[id*="banner_read"]
    ,html body table.horizontal-bar ~ br
    ,div#CommentForm
    ,.totop
    {
        display: none!important;
    }

    div.ReadContent
    {
        margin: 0px 0px 0px 100px !important;
        padding: 0px!important;
    }

    div.ReadContent div.chapter
    {
        padding-left: 10px!important;
        padding-right: 10px!important;
        padding-top: 0px!important;
        padding-bottom: 0px!important;
    	margin: 0px!important;
    }

    div.ReadContent div.chapter1
    {
    	padding:0px!important;
    	margin: 0px!important;
        float: none!important;
    }

    div.ReadContent h2
    {
        margin: 30px 0px 20px 0px!important;
    }
    
    div.ReadContent > div#text-width-container div.twcin
    {
        width: auto !important;
        max-width: 135px !important;
    }
    div.ReadContent > div#text-width-container div.twcin input.modern_button
    {
        padding: 1px 1px !important;
    }

    div.ReadContent > div#text-width-container div.twc_1
    ,div.ReadContent > div#text-width-container div.twc_2
    {
        width: auto !important;
        max-width: 260px !important;
        margin-right: 0px !important;
    }

    div.ReadContent > div#text-width-container
    {
    	padding:0px!important;
    	margin: 0px!important;
    	font-size: 8px!important;
    }

    div.ReadContent + div.ReadContent div[id^="FicFav_"]
    {
       margin: 0px 0px 30px 0px!important;
    }
    
    p.fict
    {
        margin: 5px 0 8px !important;
    }
}
