a:hover {
	color: rgba(0, 0, 0, 0.5) !important;
	text-decoration: none;}
	
.postauthor {
	color: #000 !important;
	font-weight : bold;
	font-size : 120%;}
	
h4 {color: #FF8800 !important;} 
   
input { color: rgba(0, 0, 0, 0.8) !important; }    

textarea {
    background-color: rgb(255, 255, 255) !important;
    color: rgb(0, 0, 0) !important;}
  
/* code & quote */
table[width="90%"] {width:96% !important;}
div.quotetitle, div.codetitle {	
	font:normal 7pt 'Lucida Sans Unicode' !important;
	text-shadow: none !important; 
	color: rgba(0, 0, 0, 0.8); 
	background-image: none !important; 
	background-color: rgba(222, 222, 222, 0.4) !important;}

/* цитата код */
div.quotecontent, div.codecontent {
    font-size: 82% !important;
	font: 10pt serif; 
	text-shadow: none !important;
	color: rgba(0, 0, 0, 0.6) !important; 
	background-image: none !important; 
	background-color: rgba(222, 222, 222, 0.8) !important;}

div.codecontent {font:9pt 'Andale Mono' !important;}

body, p, td, tr, div, dd, dt, table, th, span, tbody { color: rgba(0, 0, 0, 0.8) !important; }
a, a:link, a:active, a:hover, h1, h2, h3, h4, h5, h6, h7, small, i {font-weight: bold !important; }
b {color: #59B5CE; }
.box {font-weight: bold !important; }

img[src*="icon_user_online.png"] {
  	width: 0px !important;
	height:0px !important;
	background-repeat: no-repeat;
	background-image: url(data:image/png;base64,) !important;
	padding-left:9px !important;
	padding-top:9px !important;}

img[src*="icon_user_offline.png"] {
  	width: 0px !important;
	height:0px !important;
	background-repeat: no-repeat;
	background-image: url(data:image/png;base64,) !important;
	padding-left:9px !important;
	padding-top:9px !important;}

