css:
@-moz-document domain("forum.vitasite.info") {
/*[[mybg]]*/
body {
  color:#fff !important;
}
.navPopup {
  color:#000 !important;
}
.section, .messageContent, .signature, .message, .messageMeta, .primaryContent, .node .nodeLastPost, .tabs li.active a, .node .nodeDescription, #content .pageContent, #QuickSearch, #QuickSearch .formPopup {
  background:#0D0D0D !important;
}
.message .signature {
 border-top: 1px dashed #333 !important;
}
.messageUserBlock, .message .likesSummary, .bbCodeQuote .quoteContainer,.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .secondaryContent, #taigachat_box li, #taigachat_box, .tabs.noLinks li.active, .discussionListItem, .discussionListItem .posterAvatar, .discussionListItem .stats, .panel.Notice, #DiscussionListOptionsHandle a, .profilePage .primaryUserBlock .mainText, .profilePage .tabs.mainTabs {
  background:#121212 !important;
}
.messageUserBlock div.avatarHolder {
 background:#111 !important;
          }
.section a:link, .section a:visited, .messageUserInfo a:link, .messageUserInfo a:visited, .likesSummary a:link, .likesSummary a:visited, .sidebar a:link, .sidebar a:visited, .mainContainer a:link, .mainContainer a:visited, .message .likesSummary, .node .nodeLastPost {
 color:#E4E4E4 !important;
}
.bbCodeQuote .attribution {
 color:#fff !important;
 border-bottom: 1px #111 !important;
 background:#222 !important;
}
.ugc a:hover, .ugc a:focus {
 background:#222 !important;
 padding:0 3px !important;
 border-radius:3px !important;
 margin:0;
 -moz-box-shadow: 1px 1px 1px 1px #111 !important;
 -webkit-box-shadow: 1px 1px 1px 1px #111 !important;
 box-shadow: 1px 1px 1px 1px #111 !important;
}
.bbCodeBlock,.message .likesSummary, .messageUserBlock {
 border: 1px solid rgba(255,255,255,0.05) !important;
}
.sidebar .section .secondaryContent .footnote {
 color:#999 !important;
        }
.PageNav a:hover, .PageNav a:focus, .PageNav {
 color: #fff !important;
}
.messageUserBlock .arrow {
 display:none !important;
}
/*.itemPageNav a:link, .itemPageNav:visited {
 color:#000 !important;
}*/
.messageList .message {
 border-top: 1px solid rgba(255, 255, 255, 0.1) !important;
}
.Menu, .Menu .primaryContent a, #QuickSearch .secondaryControls {
 color: rgb(4, 73, 126) !important;
}
.bbCodeQuote .quoteContainer .quoteExpand {
background: -moz-linear-gradient(top,  rgba(0,0,0,0) 0%, rgba(0,0,0,0) 34%, rgba(0,0,0,0.9) 84%, rgba(0,0,0,0.9) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0)), color-stop(34%,rgba(0,0,0,0)), color-stop(84%,rgba(0,0,0,0.9)), color-stop(100%,rgba(0,0,0,0.9)));
background: -webkit-linear-gradient(top,  rgba(0,0,0,0) 0%,rgba(0,0,0,0) 34%,rgba(0,0,0,0.9) 84%,rgba(0,0,0,0.9) 100%);
background: -o-linear-gradient(top,  rgba(0,0,0,0) 0%,rgba(0,0,0,0) 34%,rgba(0,0,0,0.9) 84%,rgba(0,0,0,0.9) 100%);
background: -ms-linear-gradient(top,  rgba(0,0,0,0) 0%,rgba(0,0,0,0) 34%,rgba(0,0,0,0.9) 84%,rgba(0,0,0,0.9) 100%);
background: linear-gradient(to bottom,  rgba(0,0,0,0) 0%,rgba(0,0,0,0) 34%,rgba(0,0,0,0.9) 84%,rgba(0,0,0,0.9) 100%);
color:#fff;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon, .node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
  background: url(http://i.imgur.com/ySLSnR5.png) no-repeat top left !important;
  background-size: contain !important;
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
  -webkit-filter: invert(1) !important; -moz-filter: invert(1) !important; -o-filter: invert(1) !important; -ms-filter: invert(1) !important; filter: invert(1) !important;
                }
.sectionMain, .sidebar, .section.infoBlock {
border:none !important;
background: transparent !important;
    }
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .discussionListItem, .discussionListItem .stats dl, .profilePage .primaryUserBlock .lastActivity {
border-color:#0D0D0D !important;
      }
.node .nodeLastPost, #taigachat_box, #Block1stPanes, #Block2ndPanes, .tabs li a, .tabs.noLinks li, .tabs, #DiscussionListOptionsHandle a, .profilePage .mainProfileColumn, .mast {
border-color:#222 !important;
}
.tabs li a {
background: #222 !important;
}
.messageContent a:link, .messageContent a:visited, .messageText a:link, .messageText a:visited {
color:#888 !important;
}

.navTabs .navTab.PopupClosed .navLink {
  color:#333;
}
.Popup .PopupControl:hover, .Popup.PopupContainerControl:hover {
  background:rgb(160,160,160) !important;
  color:#333 !important;
}
.navTabs {
  background: rgb(190,190,190);
  }
.navTabs .navTab.selected .navLink, .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus, .sectionFooter, .breadcrumb .crust a.crumb, .PageNav a, .discussionList .sectionHeaders dd a[href]:hover, .itemPageNav a {
background:#7d7e7d;
}
.breadcrumb .crust .arrow span {
      border-left-color:#7d7d7d;
}
.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
color:#fff;
border-color:#999;
}
.navTabs .navTab.selected .tabLinks, .VietXfAdvStats h3, .nodeList .categoryStrip, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .breadcrumb, .discussionList .sectionHeaders, .redactor_toolbar {
background: -moz-linear-gradient(top,  rgba(125,126,125,1) 0%, rgba(51,51,51,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(125,126,125,1)), color-stop(100%,rgba(51,51,51,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(125,126,125,1) 0%,rgba(51,51,51,1) 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(125,126,125,1) 0%,rgba(51,51,51,1) 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(125,126,125,1) 0%,rgba(51,51,51,1) 100%); /* IE10+ */
background: linear-gradient(to bottom,  rgba(125,126,125,1) 0%,rgba(51,51,51,1) 100%); /* W3C */
}
.footer .pageContent, .button.primary, .button, a.callToAction span, .PageNav a.currentPage {
background:#333;
}
a.callToAction:hover span, .breadcrumb .crust:hover a.crumb, .button:hover, .button[href]:hover, .buttonProxy:hover .button {
background:#555;                    
}
.breadcrumb .crust:hover .arrow span {
border-left-color:#555;
}
.button.primary, .button, a.callToAction span {
box-shadow:0px 1px 0px 0px #272727 inset;
border:1px #222 solid;
        }
a:link, a:visited, .sectionFooter, .PageNav a.currentPage, .button:hover, .button[href]:hover, .buttonProxy:hover .button {
color:#fff;
}
.PageNav a.currentPage, .PageNav a:hover, .itemPageNav a:hover {
border:1px #222 solid;
}
.message .newIndicator {
background: rgb(140, 140, 140) url('http://forum.vitasite.info/styles/default/xenforo/gradients/category-23px-light.png') repeat-x top;
color:#000;
}
.message .newIndicator span, .redactor_toolbar li.redactor_btn_group ul {
background-color: rgb(140, 140, 140);
}
.doublePost .doublePostTagText {
color:inherit;
}
#logo {
-webkit-filter: grayscale(1);
filter: grayscale(1);
}
.blockLinksList {
background-color: transparent;
}
}

style_setting_options.value:
html {background: transparent !important;}
body {
background-image: url(http://i.b4rt0.net/smbdr.jpg) !important;
background-repeat: no-repeat;
background-size: cover;
background-attachment: fixed;
}
