css:
@-moz-document domain("8tracks.com") {
#belly_container {
background: #111 !important;
}

.playlist {
color: #555 !important;
}

.playlist li.now_playing {
color: #ddd !important;
}

.playlist li:hover {
color: #eee !important;
}

.playlist li.now_playing:hover {
color: #fff !important;
}

.track_details .detail {
color: #777 !important;
}

textarea {
background: black !important;
}

.collection_title {
border-bottom: 1px solid #222 !important;
}

.gridview .mix_name {
padding: 10px 0 0 0 !important;
color: #666 !important;
}

.profile {
color: #ddd !important;
}

.profile .nickname {
text-shadow: 1px 1px 0 #000 !important;
}

.mix_grid .mix_name {
color: #fff !important;
}

#footer {
background: #000 !important;
}

.comment .body {
border: 0 !important;
background: #222 !important;
}

.comment .body_text {
color: #999 !important;
}

.comment hr {
border-color: #333 !important;
}

#mix_wrapper .mix-stats {
background: #222 !important;
}

.new_review textarea {
background: #111 !important;
color: #aaa !important;
}

textarea {
color: #aaa !important;
}

a.propername {
color: #eee !important;
}

#mix_interactions {
border-right: 2px solid #444 !important;
}

#mix_interactions:before, #mix_interactions:after {
border: none !important;
}

.listview .mix_name {
color: #bbb !important;
}

.listview .grid_square, .listview .date{
background: #111 !important;
}

.listview .date {
border-top: 1px solid #333 !important;
}
    
a.tag, span.tag {
background-color: #000 !important;
border: 1px solid #333 !important;
color: #666 !important;
}

#explore #splash_title {
color: #444 !important;
}
}
