css:
@namespace"http://www.w3.org/1999/xhtml";

@-moz-document domain("4chan.org") {
body {
background:#101010!important;
color: #ddd !important;
}

body form[name="post"] {
margin-top: 64px;
}

.post-hidden .summary {
    display: none !important;
}

.deadlink {
    color: red !important;
}

.boxbar {
    background-color: #242424 !important;
    color: white !important;
}

.boxcontent {
    color: rgb(12, 12, 12) !important;
}

.box-outer {
    border: solid 1px black !important;
}

#recent-images li {
    background: none !important;
}

li.fill {
    display: none !important;
}

#ft li {
    background: rgb(24, 24, 24) !important;
    -webkit-transition: all 0.5s ease;
    -moz-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    transition: all 0.5s ease;
    border-color: black !important;
}

#ft li.current {
    background: rgb(30, 30, 30) !important;
    padding-bottom: 2px !important;
    border-top: solid 1px black !important;
}

#ft ul {
    width: auto !important;
}

#ft li:hover {
    background: rgb(30, 30, 30) !important;
    -webkit-transition: all 0.5s ease;
    -moz-transition: all 0.5s ease;
    -ms-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    transition: all 0.5s ease;
}

#logo {
    display: inline-block;
    -moz-box-sizing: border-box;
    width: 300px;
    height: 120px;
    margin: 0 auto !important;
    padding-left: 300px !important;
    background: url(data:image/png;base64,) !important;
}


table.postForm > tbody > tr > td:first-child { 
    background-color: #171717 !important;
}

body .center, img[src="//s.4cdn.org/image/xmashat.gif"],img[title="Toggle announcement"],#globalMessage,div[style="text-align: center; margin-top: 10px;"],.absBotText,.thread-stats,.stylechanger,.navLinks,tr.rules,.passNotice,hr,.middlead,.boardBanner,.globalMessage,.postingMode {
display:none!important
}

.globalMessage,.absBotText,#boardNavDesktop {
color:#EEE!important
}

.summary {
color:#DDD!important;
}

a,a.quotelink,a.replylink,.useremail .name,.useremail .postertrip {
color:#e01b6a!important
}

.post.reply {
background:none!important;
border:none!important
}

.replyContainer {
background:none;
-webkit-box-shadow:inset 0 0 27px -4px rgba(0,0,0,.98);
box-shadow:inset 0 0 27px -4px rgba(0,0,0,.98);
border-radius:5px;
border:none!important;
padding:8px;
margin-top:3px;
margin-left:16px;
}

.reply.highlight {
-webkit-box-shadow:0 0 30px #ff9a9f!important;
box-shadow:0 0 30px #ff9a9f!important;
z-index:600!important;
background:rgba(5,5,5,.9)!important
}

.linkfade {
font-size:110%!important;
color:red!important;
opacity:1!important
}

table#postForm {
color:#DDD!important;
padding:15px!important;
border-radius:5px;
background:rgba(10,10,10,.8)!important
}

table.postForm>tbody>tr>td:first-child {
background:none;
border:none!important;
color:#DDD!important
}

.navLinks.navLinksBot {
display:block!important;
right:0!important;
bottom:0!important;
position:fixed!important;
background: rgba(24, 24, 24, 0.8) !important;
}

.pagelist {
background:rgba(10,10,10,.9)!important;
border:none!important;
color:#DDD!important;
border-radius:4px;
display:block!important;
background-color:transparent!important
}

.extPanel {
background:rgba(10,10,10,.5)!important;
-webkit-box-shadow: 0px 0px 6px 0px rgba(50, 50, 50, 0.75);
box-shadow:         0px 0px 6px 0px rgba(50, 50, 50, 0.75);
border:solid 1px rgba(10,10,10,.01)!important;
border-radius:4px!important;
padding:5px!important;
}

#qrFile { color: #ddd !important; }

#qrHeader {
    background: rgba(20, 20, 20, 0.4) !important;
}

#settingsMenu .extPanel {
background:rgba(5,5,5,.9)!important
}

.drag.postblock {
background:none;
border:none!important;
color:#DDD!important
}

.post.reply.preview,.post.preview {
-webkit-box-shadow:0 0 21px #000;
box-shadow:0 0 21px #000;
border:solid 1px rgba(10,10,10,.01)!important;
background:rgba(5,5,5,.9)!important
}

.newPostsMarker:not(#quote-preview) {
-webkit-box-shadow:0 0 30px #ff9a9f!important;
box-shadow:0 0 30px #ff9a9f!important;
z-index:600!important
}

#quote-preview {
    background-color: rgba(20, 20, 20, 0.9) !important;
    color: #ddd !important;
    border-radius: 4px;
}

a img[alt="G"] {
width:14px!important;
display:inline-block!important;
padding:14px 0 0!important;
height:0!important;
height:14px;
margin-right:4px!important;
background:url(data:image/png;base64,)!important
}

a img[alt="I"] {
width:14px!important;
display:inline-block!important;
padding:14px 0 0!important;
height:0!important;
height:14px;
background:url(data:image/png;base64,)!important
}

img[data-cmd="report"] {
width:14px!important;
display:inline-block!important;
padding:14px 0 0!important;
height:0!important;
height:14px;
background:url(data:image/png;base64,)!important
}

img[data-cmd="hide-r"],img[data-cmd="hide"] {
width:14px!important;
display:inline-block!important;
padding:14px 0 0!important;
height:0!important;
height:14px;
background:url(data:image/png;base64,) no-repeat!important
}

img[src="//s.4cdn.org/image/buttons/futaba/post_expand_plus.png"] {
    background: url(data:image/png;base64,) !important;
    background-repeat: no-repeat;
    width: 18px;
    height: 18px;
    padding-left: 18px;
    overflow: hidden;
    display: inline-block;
    -moz-box-sizing: border-box;
}

img[src="//s.4cdn.org/image/buttons/futaba/post_expand_minus.png"] {
    background: url(data:image/png;base64,) !important;
    background-repeat: no-repeat;
    width: 18px;
    height: 18px;
    padding-left: 18px;
    overflow: hidden;
    display: inline-block;
    -moz-box-sizing: border-box;
}

.click-me {
border-color:#1e1e1e!important;
color:#fff!important;
background:#0a0a0a!important
}

#boardNavDesktop, #boardNavDesktopFoot {
width:99%;
margin:0 auto!important;
padding-bottom:32px;
font-size:8pt!important
}
}

@-moz-document regexp(".*boards.4chan.org/.*/") {
.post.op {
-webkit-box-shadow:inset 0 0 27px -4px rgba(0,0,0,.98);
box-shadow:inset 0 0 27px -4px rgba(0,0,0,.98);
padding:16px!important;
border-radius:5px!important;
display:block!important;
margin-top:32px!important;
margin-bottom:0!important
}

.summary {
display:block!important
}
}

@-moz-document regexp(".*boards.4chan.org/.*/res/.*") {
html,body {
margin:0!important;
padding:0!important
}

body {
margin-left:25%!important;
text-align:left!important
}

body form[name="post"] {
margin-top: 0 !important;
}

.deleteform {
bottom:0!important;
left:0!important;
z-index:500!important;
position:fixed!important
}

.post.op .fileText {
font-size:71%!important;
display:block!important
}

.post.op .dateTime:before {
content:"Posted: "!important
}

.post.op .dateTime {
display:block!important
}

div.post.op div.file .fileThumb img:not(.expanded-thumb) {
width:64px!important;
height:64px!important
}

.postContainer.opContainer {
height:100%!important;
width: 335px !important;
left:0!important;
top:0!important;
position:fixed!important;
background:rgba(16,16,16,.5)!important;
-webkit-box-shadow:5px 0 8px rgba(10,10,10,.75);
box-shadow:5px 0 8px rgba(10,10,10,.75);
border-right:solid 1px rgba(10,10,10,.5)!important
}

.post.op .postMessage {
max-height:77%!important;
width:90%!important;
overflow-y: auto;
padding: 0 !important;
margin: 0 auto !important;
margin-top: 32px !important;
}

.post.reply.preview,.post.preview {
width:auto!important;
-webkit-box-shadow:0 0 21px #000;
box-shadow:0 0 21px #000;
border:solid 1px rgba(10,10,10,.01)!important;
background:rgba(5,5,5,.9)!important
}

table#postForm {
margin:0 auto!important;
top:0!important;
margin-bottom:20px!important;
border-radius:0!important;
-webkit-border-bottom-right-radius:8px!important;
border-bottom-right-radius:8px!important;
-webkit-border-bottom-left-radius:8px!important;
border-bottom-left-radius:8px!important
}

.thread {
max-width:96%!important;
margin:0 auto!important
}
}
