css:
@-moz-document domain("facebook.com") {
body{
background: url("http://tdworkgroup.esy.es/userstyles/tohkadatealive.jpg") #fff top center fixed !important;
}
body>#blueBar{ background: none !important;}
#contentCol,#left_column,#right_column{ background-color: rgba(255,255,255,0.4) !important;  border: none !important;}
.UFIRow, .UFIAddComment, .UFIAddCommentWithPhotoAttacher, .UFILastComponent {background: transparent !important;}
.ego_section, .fbFeedTicker {display: none !important;}
#pageLogo a {
background: url("http://tdworkgroup.esy.es/userstyles/datealivelogo.png") no-repeat center center !important;
background-repeat-x: no-repeat;
background-repeat-y: no-repeat;
background-attachment: initial;
background-position-x: 50%;
background-position-y: 50%;
background-origin: initial;
background-clip: initial;
background-color: initial;
}
.linkWrap, .noCount, .UFICommentContent, .hasCaption, .UIShareStage_Summary, .UIShareStage_Title {color:white;}
._5uch, ._5jmm, ._5patx, .uiSideNav {color:rgba(255,255,255,0.7);background-color: rgba(255,255,255,255,0.5);border:none;}
._5pcr, .userContentWrapper, ._3dp, ._29k, .uiScrollableAreaContent, ._584t, .uiUfi, .UFIContainer {padding: 5px;border: 3px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;background-color:rgba(125,39,169,0.5)}
.homeSideNav {background-color:rgba(125,39,169,0.5);border: 1px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;}

a:link {color:rgba(255,255,255,0.8);text-decoration:none;}
a:hover {color:rgba(255,255,255,1);text-decoration:none;}
.selectedItem {background-color:black;}
._55pe {color:black;}
.fbNubFlyoutInner {padding: 5px;border: 3px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;background-color:rgba(125,39,169,0.5); -moz-box-shadow: 3px 3px 5px 6px #c560f9; -webkit-box-shadow: 3px 3px 5px 6px #c560f9;}
.fbNubFlyoutTitlebar, .titlebar {padding: 5px;border: 3px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;background-color:rgba(125,39,169,0.5)}
.fbNubButton {padding: 5px;border: 3px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;background-color:rgba(125,39,169,0.5)}
.fbNubButton:hover {padding: 5px;border: 3px solid #7d23a9; -moz-border-radius:6px; -webkit-border-radius:6px;background-color:rgba(125,39,169,0.5)}
}
