css:
@-moz-document domain("gpforums.co.nz") {
/* ---- GENERAL ---- */

body { background: #0F121F none !important; color: #1FA0B0 !important  }
.body_wrapper { background: #0F1F2F none !important }
a { text-decoration: none !important }

#pagetitle h1,
    #pagetitle h1 a { color: #10aeae !important }

.navbit a { color: #8A6199 !important }
.navbit a:hover { color: #EE0EEE !important }

#forumdisplaypopups a:hover { color: #EEEEEE !important }

/* ------ SIDEBAR -------- */

.widget_post_bit .widget_post_content  { background: #333333 none !important; color: #dddddd !important }
.widget_post_bit .widget_post_content .post_bubble_arrow { display: none !important }
.widget_post_comment .date .time { color: #8A9199 !important }

#page_sidebar { border-left: none !important }
#page_sidebar .sidebar_block { border-bottom: none !important }

#page_main_wrapper { border-right: none !important }

#block_html_3 .sidebar_mm_block { background: none repeat scroll 0 0 #8FA1B3 !important }

/* ------ TOPIC/THREAD LIST -------- */

table.threadbit,
    table.forumbit { background: #0C1826 none !important; border: none !important }

.threadlist .threads, .threadlist .stickies { border-bottom: none !important; }

.forumbit a,
    .forumbit a .time { color: #6A9199 !important }
.forumbit a:hover,
    .forumbit a:hover .time { color: #C0AEAE !important; text-decoration: none !important }

.threadbit a.title,
    .threadbit a.username,
    .threadbit a.lastposticon { color: #8A9199 !important }

.threadbit a.title:hover,
    .threadbit a.username:hover,
    .threadbit a.lastposticon:hover,
    .threadbit a.lastposticon:hover .time { color: #10A0B0 !important; text-decoration: none !important }

/* ------ THREAD CONTENT -------- */

.postbit { background: #0C2C30 none !important }
.postbit .postbody { background: #18202F none !important; color: #3d90Ae !important }

.postbit .postbody a { color: #4464BC !important }
.postbit .postbody a:hover { color: #0ACACA !important }

.postbit .postmain { margin-left: 125px !important }

.bbcode_container .bbcode_quote,
    .bbcode_container .bbcode_code { background: #423242 none !important }

.lastedited, .lastedited .time { color: #a1a1a1 !important }

#threadrating a, .thread_tags a { color: #8A9199 !important }
#threadrating a:hover, .thread_tags a:hover { color: #EEEEEE !important }

.postbody_bubble, .quote_bubble{ display:none;}

.postuserinfo div.avatar { background: none !important; border: none !important; }

.postuserinfo div.avatar a.avatar { background-image: none !important; }
.postuserinfo div.avatar a.avatar img { visibility: visible !important; }
.postuserinfo div.username a.username { color: #90A0B0 !important; } 

img[src='/skins/gpforums/images/avatar-default.png'] { display:none !important; }

/* text editor */

.texteditor.forum textarea { background: #406080 !important; color: #90B0A0 !important; font: 14px 'Consolas', sans-serif; }


.bbcode_quote .bbcode_postedby .username {
 color: #ffffff !important;
}
/* --- END --- */
}
