@-moz-document url-prefix('http://d.hatena.ne.jp/musikusanouen/') {
body>*
  {box-sizing: border-box; width: auto !important; max-width: inherit !important; }
body>h1 {padding: 0 !important;}

/*--- 2columns topics ---*/
.main {width: 100% !important; margin: 0 -220px 0 0 !important;}
.main>div {width: auto !important; margin: 0 220px 0 0 !important;}
.main>div .calender, .main>div .day {margin: 0 !important;}
.sidebar {box-sizing: border-box; width: 220px !important; margin: 0 !important;}
}
