css:
/* AGENT_SHEET */
#nav-bar{
	background:-moz-radial-gradient(center bottom , rgba(0,105,200,.8), #000) !important;
	background-position: 0 -12px !important; 
	border-top:groove 2px!important;
	border-bottom:none!important;
	border-color:rgba(0,0,0,.375)!important;
}

.tab-close-button[selected="true"]{
    box-shadow:0px 0px 5px 2px red inset!important;
    border-radius:5px;
    margin-right:10px !important;
}

.tab-close-button{
    box-shadow:0px 0px 1px 0px darkred inset!important;
    border-radius:5px;
    margin-right:10px !important;
}

#tabbrowser-tabs{
	background:-moz-radial-gradient(center bottom , rgba(0,105,200,.5), Transparent) !important;
	border:none !important;
}
.tabbrowser-tab{
    background: rgba(0,0,0,0.5);
	font-size: 14px;
	border: 1px solid rgba(0,105,200,1) !important;
	text-shadow: 1px 1px 1px rgba(0,0,0,0.3) !important;
	border:none !important;
	max-width:237px;
	width:188px;
	height:30px;
	color:#0069a7 !important;
	text-align:center;
	overflow: hidden;
	filter: none; 
	background-position: 0 -10px; 
	box-shadow: black 0px 0px 2px,
				black 0px 0px 1px !important;
	text-overflow: ellipsis;
	-o-text-overflow: ellipsis;
	white-space: nowrap;
	-moz-transition: all .5s ease;
}

#PersonalToolbar{
	background:-moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
	color:#e5ffb9!important;
	border: 1px solid rgba(0,105,200,1) !important;
	-moz-transition: all .5s ease;
}

.toolbarbutton-text{
    color:rgba(0,155,255,1)!important;
}


.tabbrowser-tab[selected="true"]{
	background: -moz-linear-gradient(center bottom , rgba(0,155, 255, .5), Transparent),
    -moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
    color:rgba(0,155,205,1)!important;
    text-shadow:rgba(0,155,205,1) 0px 0px 5px !important;
    font-weight: bolder;
	-moz-transition: all .5s ease;
}

.tabbrowser-tab{
	background: -moz-linear-gradient(center top , rgba(50,50,50,1), rgba(0,0,0,0.75))!important;
    border-top-left-radius:10px;
    color:rgba(0,105,155,.7)!important;
	-moz-transition: all .5s ease;

}

.tabs-newtab-button{
	background:-moz-linear-gradient(center top , rgba(30,30,30,.75), rgba(0,0,0,0.75))!important;
	border:none!important;
	border-top:groove 2px!important;
	border-right:ridge 2px!important;
	border-color:rgba(0,0,0,.375)!important;
	border-top-right-radius: 3px!important;
}

.tab-icon-image{
    margin-left:10px !important;
}

#appmenu-button{
	background:-moz-radial-gradient(center top , #333333, #000000)!important;
	border-color:black!important;
	box-shadow:rgba(0,155,205,7) 0px 0px 2px!important;
}

#appmenu-button:hover{
	background:-moz-radial-gradient(center top , rgba(0,155,205,1), #000000)!important;
}

#nav-bar toolbarbutton.toolbarbutton-1:hover[disabled="true"]{
	background:-moz-linear-gradient(center top , #101010, #000000)!important;
	border:solid 1px!important;
	border-color:transparent!important;
	border-radius:5px!important;
	box-shadow: rgba(255,255,255,.375) 0px 1px 0px inset,
				rgba(0,0,0,.375) 0px -1px 0px inset!important;
}

#nav-bar toolbarbutton.toolbarbutton-1:hover{
	background:-moz-linear-gradient(center top , #303030, #000000)!important;
	border:solid 1px!important;
	border-color:transparent!important;
	border-radius:3px!important;
	box-shadow: rgba(255,255,255,.375) 0px 1px 0px inset,
				rgba(0,0,0,.375) 0px -1px 1px inset!important;
}

#urlbar, .searchbar-textbox{
	background:rgba(0,0,0,.25)!important;
	color:rgba(255,255,255,.85)!important;
	box-shadow: rgba(0,0,0,.75) 0px 0px 3px inset,
				rgba(255,255,255,.25)0px 1px 0px !important;
	border-radius: 5px!important;
}

scrollbar {
	 -moz-appearance: none !important;
	 background: rgba(0,0,0,0) !important;
	 width:10px !important
 }
 
 thumb,scrollbarbutton {
	 -moz-appearance: none !important;
	 border:1px solid rgba(0,0,0,.6)!important;
	 border-radius:50px!important;
	 box-shadow:black 0px 0px 2px!important;
	 background-color: rgba(0,0,0,.5) !important;
	 -moz-transition: all .5s ease-in-out;
 }
 thumb:hover,scrollbarbutton:hover {
	 -moz-appearance: none !important;
	 background:-moz-radial-gradient(center center , rgba(0,160,255,.5), #333333) !important;
	 -moz-transition: all .5s ease-in-out;
 }
 scrollbarbutton {
	 display: none !important;
	 -moz-transition: all .5s ease-in-out;
 }
	 scrollbar[orient="vertical"] {
	 min-width: 8px !important;
 }
