  background-size: cover !important;
font-size: small;
}

img[src*="/images/srpr/chrome_ntp_white_logo2.png"]{
    width:0px !important;
    height:0px !important;
    padding-right:177px;
}

#appbar {
background: rgba(255, 255, 255, 0) !important;
-webkit-box-sizing: border-box;
width: 100%;
}
  
#hdtbSum {
background: rgba(255, 255, 255, 0) !important;
background-image: webkit-linear-gradient(left,rgba(254, 17, 47, 0) 0%,rgba(254, 17, 47, 0.5) 16.66%,rgba(255, 83, 19, 0.5) 16.67%,rgba(255, 83, 19, 0.5) 33.33%,rgba(255, 255, 155, 0.5) 33.34%,rgba(255, 255, 155, 0.5) 50%,rgba(77, 217, 46, 0.5) 50.01%,rgba(77, 217, 46, 0.5) 66.66%,rgba(10, 152, 226, 0.5) 66.67%,rgba(10, 152, 226, 0.5) 83.33%,rgba(94, 11, 145, 0.5) 83.34%,rgba(94, 11, 145, 0) 100%) !important;
border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
height: 80px !important;
line-height: 35px;
padding: 0;
position: relative;
z-index: 102;
}

#hdtb {
background: rgba(255, 255, 255, 0) !important;
color: #666;
outline-width: 0;
outline: none;
position: relative;
z-index: 102;
}

.gb_ib {
background: rgba(255, 255, 255, 0) !important;
background-image: -webkit-linear-gradient(left,rgba(254, 17, 47, 0) 0%,rgba(254, 17, 47, 0.5) 16.66%,rgba(255, 83, 19, 0.5) 16.67%,rgba(255, 83, 19, 0.5) 33.33%,rgba(255, 255, 155, 0.5) 33.34%,rgba(255, 255, 155, 0.5) 50%,rgba(77, 217, 46, 0.5) 50.01%,rgba(77, 217, 46, 0.5) 66.66%,rgba(10, 152, 226, 0.5) 66.67%,rgba(10, 152, 226, 0.5) 83.33%,rgba(94, 11, 145, 0.5) 83.34%,rgba(94, 11, 145, 0) 100%) !important;
  height: 0px;
}

.gb_la .gb_g {
background-position: -444px -69px;
height: 0px !important;
width: 92px;
}

#gbqfb {
border: none;
border-bottom-left-radius: 0;
border-top-left-radius: 0;
height: 30px;
outline: none;
padding: 0 0;
width: 60px;
-webkit-box-shadow: none;
box-shadow: none;
background: #4285f4;
background: -webkit-linear-gradient(top,#FE112F 0%,#FF5313  16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%) !important;
}

.ksb.ksbs, .ksb.ksbs:hover, .ab_button.selected, .ab_button.selected:hover {
background-color: #000;
background-image: -webkit-gradient(linear,left top,left bottom,from(#eee),to(#e0e0e0));
background-image: -webkit-linear-gradient(top,#FE112F 0%,#FF5313 16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%);
background-image: linear-gradient(top,#eee,#e0e0e0);
border: 1px solid rgba(0, 0, 0, 1) !important;
-webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
color: #222;
margin: 0;
}

.ksb, .ab_button {
background-color: #000;
background-image: -webkit-gradient(linear,left top,left bottom,from(#eee),to(#e0e0e0));
background-image: -webkit-linear-gradient(top,#FE112F 0%,#FF5313 16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%);
background-image: linear-gradient(top,#eee,#e0e0e0);
border: 1px solid rgba(0, 0, 0, 1) !important;
-webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
box-shadow: inset 0 1px 2px rgba(0,0,0,0.1);
color: #222;
margin: 0;
}

.ksb:hover, .ab_button:hover, #hdtb_tls:hover {
background-color: -webkit-linear-gradient(top,#FE112F 0%,#FF5313 16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%) !important;
background-image: -webkit-gradient(linear,left top,left bottom,from(#f8f8f8),to(#f1f1f1));
background-image: -webkit-linear-gradient(top,#FE112F 0%,#FF5313 16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%);
background-image: linear-gradient(top,#f8f8f8,#f1f1f1);
border: 1px solid #000000 !important;
color: #222;
}

.fbar {
background: rgba(242, 242, 242, 0) !important;
border-top: 1px solid rgba(228, 228, 228, 0) !important;
font-size: small;
line-height: 40px;
min-width: 980px;
}

#ctDoodleNotifier {
background-position: 0 0;
height: 0px !important;
left: 269px;
position: absolute;
top: -95px;
width: 22px;
}

.lhshdr, .tphdr, #tbbcc, .rshdr {
background: rgba(255, 255, 255, 0) !important;
}

#cnt {
background-color: rgba(241, 241, 241, 0) !important;
}

.rg_fbl {
border: 1px solid rgba(241, 241, 241, 0) !important;
display: inline-block;
margin: 0 32px 0 -8px;
padding: 7px 7px 0 7px;
}

.prcmg {
border-color: rgba(241, 241, 241, 0) !important;
border-style: solid;
border-width: 0 16px 0 17px;
height: 10px;
left: 0;
position: absolute;
right: 0;
}

#ifbd {
background: rgba(5, 5, 5, 0) !important;
height: 1px;
margin: 0 -4px 10px -17px;
overflow: hidden;
}

.prp {
background: rgba(241, 241, 241, 0) !important;
bottom: 0;
opacity: .4;
position: absolute;
top: 0;
width: 42px;
z-index: 10;
}

.rg_fbl:hover {
border: 1px solid #d6d6d6;
box-shadow: 1px 1px rgba(255, 255, 255, 0) !important;
background-color: rgba(229, 230, 230, 0) !important;
}

a:link, .w, #prs a:visited, #prs a:active, .q:active, .q:visited, .kl:active, .tbotu {
color: #9DDAF7 !important;
}

.mblink:visited, a:visited {
color: #6CAADD;
}

#hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre {
border-bottom: 3px solid #6CAADD !important;
color: #6CAADD !important;
font-weight: bold;
height: 35px;
margin: 2px 8px 0;
padding: 0 8px;
}

.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #ans>i, .bc a:link {
color: #BB1C76 !important;
font-style: normal;
}

.s {
color: #DB53A2 !important;
}

#resultStats, .ab_center_col {
color: #1D98D5 !important;
font-size: 13px;
line-height: 35px;
overflow: hidden;
text-overflow: ellipsis;
white-space: nowrap;
}

#hdtb_msb #hdtb_more, #hdtb_msb #hdtb_tls {
color: #1D98D5 !important;
}

body {
color: #1D98D5 !important;
}

.mn-dwn-arw {
border-color: #1D98D5 rgba(255, 0, 0, 0) !important;
border-style: solid;
border-width: 4px 4px 0 4px;
width: 0;
height: 0;
margin-left: -2px;
top: 50%;
margin-top: -2px;
position: absolute;
}

.hdtb-mn-o, .hdtb-mn-c {
-webkit-box-shadow: 0 2px 4px #1D98D5 !important;
background: rgba(29, 152, 213, 0.44) !important;
border: 1px solid #1D98D5 !important;
box-shadow: 0 2px 4px #1D98D5 !important;
color: #333;
position: absolute;
z-index: 103;
line-height: 17px;
padding-top: 5px;
padding-bottom: 5px;
top: 36px;
}

.f, .f a:link, .m, .descbox {
color: #1D98D5 !important;
}

.clickable-dropdown-arrow, .clickable-dropdown-arrow:hover, .clickable-dropdown-arrow.selected, .clickable-dropdown-arrow.selected:hover {
background-color: rgba(255, 255, 255, 0) !important;
background-image: none;
border: 0;
border-radius: 0;
-webkit-border-radius: 0;
box-shadow: 0 0 0 0;
-webkit-box-shadow: 0 0 0 0;
cursor: pointer !important;
display: inline-block;
filter: none;
height: 12px;
margin: 0;
min-width: 0;
padding: 0;
transition: none;
-webkit-user-select: none;
width: 13px;
}

#hdtbMenus {
background: rgba(255, 255, 255, 0) !important;
padding-bottom: 5px;
padding-top: 7px;
top: 0;
width: 100%;
height: 22px;
position: absolute;
transition: top 220ms ease-in-out;
-webkit-transition: top 220ms ease-in-out;
}

.hdtb-mn-hd {
color: #1D98D5 !important;
line-height: 22px;
cursor: pointer;
}

#hdtb_msb .hdtb-tl-sel, #hdtb_msb .hdtb-tl-sel:hover {
background: -webkit-linear-gradient(top,#FE112F 0%,#FF5313 16.66%,#FF5313 16.67%,#FFFF9B 33.33%,#FFFF9B 33.34%,#4DD92E 50%,#4DD92E 50.01%,#0A98E2 66.66%,#0A98E2 66.67%,#5E0B91 83.33%,#5E0B91 83.34%,#5E0B91 100%) !important;
-webkit-box-shadow: inset 0 1px 2px 0 rgba(0,0,0,0) !important;
border: 1px solid #000 !important;
box-shadow: inset 0 1px 2px 0 rgba(0,0,0,0) !important;
margin-left: 9px;
}

.fbar a:hover {
color: #6CAADD !important;
}

.fbar a, #fsettl {
color: #6CAADD !important;
text-decoration: none;
white-space: nowrap;
}

.mv-mask {
opacity: 100;
pointer-events: none;
background: -webkit-linear-gradient(top,rgba(255, 255, 155, 0) 0%,rgba(10, 152, 226, 0) 50%,#9EDBF9 100%) !important;
border-bottom: 1px solid rgba(255, 255, 255, 0) !important;
}

#newtab-scrollbox:not([page-disabled]) {
    background-color: rgb(242, 242, 242);
    color: rgb(0, 0, 0);
    background: url(http://fc06.deviantart.net/fs71/f/2014/002/3/3/rd_walpaper_by_focuswilliams-d70gb3c.png) fixed no-repeat !important;
    background-size: cover !important;
}

.newtab-title {
    padding: 0px 8px;
    background-color: rgba(158, 219, 249, 0.7) !important;
    color: rgb(255, 81, 168) !important;
    line-height: 24px;
}

.gb_jb {
background: rgba(241, 241, 241, 0) !important;
}

.gb_g {
background-image: url('http://fc05.deviantart.net/fs70/f/2014/027/4/c/google_by_focuswilliams-d74219u.png') !important;
background-size: 536px 341px !important;
}

