css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freekick.org") {
 
.width {
   background-color: #363 !important;
}
.green-head{
   background-image: none !important;
   background-color: #030 !important;
}
.red-head{
   background-image: none !important;
   background-color: #030 !important;
   
}
.blue-head{
   background-image: none !important;
   background-color: #030 !important;
   
}
.green-title{background-image: none !important;}
.red-title{background-image: none !important;}
.blue-title{background-image: none !important;}
.green-title a, .red-title a, .blue-title a{
   color:#fff !important;
}

.green-crn-re{background-image: none !important;}
.red-crn-re{background-image: none !important;}
.blue-crn-re{background-image: none !important;}

.red-crn-r{background-image: none !important;}
.green-crn-r{background-image: none !important;}
.blue-crn-r{background-image: none !important;}

#main-green-wrap{               
   background-image: none !important;
   background-color: #bca !important;
}
#green-round{
   background-image: none !important;
   height: 10px !important;
}
#white-content{
   background-color:#484 !important;
   background-image: url(http://www.mazuffer.com/freekick/maz_white_tab_r_bg.png) !important;
}
.rigth-crn{
   background-color:#484 !important;
   background-image: url(http://www.mazuffer.com/freekick/maz_white_tab_r_bg.png) !important;
   background-repeat: repeat-y !important;
      
}
.btm-shadow{
   background-color:#484 !important;
   background-image: none !important;
   width: 400px !important;   
}
.section-head{
   color:#fff !important;
}
.red-stripe{
   background-image:none !important;
   background-color:#030 !important;
}
#footer-tabs span{
   background-image: none !important;
}
.odd, tr.odd td {
   background-color: #060 !important;

}   
.even, tr.even td {
   background-color: #050 !important;
}
.odd a, tr.odd td a, .even a, tr.even td a {
   color: #fff !important;
}
table th{
   background-color:#474 !important;
   color: #fff !important;
}
.dotted, table.dotted tr th , table.dotted tr td, table.dotted-header tr th , table.achievers tr th , table.achievers tr td, table.transfers tr th , table.transfers tr td, table.moments tr th , table.moments tr td, table.clubnews tr th , table.clubnews tr td, table.standard-list tr th, table.standard-list tr td, .players td, .players th {
   background-image:none !important;
   border-bottom: 1px dotted #696 !important;
}
#main-green-wrap {
   color: #fff !important;
}
#content-l a{
   color: #fff !important;
}
#content-r a{
   color: #fff !important;
}
#blue-bar{      
   background-image: none !important;
   background-color: #030 !important;
   height:24px !important;
   }
#blue-nav{
   background-image: none !important;
   }
#blue-bar a{
   color:#fff !important;
}
#lang-select{
   background-image:none !important;
   background-color: #363 !important;
   cursor: default !important ;
}
#menu-left{
   background-image:none !important;
   background-color: #030 !important;
   margin-top:1px !important;
   width: 160px !important;
}
#inner-menu{   
   background-image: none !important;
   background-color: #030 !important;
}
#menu-left2{
   background-image:none !important;
   background-color: #030 !important;
   width: 160px !important;
}
#inner-menu2{   
   background-image: none !important;
   background-color: #030 !important;
}
.menu-btm{
   background-image: none !important;
   background-color: #030 !important;
}

/** club name on left menu ****/

.club-name a{
   color:#fff !important;
}
.settings-header {
   color:#fff !important;
}

/*** navigation menu   *****/

#nav-left li{
   background-image:none !important;
   border-bottom: 1px dotted #fff !important;
   padding-bottom:2px !important;
}

/*Sub level menu items */

#nav-left li ul{   
   border:3px solid #040 !important;
   background-color:#030 !important;
}

/* Sub level menu links style */

#nav-left li a{
   color: #fff !important;
   font-weight: bold !important;
   border-left: 4px solid #d70 !important;
}
#nav-left li a:visited{
   color: #fff !important;
}
#nav-left ul li a{
   border-left: 4px solid #d70 !important;
}
#nav-left li a:hover{
   background-color: #151 !important;
}
#inner-menu2 ul li p{
   color:#fff !important;
   border-left: 4px solid #d70 !important;
}
#inner-menu2 ul li p:hover{
   background-color: #151 !important;
}
#inner-menu2 div.status-left p{
   color:#fff !important;   
}
#inner-menu2 .greyed-out p{
   color: #eee !important;
}
#inner-menu2 .greyed-out p:hover{
   background-color: #151 !important;
}
.on {
   color:#595 !important;
}
.status-right{
   color:#fff !important;
}
.awesome {color: #d61 !important;}
.brilliant {color: #f83 !important;}
.superb {color: #f96 !important;}
.excellent {color: #f9c !important;}
.good {color: #c3c !important;}
.decent {color: #fff !important;}
.weak {color: #3cc !important;}
.poor {color: #ff0 !important;}
.awful {color: #f00 !important;}

#main-tabs li.tab-hidden{                  
   background-color: #f4f4f4 !important;
   background-image: none !important ;
}
#main-tabs li.tab-active{         
   background-image: none !important;
}
.age-old, .drop { color:#f71 !important; }
.age-senior { color:#FFFFFF!important; }
.age-youth-0, .abovepot { color:#33CCFF!important; }
.age-youth-1, .age-youth, .pot { color:#33CCFF!important; }
.age-youth-2 { color:#33CCFF!important; }
.age-youth-3 { color:#33CCFF!important; }
.age-youth-4, .age-youth-5 { color:#33CCFF!important; }
.age-retired { color: #F00 !important; }
}
