css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("betaforum.thegamecreators.com") {

body{
	
	font-weight:normal;
	background: #d0dbdb !important;
}

.groupListingHolder{background:#e0ebeb;border-radius:0;padding:0;}

.groupListingInnerHolder{box-shadow:0;border:1px solid #e0ebeb;}
.groupHeader{background:#a0abab;color:black;border-radius:0;border:1px solid #e0ebeb;}

.boardItem {background-color:#d0dbdb !important;height:auto;}


.boardItem a {color: #005177;}

.boardItem a:hover {color: #cc3333;text-decoration: none;}


#containerMain{background:#c0cbcb;border:1px solid #e0ebeb;border-radius:0;
	-webkit-box-shadow: 0;
	-moz-box-shadow: 0;
	box-shadow: 0; 

}

.board{margin-left:2px;}
.last, .posts, .topics{border-left:1px solid #e0ebeb;}
.last{border-right:1px solid #e0ebeb;}

.forumGroup h3{color: black;margin-top:30px;margin-bottom:0px;}
h3{color: black;}


.threadItemHolder{background:#e0ebeb;border-radius:0;padding:0;}
.threadItemInnerHolder{box-shadow:0;border:1px solid #e0ebeb;}
.threadItem{background-color:#d0dbdb;height:auto;}
.threadItem a {color: #005177;font-weight:normal}
.threadItem a:hover {color: #cc3333;text-decoration: none;}



.threadItem{margin-bottom:1px;}

.board{margin-left:2px;}
.last, .posts, .topics{border-left:1px solid #e0ebeb;}
.last{border-right:1px solid #e0ebeb;}

.threadItem>.subject{background:#d0dbdb;}
.threadItem>.views,.threadItem>.posts{background-color:#c0cbcb;}
.threadItem>.views,.threadItem>.posts,.threadItem>.author{border-right:1px solid #e0ebeb;}
.threadItem>.subject>.icon {border-right:1px solid #e0ebeb;height:31px}

.threadsHeader{border-radius:0;background:#a0abab;border:1px solid #e0ebeb;border-top:2px solid #e0ebeb;}
.threadsHeader>.posts, .threadsHeader>.author, .threadsHeader>.views{border-right:1px solid #e0ebeb;}

a:hover{text-decoration:none}

.buttonHolder, .buttonLight{background:#e9e9e9;border-radius:4px;border:1px solid #005a8c;color:#000 !important;text-transform:capitalize;height: 20px;line-height: 20px;}
.buttonHolder a, .buttonLight a{color:black;}
.buttonHolder a:hover, .buttonLight a:hover{color:black;text-decoration:none;}

input.button{height:20px;background:#e9e9e9;border:1px solid #005a8c;color:#000 !important;}
input.button:hover{text-decoration:none;}


.messageHeader{border-radius:0;background:#a0abab;border:1px solid #e0ebeb;border-top:2px solid #e0ebeb;border-top:0}
.messageHeader>.author {border-right:1px solid #e0ebeb;width:157px}
.messageHeader>.message{padding-left:4px}

.messageItemHolder{background:#d0dbdb;border-radius:0;padding:0;padding-right:0px;shadow-box:0;}
.messageItemInnerHolder {box-shadow: 0;}

.messageItem>.message {border-left: 1px solid #e0ebeb;border: 1px solid #e0ebeb;border-bottom:0;}

.messageItem{background:#d0dbdb;box-shadow:0;}

#messageHolder .postMessage {background:#d0dbdb;}

.messageItem>.message>.messageDetails {color:#444444}
#messageHolder{border: 1px solid #e0ebeb;}

.messageItem>.author{border-top:1px solid #e0ebeb;}
.messageItem{border-bottom:1px solid #e0ebeb;}

#messageHolder .postMessage a {color: black;}

a{color:#005177;}
a:hover{color:#cc3333;}

}
