css:
@-moz-document url("http://tieba.baidu.com/") {
/*CSS阴影*/
body *{text-shadow:0.08em 0.08em 0.04em #E3E3E3 !important;} 
/*TBAD*/
.tbsug_break:first-child, [class*=tbsug_top] {display: none !important;}
.nav_item_hot_flag,.tb_icon_new,.l_client,.lzl_panel_wrapper p,.editor_tip_show div,.wapapp_adv{display:none;}
/*屏蔽水笔*/
.l_post[data-field^="{\"author\":{\"id\":644711597,"],.lzl_single_post[data-field*="\"portrait\":\"e10b"][data-field$="e817\"}"]{display:none!important}




/*!!!!!!!!!Tieba Gravatar Fillet!!!!!!!!!!!!!!*/

/*主页头像区域去除边框*/
.userlike_info_head{  background:none !important;}
/*主页头像处理*/
.userlike_info_head a img{  padding:3px;  border-radius:15px;    box-shadow:1px 1px 3px #292929;}
/*去除层主区头像边框、白色背景 添加阴影*/
.p_author_face img{border:0px !important;background:none !important;border-radius:15px;box-shadow:1px 1px 2px #292929;}
/*去除有头像装饰的头像的装饰*/
.icon div{background:none !important;}
/*有头像装饰的头像阴影*/
.d_avatar_medium_frame a img{padding:3px;box-shadow:1px 1px 2px #292929;}
/*去除楼中楼头像区域边框*/
.lzl_p_p{border:0px !important;}
/*楼中楼头像圆角，阴影*/
.lzl_p_p img{padding:1px;border-radius:5px;box-shadow:1px 1px 3px rgb(104, 103, 103);}
 /*去除狗牌上的字*/
.badge{display:none;}
}
