css:
@-moz-document domain("vegzetur.hu") {
  
  img, input[type=image]{
    opacity: 0.05 !important;
    }
  img[src*="add"],
  img[src*="cross"],
  img[src*="tick"]{
    opacity: 0.2 !important;
    }
  img:hover, input[type=image]:hover{
    opacity: 0.5 !important;
    }
    
  .templomkep{opacity: 0.1 !important;}
  .templomkep:hover{opacity: 0.5 !important;}
  .szintkuldikep{opacity: 0.1 !important;}
  .szintkuldikep:hover{opacity: 0.5 !important;}
  #szerencsekerek{opacity: 0.1 !important;}
  #szerencsekerek:hover{opacity: 0.5 !important;}
  #hordak_haboruja_map{opacity: 0.05 !important;}
  #hordak_haboruja_map:hover{opacity: 0.3 !important;}
  .mezo{opacity: 0.1 !important;}
  .mezo:hover{opacity: 0.9 !important;}
  
  tr.link_sor_even.kiemelt,tr.link_sor_odd.kiemelt{font-weight:bold!important;}
  #targy_popup_content{ border: 2px solid black!important;}
  
  *:not(.templomkep):not(.szintkuldikep):not(#szerencsekerek):not(#hordak_haboruja_map):not(.mezo){;background:#fff!important; color:#000!important; }
  a { color: #007!important; text-decoration: none; }
  a:hover { color: #5bf!important; }
  a:visited { color: #557!important; }
  li.uj a, .specializacio, .turbo, .counter_akt { font-weight: bolder!important;text-decoration:underline!important;color: #f70!important; background: #eee!important;border: 1px solid #ccc!important;}
  .lathatatlan td { background: #aaa!important; font-weight:bold; }
  a.gomblink2 span, a.gomblink span, .gomblink_hordja span, .gomblink2_big span, .eladas_gomb, .epitesgomb { border: 2px solid black!important; background: #eee!important; }

  div.hr { border: 2px solid black!important; height: 0px !important; }
  div.h3_out h3 { border: 1px solid #ccc!important; text-transform: uppercase; }
 
  div.bal_content {  position: absolute;left: 0px; top: 10px; width:160px}
  span#rendszerido { position: fixed; left: 45px; top: 0px; }
 
  td[style="color: yellow;"]           {color: yellow !important; }
  td[style="color: rgb(0, 255, 255);"] {color: #0FF !important; }
  td[style="color: rgb(0, 204, 0);"]   {color: #0a0 !important; }
  td[style="color: red;"]              {color: red !important; }
  td[style="color: rgb(153, 153, 153);"]   {color: #999 !important; }
  
  .felszereles_kep, .fk_kep { width: 20px!important; height: auto !important;}
 
  div #harmonet, #harmonet_linksor, #header,  .bannerek, #linklista {display: none !important;}
  .banner {display: none !important; }
  div #infobox { font-size: 11px!important; }
  tr[class="topicrow"][style] { background: #f00!important; font-weight:bold }
 
  td.nevadas a { display: none !important; }

}
