#twotabsearchtextbox, #nav-iss-attach,
.nav-flyout-api, .srch_sggst_flyout, .nav-flyout
{
background:#000000 !important;
}
#sbcBackoutMsgBox 
{
border:none!important;
}
html, #wrapper, .nav-flyout-content, #SDPBlock,
#buyBoxContent, .cBoxInner, .mbcTitle,
.a-button-inner, select
{
background:#111111 !important;
}
.a-button-inner:hover, .a-button-text:hover, .a-normal tr:hover
{
