.a-size-small, .small, .cdQAListAnswerText, .nav_tag,
.cdQAListItemBestAnswerFooter span, .cdQAListItemBestAnswerFooter, .click_region, .cdQuestionListAskQuestionPostBoxText, .seed_header, .other_header, .as-title-block-left span,
.cdJSEnabledTextBody, .cdAskAnswerTag span, *,
.a-color-secondary, .a-color-base, .cbcc_box span,
.fresh-shoveler .as-title-block .a-color-base,
.cbcc_customer_name_text
{
color:#FFF !important;
}
a:hover, .a-list-item a
{
background:#111111 !important;
text-decoration:underline !important;
}
.a-divider-inner, .bucketDivider, .nav-promo
{
