@-moz-document domain("audible.com") {

html, .recommendations, .dropdown, .ftx-container, .menuItems,
 .adbl-prod-col, th, td, .categories, .categories li, #adbl_page_content,
 .search, * 
{
background:#111111 !important;
}
h1, h2, h3, h4, h5, a, p, label, .adbl-live-chat-grey, li, th, hd,
.adbl-price-col, #mast-member-credit-avail, .title, .search, * 
{
color:#FFFFFF !important;
}
}
@-moz-document domain("audible.custhelp.com") {

html, table, td, #rn_Body, #rn_MainColumn,
 .CollapsiblePanelTab, #rn_SearchControls 
{
background:#111111 !important;
}
h1, h2, h3, h4, h5, a, p, .CollapsiblePanelTab, .rn_Title 
{
color:#FFFFFF !important;
}
.CollapsiblePanelTab 
{
border-bottom:none !important;
}
}
