css:
@-moz-document regexp("http://gamesdonequick.com/(?!tracker).*"), regexp("https://gamesdonequick.com/(?!tracker).*") {
body {
background-color: #101010 !important;
color: silver !important;
}

#header {
    background-color: #333333 !important;
}

/* Home button gradient below player */
#donatebtn {background-color: #252525 !important; color: white !important;}
#popoutbtn {background-color: #303030 !important;}
#discordbtn{background-color: #353535 !important;}
#schedulebtn{background-color: #404040 !important}

#white-bg {
	background-color: #101010;
}

nav,.navbar,.navbar-default,.navbar-static-top,.nav,.navbar-nav,.navbar-text,.navbar-right {
	text-rendering: optimizelegibility;
}

nav {border-bottom: 1px solid rgb(0, 0, 0) !important;}

a {text-shadow: none !important;}

.medium-icon-green .fa-stack-2x,.text-gdq-green {
color: #00A102 !important;
}

.dropdown-menu {
    background-color: #080808 !important;
}
      
.dropdown-menu>li>a {
      color: white !important;
        }
.dropdown-menu>li>a:hover {
          background-color: #222 !important;
            }
.dropdown-header {
          color: white !important;
          font-weight: bold !important;
       }
.dropdown-menu>li>a:active {
    background-color: #222 !important;
}
.dropdown-menu>li:active {
    background-color: blue
}

     
/* Black borders */
td,.panel-footer,.panel-heading,.panel,.list-group-item {border-color: black !important;}

/* dark backgrounds */
.container,#twitch-video,#stream,#chat {background-color: #101010 !important;}

/* less dark backgrounds */
.panel,.panel-heading,.panel-footer,.list-group-item,.dataTables_scroll,.thumbnail {background-color: #191919 !important;}

/* New Schedule Stuff */
#runTable{
	background-color: #333 !important;
}
.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th{
	background-color: #222 !important;
}

.table-hover > tbody > tr:hover > td,
.table-hover > tbody > tr:hover > th {
	background-color: #444 !important;
	border-color: cyan !important;
}



tr.info td {
background-color: #323251 !important;
}

tr.danger td {
	background-color: #784A4A !important;
}
	
tr.warning td {
	background-color: #C98423 !important;
	color: black;
}

	
#runTable .day-split td {
	background-color: #444 !important;
	border-bottom: 1px solid black !important;
	border-top: 1px solid black !important;
}
/* end Schedule */

/* This stuff is already dark now */
.jumbotron .container, nav .container {
	background: none !important;
	border: none !important;
}
/* /already dark */	

/* Possibly non-existant now, but keeping just in case */
	
.panel-title {color: white !important;}

.panel-heading {background: 0 !important;}

#stream {border-color: #101010 !important;}

.text-gdq-black { color: #e2e2e2!important; }
.tickercontainer {
background: #222222 !important;
border: 1px solid rgb(0, 0, 0) !important;
}

.tickeroverlay-right {background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 1%, rgb(34, 34, 34) 99%, rgb(0, 0, 0) 100%) repeat scroll 0% 0% transparent !important;}
.tickeroverlay-left {background: linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 1%, rgb(34, 34, 34) 99%, rgb(0, 0, 0) 100%) repeat scroll 0% 0% transparent !important;}

#twitch-video,.thumbnail {
border: none !important;
}
    
#chat-tabs .nav  {
    background-color: #222222 !important;
}

.well {
background-color: #181818 !important;
border: 1px solid #000000 !important;
}

.table>tbody>tr.danger>td, .table>tbody>tr.danger>th, .table>tbody>tr>td.danger, .table>tbody>tr>th.danger, .table>tfoot>tr.danger>td, .table>tfoot>tr.danger>th, .table>tfoot>tr>td.danger, .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>thead>tr.danger>th, .table>thead>tr>td.danger, .table>thead>tr>th.danger {
        background-color: #AC0000 !important;
}
.table>tbody>tr.accept>td, .table>tbody>tr.accept>th, .table>tbody>tr>td.accept, .table>tbody>tr>th.accept, .table>tfoot>tr.accept>td, .table>tfoot>tr.accept>th, .table>tfoot>tr>td.accept, .table>tfoot>tr>th.accept, .table>thead>tr.accept>td, .table>thead>tr.accept>th, .table>thead>tr>td.accept, .table>thead>tr>th.accept,
.table>tbody>tr.success>td, .table>tbody>tr.success>th, .table>tbody>tr>td.success, .table>tbody>tr>th.success, .table>tfoot>tr.success>td, .table>tfoot>tr.success>th, .table>tfoot>tr>td.success, .table>tfoot>tr>th.success, .table>thead>tr.success>td, .table>thead>tr.success>th, .table>thead>tr>td.success, .table>thead>tr>th.success {
	background-color: green !important;
  color: white !important;

}
.text-center.danger {
      color: white !important;
    }

.dataTables_wrapper .row:first-of-type, .dataTables_wrapper .row:last-of-type {
    background-color: transparent !important;
}

h1,h2,h3,h4,h5,h6,.thumbnail .caption {
    color: white !important;
}
	button.btn:nth-child(4) {
		background-color: green !important;
	}
	.table-hover > tbody > tr:hover > .warning,
	.warning {
		color: black !important;;
		background-color: #f0ad4e !important;
	}
	
	.table-hover > tbody > tr:hover > .info,
	.info {
		color: #222 !important;;
		background-color: #5bc0de !important;
	}

#runTable tr:hover, #runTable tr:hover + .second-row{
	background-color: #444 !important;
}

.accept {
    background-color: #35b900 !important;
    color: black !important;}

.btn-default {
    color: rgb(255, 255, 255) !important;
    background-color: #313131 !important;
    border-color: #464646 !important;
}

.btn-warning {
    color: #000 !important;
    background-color: #f0914e !important;
    border-color: #eea236 !important;
}
}
