css:
/*
Animecalendar.net CSS userstyle by Nemura.
Use Stylish to apply.
It's designed to be somewhat minimal and match the colors of the appows peek VS.

Last updated 04/01/2014
*/

@-moz-document url-prefix("http://animecalendar.net/"), url-prefix("http://www.animecalendar.net/"){
html {
  background: rgb(27,27,27) !important;
}
body {
  background-color: rgb(27,27,27) !important;
  
}
#container {
  margin: 0px !important;
  padding:0px !important;
  height: 100% !important;
}
#newsBox, #menu div:nth-child(2), #footer, body div:nth-child(8), #uservoice-feedback {
  display: none !important;
}
#whereAreWe {
  
}
#menu {
  background-color: rgb(27,27,27) !important;
  height: 20px !important;
  border:none !important;
}
#menu li, #calendarContent table th, #whereAreWe h1, #small_menu li {
  color: rgb(220,220,220) !important;
}
#menu li a, #whereAreWe a:hover, #whereAreWe a, #small_menu li a {
  color: rgb(220,220,220) !important; 
}
#small_menu {
  height: 15px !important;
  border:none !important;
}
#whereAreWe table {
  width: 100% !important;
  border: none !important;
  background-color: rgb(34,34,34) !important;
  margin-bottom:30px !important;
  border-top:1px solid rgba(44,44,44,.6)!important;
  border-bottom:1px solid rgba(30,30,30,.6)!important;
  box-shadow:#060606 0px 0px 6px !important;
}
table {
  width:100% !important;
}
#calendarContent {
  color: rgb(220,220,220) !important; 
}

#calendarContent div.day th {
  background-color: rgb(31,31,31) !important;
}
#calendarContent div.day, #ac_fieldset {
  border:none !important; 
  margin: 5px !important;
  padding:0px !important;
  box-shadow:#060606 0px 0px 6px;
  border-top:1px solid rgb(37,37,37)!important;
  border-bottom:1px solid rgb(30,30,30)!important;
}
#calendarContent div.day table, #calendarContent div.day_now table{
  width:100% !important;
}
#showInfo th {
  background-color: rgb(31,31,31) !important;
}
#calendarContent a {
  font-size: 12px !important;
  
}
#calendarContent div.day td.filter h3 a, #calendarContent div.day_now td.filter h3 a {
  color: rgb(220,220,220) !important;
}
#calendarContent div.day td.filter small, #calendarContent div.day_now td.filter small {
  color: rgb(190, 190, 190) !important;
}
#calendarContent div.day_now {
  background-color: rgb(31,31,31) !important;
  border: none !important;
  margin: 5px !important;
  padding:0px !important;
  box-shadow:#004993 0px 0px 6px;
  border-top:1px solid rgb(37,37,37)!important;
  border-bottom:1px solid rgb(30,30,30)!important;
}
#calendarContent div.day_now th {
  background-color: rgb(31,31,31) !important;
}
#calendarContent div.day_now td.one {
  background-color: rgb(40,40,40) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day_now td.two {
  background-color: rgb(31,31,31) !important;
  padding-top: 5px !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day td.one {
  background-color: rgb(40,40,40) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day td.two {
  background-color: rgb(31,31,31) !important;
  padding:5px 0px 5px !important;
}
#calendarContent div.day table tbody tr td img, #calendarContent div.day_now table tbody tr td img {
  padding-left:4px !important;
}
#calendarContent div.day td h3 a, #calendarContent div.day_now td h3 a {
  color: rgb(220,220,220) ! important;
  font-size: 11px !important;
  font-weight:normal !important;
}
#calendarContent div.day td small, #calendarContent div.day_now td small {
  color: rgb(140,140,140) ! important;
  font-size: 9px !important;
}
#ac_fieldset h2, #profile th, #filters th {
  background-color: rgb(31,31,31) !important;
  border:none !important; 
}
#ac_fieldset div.ac_form_row, #profile tr, #filters table, #filters a{
  background-color: rgb(40,40,40) !important;
  border:none !important; 
  color: rgb(220,220,220) !important;
}
#ac_fieldset label{
    color: rgb(220,220,220) !important;
}
#profile {
  width: 500px;
  margin: 5px !important;
}
#profile table{
  border:none !important;
  box-shadow:#060606 0px 0px 6px;
  border-top:1px solid rgb(37,37,37)!important;
  border-bottom:1px solid rgb(30,30,30)!important;
}
#profile td{
  border:none !important;
}
#main div.lightSection{
  background-color: rgb(27,27,27) !important;
  color: rgb(220,220,220) ! important;
  border:none !important;
}
#main p.comment{
  background-color: rgb(40,40,40) !important;
  color: rgb(220,220,220) !important;
  border:none !important;
}
#main span.tip{
  border-right: 15px solid rgb(27, 27, 27) !important;
  border-color: rgb(40, 40, 40) rgb(27, 27, 27) rgb(27, 27, 27) !important;
}
#filters td{
  border-bottom:none !important;
}
#filters table{
  width: 80% !important;
  box-shadow:#060606 0px 0px 6px;
}
}
