@-moz-document domain("feedly.com") {
/*-------------------------------------------------------------------------*/
/*                                                                         */
/*                         RMC @ 07.09.2019 - v5.4                         */
/*                                                                         */
/*-------------------------------------------------------------------------*/

/*Fundo de pagina geral*/
body  {
    background-color: #0F1110 !important;
    position: absolute !important;
    margin-top: -45px !important;
    right: 0px !important;
    left:  0px !important;
}

#feedlyFrame {
    margin-top: 45px !important; 
}


/*Fundo do tema light*/
#feedlyPageHolderFX,
#headerBarFX header {
    background-color: #F8F8F8;
}


/*Fundo do tema dark*/
.theme--dark #feedlyPageHolderFX {
   background-image: url("https://i.imgur.com/XSLoYNA.jpg") !important;
   background-repeat: repeat !important;
}



/*-------------------------------------------------------------------------*/
/* POSTS                                                                   */
/*-------------------------------------------------------------------------*/
.area,
.inlineFrame {
    background-color: #fffffd !important;
    border: 0 solid rgba(51, 51, 51, .4)!important;
}


/*Textos de saparadores de datas entre posts*/
h4 {
    margin-top: 15px !important;
    /*display:        none !important; }*/
    margin-bottom: 5px !important;
}


/*Post e post selecionado*/
div[class="entryholder"],
div[class="inlineFrame read u100"],
div[class="inlineFrame unread u100"],
div[class="inlineFrame read selected u100"] {
    padding: 10px !important;
}

div[class="inlineFrame read selected u100"] {
    margin-bottom: 20px !important;
}


/*Caixa de procura e menu de opções*/
div[class="search-bar-right-col"] {
    position: absolute !important;
    /*                                 bottom:        0px !important; */
    top: 10px !important;
    left: -244px !important;
}

div[class="popover"] {
    left: -210px !important;
}



/*Barras no topo da coluna dos posts*/
#headerBarFX,
#topHeaderBarFX {
    display: none !important;
}

i[class="icon icon-md icon-tertiary icon-fx-feedly-android-md-black feedly-logo"],
button[class="pro primary small"] {
    display: none !important
}

#searchBarFX {
    height: 0px !important;
    margin: 0px !important;
    padding: 0px !important;
}



/* sidebar add content */
.menu.menu-custom {
    background: #1a1a1a;
    border: 1px solid #333;
}

.theme--light
.MarkAsReadButton {
    background-color: #eaeaea !important;
}


div[class="actions-container"]{ 
    background-color: #ffffffd9 !important; 
    border-radius: 5px !important; 
    height: 39px !important; 
}


/*Dentro do post*/
button[class*="headerInfo-expanded-img button-icon-only-micro icon icon-fx-star-save-ios-md-black tertiary"],
a[class="fx-button secondary full-width visitWebsiteButton"],
div[class="tagsHolder decoration-holder"] {
    display: none !important
}


.headerInfo {
    background-color: #fffffdd1 !important;
    border: 1px solid #222 !important;
}

.headerInfo.headerInfo-article {
    margin-top: 0 !important;
}


/*Botões*/
div[class="shareBarHolder"],
div[class="headerInfo headerInfo-article"] {
    margin: 0px !important;
    margin-top: -44px !important;
    max-width: 300px !important;
    max-height: 34px !important;
    padding: 1px !important;
    float: right !important;
}


div[class="fx tag-button"] {
      display: none !important  
}


i[class*="headerInfo-expanded-img icon-fx-"],
i[class*="headerInfo-expanded-img headerInfo-expanded-img"]
{
    padding: 0px !important;
    width:  30px !important;
    height: 30px !important;
}


/*Titulo do post*/
a[class^="entryTitle title"] {
    color: #346096 !important;
    font-size: 18px !important;
    line-height: 110% !important;
    letter-spacing: 0px !important;
    z-index: 3 !important;
    overflow: hidden !important;
    -o-text-overflow: ellipsis !important;
    text-overflow: ellipsis !important;
    max-width: 88% !important;
    margin-bottom: 3px !important; 
}


/*Texto do post*/
.entryBody,
p {
    text-align: justify !important;
    line-height: 125% !important;
    white-space: normal !important;
    color: #000000 !important;
    margin-top: 5px !important;
    max-width: none !important;
}
.entryBody a {
    color: #2bb24c !important;
}


h1,
h2,
h3,
h4,
h5,
h6 {
    color: #2bb24c !important;
}


.u100Entry {
    min-height: 80px !important;
    max-width: none !important;
}
.u100Entry .title {
    margin-bottom: 8px !important;
    max-width: none !important;
}
.u100Frame {
    border: none !important;
    padding: 10px !important;
}
.u100Frame .selectedEntry {
    background-color: #1a1bba !important;
}
.u5Entry {
    border-color: #333333 !important;
    background: none repeat scroll 0% 0% #222222 !important;
}
.u5Entry .title {
    background-color: #1bba1a !important;
}
div .wikibar {
    border-top-color: #333333 !important;
}


.entryholder {
    background-color: #fffffd !important;
    border: 0 !important;
}
.metadata {
    color: #666666 !important;
}
.websiteCallForAction {
    background-color: #222222 !important;
    border-color: #333333 !important;
}
.toBeTagged {
    background-color: #1a1a1a !important;
    color: #cccccc !important;
}



.popup {
    background-color: #1a1a1a !important;
    border-color: #333333 !important;
}
.popup .option {
    color: #999999 !important;
}
.popup hr {
    border-color: #666666 !important;
}


#popupPart {
    color: #999999 !important;
}



.preferencesItemLabel {
    color: #cccccc !important;
}
.preferencesItemDescription {
    color: #999999 !important;
}
.preferencesPanel label {
    color: #999999 !important;
}
#feedlyTabsHolder .icon {
    opacity: .4 !important;
}
#feedlyTabsHolder:hover .icon {
    opacity: 1 !important;
}


#headerBarFX header,
#headerBarFX .row {
    max-width: 100% !important;
}


.item-header {
    color: #ccc !important;
}




.fx .entry .title {
    color: #ccc !important;
}
.fx .entry.u0 {
    border-top: 1px solid #333 !important;
}
.fx .entry.u0:hover {
    background-color: #333 !important;
}
.fx.popover,
.fx .popover {
    background-color: #1a1a1a;
    border: 1px solid #333;
}

.fx .menu 
.fx .menu .item {
    color: #999 !important;
}
.fx .menu .item:hover,
.fx .menu .item.active,
.fx .menu .board-tag:hover .item.delete-tag,
.fx .board-tag:hover .menu .item.delete-tag {
    background-color: #333 !important;
    color: #ffffff !important;
}
.fx .entry.u0.selected,
.fx .entry.u100.selected {
    border: 1px solid #333 !important;
}
.fx .entry.u0.quicklisted {
    background-color: #333 !important;
}
.fx .inlineFrame {
    border: none !important;
    box-shadow: none !important;
}
.unread,
.fx .entry.unread .title {
    color: #cccccc !important;
}
.read,
.fx .entry.read .title,
.fx .entry.read .summary {
    color: #666666 !important;
}
/* Improve card view */
.fx .entry.u5:nth-child(3n + 1) {
    clear: none;
}
.fx .container {
    max-width: none;
}
.fx .entry.u5 {
    margin-left: 0;
    margin-right: 0;
    margin-bottom: 20px;
    margin-top: -8px;
    padding: 4px;
    width: 16.5% !important;
}

.fx h4, h4.fx, .fx-h4 {
    font-size: 1.0rem !important;
}


/* Improve magazine view */
.fx .entry.u4 {
    max-width: none;
    padding: 0;
}
.summary {
    max-width: 100% !important;
}


.theme--dark
::-webkit-scrollbar {
    background-color: #222 !important;
}
::-webkit-scrollbar-thumb {
    background-color: #666 !important;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 1px rgba(0, 0, 0, 1);
}

.headerInfo-expanded-img,

#searchBarFX .feedly-logo,
.hoverableIcon,
.condensed-toolbar-icon,
.fx .board-tag .user,
.fx .board-tag .delete-tag,
.fx .group-button .button-dropdown,
.fx .following-feed,
.icon-fx-add-ios-sm-white,
.fx .save-for-later {
    filter: grayscale(100%) invert(100%) !important;
}

.icon-toolbar-refresh-secondary,
.icon-fx-arrowhead-down-ios-sm-black {
    color: #fff !important;
}
.icon-fx-title-only-layout,
.icon-fx-more-ios-md-black,
.icon-fx-light-gear-ios-md-black,
.icon-fx-cross-ios-sm-black,
.icon-fx-cards-layout,
.icon-toolbar-refresh-secondary,
.icon-toolbar-markasread-secondary,
.icon-fx-arrowhead-down-ios-sm-black,
.icon-fx-full-article-layout,
.icon-fx-plane,
.icon-fx-share-to-team-black {
    filter: grayscale(100%) invert(80%) !important;

}

/* layout button dropdown */
.fx .page-layout-menu.form .field .label-radio {
    color: #ccc
}


/* share button bar */
.headerInfo-expanded-img:hover {
    background-color: #999;
    filter: grayscale(100%) invert(100%) !important;
}


/* dropdown button */
.fx .button.secondary {
    border: 1px solid #333;
}
.fx .button.secondary:hover {
    border: 1px solid #666;
}



/* share more menu */
.headerInfo-arrow {
    filter: grayscale(100%) invert(62%) !important;
}
.headerInfo-expanded {
    background-color: #111111 !important;
}
.headerInfo-expanded section,
.headerInfo-expanded section:hover {
    color: #ccc;
}
.headerInfo-expanded section {
    border-color: #333 !important;
}
.headerInfo-expanded section:hover {
    background-color: #333333 !important;
}

/* image border Magazine View */
.fx .entry.u4 {
    border-left: none !important;
}

.fx .form.hercule-search input[type="text"] {
    background-color: #333 !important;
}



/* USER SETTINGS AREA */
/* user menu */
.profile-bubble-menu {
    background: #1a1a1a;
}

/* floating tab */
.floatingEntryContent-tab {
    filter: grayscale(100%) invert(90%);
}

.fx .form .field label,
.fx .form .field .label,
.fx .picker {
    color: #999;
}

.fx .profile-bubble .user {
    background-color: #333;
}

.fx .profile-bubble .user
.fx .profile-bubble .user .item-meta {
    color: #2bb24c;
}

/* settings card*/
.list-cards .card {
    box-shadow: none !important;
    background-color: #333;
}

.preference-sections p,
.preference-sections .fx .feed-preview ul,
.fx .feed-preview .preference-sections ul,
.theme-panel .theme-panel-label {
    color: #999;
}

.theme--dark
.icon-fx-key-ios-md-black,
.icon-fx-custom-sharing-ios-md-black,
.icon-fx-evernote-ios-md-black,
.icon-fx-instapaper-ios-md-black,
.icon-fx-pocket-ios-md-black,
.icon-fx-onenote-ios-md-black,
.icon-fx-google-plus-ios-md-black,
.icon-fx-twitter-ios-md-black,
.icon-fx-facebook-ios-md-black,
.icon-fx-linkedin-ios-md-black,
.icon-fx-buffer-ios-md-black,
.icon-fx-email-ios-md-black,
.icon-fx-hootsuite-ios-md-black,
.icon-fx-wordpress-ios-md-black,
.icon-fx-save-ios-md-black,
.icon-fx-check-ios-sm-black,
.icon-fx-rss-ios-sm-black,
.icon-fx-link-ios-md-black {
    filter: grayscale(100%)  invert(80%) !important;
}


.fx table {
    border: 1px solid #333;
}





/*-------------------------------------------------------------------------*/
/* OUTRAS PÁGINAS                                                          */
/*-------------------------------------------------------------------------*/
/*Página de login*/
div[class="landing"] {
    margin-left: 40px !important;
}


.floatingEntryScroller {
    background-color: #fffffd !important;
    /*   border: 5 !important;
    border-color: #ffffff !important; */
}





/*-------------------------------------------------------------------------*/
/* IMAGEM & VIDEOS                                                         */
/*-------------------------------------------------------------------------*/
/*Links de dowload (adiciona uma imagem 10x10 no fim)*/
a[href$=".mp3"]:after,
a[href$=".mp4"]:after {
    content: url(https://i.imgsafe.org/3f/3f79820960.png) !important;
}


a[href*="twitter"]:before {
    content: url(https://i.imgsafe.org/3f/3f80e9b427.png) !important;
}



/*Tamanho e posição das imagens*/
img[src$=".gif"] {
    margin: auto !important;
}
.content a img,
.content img,
.entryBody div img {
    margin: auto !important;
    /* float:         left !important; */
    max-width: 99% !important;
    /* width:         auto !important; */
    object-fit: contain !important;
}

/*Tamanhos max da imagem por tamanho do ecra*/
@media screen and (min-height: 1px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 400px !important;
    }
}
@media screen and (min-height: 560px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 500px !important;
    }
}
@media screen and (min-height: 660px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 600px !important;
    }
}
@media screen and (min-height: 760px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 700px !important;
    }
}
@media screen and (min-height: 860px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 800px !important;
    }
}
@media screen and (min-height: 960px) {
    .content a img,
    .content img,
    .entryBody div img {
        max-height: 900px !important;
    }
}


/*Definicoes de videos*/
iframe,
.youtubePlaceholder {
    margin-left: 0px !important;
    margin-top: auto !important;
    margin-bottom: auto !important;
}

@media screen and (min-height: 560px) {
    iframe,
    .youtubePlaceholder {
        min-width: 720px !important;
        min-height: 405px !important;
        max-width: 800px !important;
        max-height: 450px !important;
    }
}

@media screen and (min-height: 960px) {
    iframe,
    .youtubePlaceholder {
        min-width: 880px !important;
        min-height: 495px !important;
        max-width: 1040px !important;
        max-height: 585px !important;
    }
}


/*Envia para o fundo um retangulo com o backgoud que mostra sobre tudo (?!)*/
iframe {
    z-index: -1000 !important;
}



/*-------------------------------------------------------------------------*/
/* FIM                                                                     */
/*-------------------------------------------------------------------------*/
}
