css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gamesdonequick.com") {
body {
background-color: #101010 !important;
color: silver !important;
}

nav,.navbar,.navbar-default,.navbar-static-top,.nav,.navbar-nav,.navbar-text,.navbar-right {
background: 0 !important;
box-shadow: none !important;

background-color: #191919!important;

text-rendering: optimizelegibility;
}
nav {
border-bottom: 1px solid rgb(0, 0, 0) !important;
}
a { 
text-shadow: none !important;
}
p {
color: silver !important;
}
table{
background-color: #101010 !important;
}
td > a, p > a {
color: #6bf !important;
}
tr {
background-color: #222 !important;
}
th {
background-color: #222 !important;
}
div.title, div#info, #totalallocated, #totalremaining{
color: silver !important;
background-color: #222 !important;
}
.asc, .dsc, .first, .last {
background-color: #666 !important;
margin: 0 2px 0 !important;
}

.panel, div.footer {
background-color: #191919 !important;
border-color: black !important;
}
.panel-title {
color: white !important;
}

.panel-heading {
background: 0 !important;
background-color: #191919 !important;
border-color: black !important;
}

.panel-footer {
border-color: black !important;
background-color: #191919 !important;
}

.container, #container, #twitch-video {
border-left: 1px solid rgb(0, 0, 0) !important;
border-right: 1px solid rgb(0, 0, 0) !important;
border-bottom: 1px solid rgb(0, 0, 0) !important;
background-color: #101010 !important;
}

#twitch-video {
border-top: 1px solid rgb(0, 0, 0) !important;
}

#stream {
border-color: #101010 !important;
}

#stream,#chat {
background-color: #101010 !important;
}

.tickercontainer {
background: black !important;
border: 1px solid rgb(0, 0, 0) !important;
}
.tickeroverlay-right {
background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 1%, rgb(0, 0, 0) 99%, rgb(0, 0, 0) 100%) repeat scroll 0% 0% transparent !important;
}
.tickeroverlay-left {
background: linear-gradient(to left, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 1%, rgb(0, 0, 0) 99%, rgb(0, 0, 0) 100%) repeat scroll 0% 0% transparent !important;
}


/* Scedual :X */

.daySplit {
background-color: #191919 !important;
border-color: black !important;
}

td { border-color: black !important;}
}
