css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kickstarter.com") {

#header-wrap, #content, #content-wrap, #running-board-wrap,
#location-projects, #trending-projects, #social-projects,
#curated-pages, .container, .social_tout, .connect-box,
 .NS-projects-reward, .search-link, .block, .project-card-list,
.project-stats-wrap
{
background:#000 !important;
}
#events, .primary-links, .menu-sub, .no-margin, .link-view-all
{
background-color:#000 !important;
}
.project-card, .navigation li a:hover
{
background:#222 !important;
}
a, p, h1, h2, h3, h4, h5, .num-backers, .grey-dark,
label, strong
{
color:#FFF !important;
}
}
