css:
/*
-------https://userstyles.org/users/145873---------
--------Please contact if anything breaks----------

        WHAT IT DOES:
        removes adverts and clutter
        ultra simple reading page
        drop-down navigation at top of page

*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mangareader.net") {

/*overall*/
.content-r-ad, .content-l-ad, .logo, #bottomchapter, #wrapper_footer, #topchapter .c3, .episode-table .c4, #adfooter, #popularmangas .c6, #adtop, #tfollowme, #tbut, #fbut {display:none;}
a:link {color:#005FA1 !important;}
a:visited {color:#005FA1 !important;}
a:hover {color:#000 !important;}
a:active {color:#000 !important;}

body {background-image:url("http://imageshack.com/a/img24/1323/ptqf.gif"), url("http://s2.mangareader.net/images/bgana.png") !important;
background-repeat:no-repeat, repeat !important;
background-attachment:fixed, scroll !important;
background-position:50% 100px, 0 0 !important;}


/*advanced search*/
#mangaresults h2, #authorresults h2 {color:#1B4A7D !Important;}
#genreinfodiv {background:none !important;}
#genreinfotext h2 {color:#1B4A7D !important;}
#options .submitbutton, #options .resetbutton, #searchcontainer .submitbutton {box-shadow:none !important;}

/*list*/
ul#alpha_listing li a {background:#D6EAEE !important;font-size:14px !important; padding:3px 5px !important; margin:1px 0 !important;}
ul#alpha_listing li.c1 a {padding:0 !important;}
.mangacompleted {color:#7EA9B1 !important;}

/*manga page*/
.aname {color:#000 !Important; font:normal 13px/1.231 "Lucida Grande",Tahoma,Arial,Sans-serif,sans !important;}
#latestchapters h2, #popularcaption h2, #latestchapters h3 {font-weight:normal !important;}
.genretags {border:1px solid #3676A3 !Important;}
.genretags:hover {border:1px solid #D6EAEE !Important;}
#readmangasum h2 {font-weight:normal !Important; color:#fff !Important; padding-bottom:8px !important;}
#readmangasum p {padding-bottom:4px !important;}
#readmangasum {border-width:1px 0 !important; border-color:#000 !Important;}
#latestchapters #popularcaption {padding:0 !Important; border-radius:7px 7px 0 0 !important;}
#latestchapters #popularcaption h3 {color:#fff !important;}
.propertytitle {color:#000 !Important;}
#listing th {color:#000 !Important;}

/*popular*/
div.series_col div.series_alpha, .mangaresultitem, .mangaresultauthoritem {background:#D6EAEE none !Important; border:none !important; border-radius:7px !important; box-shadow:none !important; border:1px solid #1A4A7D !important;}
.kutu-baslik {background:#D6EAEE none !Important;}
.mangaresultitem {margin-top:7px !important;}
.imgsearchresults {background-repeat:no-repeat !Important;}
.kutu .listeyan .nactive a {color:#fff !Important; font-weight:normal !Important;}

/*homepage*/
#latestchapters h2, #popularcaption h2, #latestchapters h3 {font-weight:normal !important;}
#mangaproperties h1, h1 {color:#1B4A7D !Important;}
.latesthotimages {border:none !important; margin-right:15px !important; box-shadow:0 2px 3px #222 !important;}
.imageinside {border-radius:7px !Important; border:none !important; margin:4px 0 0 4px !important;}
#latestchapters h3 {color:#000 !important; padding:5px !important; border-bottom:1px solid #01487B !important;} 
#latestchapters h2 {color:#F6FFFF !important; background-color:#3676A3 !important; padding:5px !important; border-bottom:1px solid #01487B !important;}
#popularlist {background-color:#3676A3 !important; border:none !important; border-radius:0 7px 7px 7px !important;}
#popularcaption {background-color:#3676A3 !important; border:none !important; border-radius:7px 7px 0 0 !Important;}
h3+.updates .c3 .c2 {font-weight:bold !important;}

/*header*/
.top {background:transparent !important; padding:0 !important;}
#wrapper_header  {background:#1D1D1D !important; position:fixed !important;
border-bottom:1px solid #1A4A7D !important; padding:0 !important; top:-68px !important; cursor:s-resize; transition:all 500ms cubic-bezier(0.420, 0.000, 0.580, 1.000) 200ms !important; height:68px !important; z-index:999 !important;}
#wrapper_header:hover {top:0 !important;}
#wrapper_header .clear {height:10px !important;position:relative !important; top:10px !important; background:transparent !important;z-index:0 !important;}
#wrapper_header a {color:#ababab !important;}
#wrapper_header a:hover {color:#ddd !important;}
#top_navigation, #sec_navigation {float:none !important;}
#sec_navigation a {line-height:normal !important; margin:0px 5px 0 5px !important; padding:0 !important;}
#sec_navigation a:hover {background:transparent !important; border:none !important;}
.top {width:800px !important;}
.topnav-current {text-shadow:none !important;}
#sec_navigation ul {padding:0 !important;}
#top_navigation li {border:none !important;}
#fpage {display:none !important;}
.aftersearch {margin-left:20px !important; border:none !important;}
.aftersearch #searchbox {border:none !important; margin:0 5px 0 0 !important; padding:2px 0 3px 20px !important; text-transform: none !Important;}

/*reading page*/
.episode-table tr td {padding:0 !important;}
#topchapter {width:800px !important;}
#mangainfo {margin:0 !important;}
#chapterMenu {width:200px !important;}
#mangainfo {height:23px !important;}
#mangainfo a {visibility:hidden;}
#mangainfo a:before {content:"back" !important; visibility:visible;}
#mangainfo .c2>a {color:#ddd !important; font-size:18px !important; font-weight:normal !important;}
#mangainfo .c2>a:hover {color:#ababab !important;}
.zoomimg {position:absolute !important; text-align: center !important; top:20px !important; left:40% !important; margin-left:28px !important;}
.zoomimg {background-color:transparent !important; font-weight:normal !important; color:#ddd !important;}
.zoomimg:hover {color:#ababab !important;}
#mangainfo h1, #mangainfo .c1 {color:#ABABAB !important; font-size:18px !important; font-weight:normal !important;}
.prev a, .next a {background-color:#666 !important; color:#ddd !important;}
.next a {border-right:1px solid #1A1715 !important;}
.prev a:hover, .next a:hover {background-color:#555 !important;}
  
  /*remove ads*/
  iframe {display: none !important;}
  iframe + a[href="http://www.animemobile.com/MangaApp/index.html"] {display: none !important;}
  .pageSkinAds {display: none !important;}
}
