/*个人中心左边栏QQ演出*/

a#tab_menu_yanchu {
    display: none !important;
}


/*个人中心左边栏那年今日*/

a#tab_menu_sdc {
    display: none !important;
}


/*个人中心左边栏微云备份*/

a#tab_menu_weiyun {
    display: none !important;
}


/*个人中心左边栏视频广场*/

a#tab_menu_mv {
    display: none !important;
}


/*开通黄钻*/

