.user-vip-info,
.infomation p,
#recent_visitors_side,
p.right,
a.open_vip_ad,
#vipBottomAdContainer,
div.mod_ad.showInNoVip.none,
p.light,
a.mod-open-vip,
div#js-capacity-droplist,
