.layout-copyright,
.lay_copyright,
.page_foot.big_page,
body>div>div.footer,
#lay>div.lay_foot>div.lay_inner>div.copyright,
#lay>div.lay_foot>div.author-info {
    visibility: hidden !important;
}


/*开通空间*/

#top_tips_container,
#top_tips_seat {
    display: none !important;
}

.top-fix-inner {
    margin-top: 0 !important;
}


/*热门直播*/

#QM_Container_HotLive {
    display: none !important;
}


/*腾讯课堂*/

#tab_menu_list>li.ke_gray {
    display: none !important;
}



/*美化*/


/*去除下划线*/

* {
    text-decoration: none !important;
}


/*更改字体*/

* {
    font-family: 微软雅黑!important;
}


/*[[beijing]]*/


/*图片圆角*/

li[id^=fct_] img,
a.img-two,
a.img-three,
li[id^=fct_] div.img-box,
div.img-attachments.img_thumb.imgscale-big.smallPic.pic img,
[id^=hex_]>div a {
    border-radius: 6px;
}

.feed .f-item .f-ct .img-box-row-wrap .img-num {
    border-radius: 6px;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
}


/*版头美化*/


/*[[daohanglan]]*/


/*头像位置*/

#layBackground .head-avatar {
    width: 93% !important;
    text-align: center;
    top: -270px !important;
}

#layBackground .head-avatar div {
    width: 36% !important;
    margin: 0 32% 0 32%;
    text-align: center;
}


/*标题位置*/

#headContainer>div.head-info {
    width: 96% !important;
    text-align: center;
    top: 150px !important;
}

#headContainer>div.head-info h1 {
    width: 100% !important;
    text-align: center;
    top: 150px !important;
}

#headContainer>div.head-info>div.qz-title-editor {
    width: 16% !important;
    margin: 0 42% 0 42%;
    text-align: center;
}

#headContainer>div.head-info div span {
    width: 30% !important;
    margin: 0 35% 0 35%;
    text-align: center;
}

#headContainer>div.head-info>div.dragdrop_item_title>a {
    width: 2% !important;
    margin: 0 40% 0 58%;
    text-align: center;
}


/*版头高度*/

#headContainer {
    height: 280px !important;
}


/*[[touxiang]]*/
}

