css:
@-moz-document url-prefix("https://digg.com/reader") {
html, body { /* unread */
	background: #333 !important; /* unread */
  font-family:
/*    'Gill Sans',  /* use it if you like */
    'Lucida Grande',
    'Hiragino Kaku Gothic ProN',
     Meiryo,
     sans-serif;
 	color: #ccc !important; /* title text focused text */
}
.feeditem-list.expanded .story-detail-view-container,
.story-detail-view-container,
.story-detail-view { /* focused items */
	background-color: #0c2220 !important;
}
.site-header { /* header */
	background-color: #333 !important;
}
.reader-nav .reader-nav-item { /* header menu */
	background-color: #333 !important;
}
.view-add { /* add menu */
	background-color: #222 !important;
}

.col-left { /* ctgly */
	background-color: #222 !important;
}

.story-title a { /* unread title text */
	color: #cc9322 !important;
}
.feed-item-focused.expanded .story-detail-view-container .story-title a { /* focuced title text */
	color: #0093cc !important;
}
.feeditem-list.expanded .story-detail-view-container .story-title a, .feeditem-read .feeditem-headline { /* focuced title text */
	color: #0093cc !important;
}

.detail-body { /* item text */
	color: #ccc !important;
}

.reader-nav .reader-nav-item { /* toprightmenu border */
	border-left: 1px solid #333 !important;
	color: #93cc22 !important;
}
.site-header-logo-container a { /* digg */
  background-color: #fff !important; /* bg */
}
.dr-feed-selected>.dr-label, .dr-feed-selected>.dr-label:hover { /* seld ctgly */
  background-color: #333 !important; /* bg */
  border-top-color: #444 !important; /* top border */
  border-bottom-color: #444 !important; /* bottom border */
  color: #93cc22 !important;
}
.dr-label:hover { /* focused ctgly */
  background-color: #1d1d1d !important; /* bg */
  border-top-color: #222 !important; /* top borer */
  border-bottom-color: #222 !important; /* bottom border */
}
.dr-label { /* categoly text */
	color: #aaa !important;
  text-shadow: 0 1px #111 !important;
}
.feeditem-headline { /* unread title text */
	color: #cc9322 !important;
}
.feeditem-read .feeditem-headline { /* read title text */
	color: #cc9322 !important;
}
.feeditem-list.expanded .story-detail-view-container .story-title a { /* focused title text */
	color: #0093cc !important;
}

.no-touch .feeditem-list:hover { /*  */
    background-color: #333 !important;
}
.feeditem-list .feeditem-feedtitle { /*  */
	font-weight: bold !important;
}
.feeditem-list { /* item boarder */
  border-top: 1px solid #222 !important;
}
.feeditem-list.expanded,
.feed-stream-header { /* expanded dist bottom boarder */
  border-bottom: 1px solid #444 !important;
}
.col-left { /* col-boarder */
	border-right: 1px solid #444 !important;
}
.feeditem-list .feeditem-fuzzytime a { /* clock */
	color: #aaa !important;
}
.feed-item-focused .feeditem-feedtitle, article.expanded .feeditem-feedtitle, .feed-item-focused .feeditem-list-content, article.expanded .feeditem-list-content,
.feeditem-list:hover { /* expanded src */
	background-color: #444;
}
.view-add {  /* add-section boarder */
	border-top: 1px solid #444 !important;
}
.view-add>div { /* none */
  -webkit-box-shadow: inset 0 1px 0 0 #222 !important;
  box-shadow: inset 0 1px 0 0 #222 !important;
}
.dr-add-btn { /* none */
  border-right: 1px solid #444 !important;
}
}
